/*******************************************************************************
* 함수명 : f_isNumber
* 기능명 :  숫자인지 check
*******************************************************************************/
function f_isNumber(sNumber)
{
	var i;

	var digit = replace(sNumber,'-',''); //-은 제외
	//숫자인지 check
	for(i=0; i<length(digit); i++) {
		if(asc(CharAt(digit, i)) < 48
		   || asc(CharAt(digit, i)) > 57) {
			return false;
		}
	}

	return true;
}

