/*******************************************************************************
// 2. 폼 변수 정의
*******************************************************************************/
var FV_CLOSE_DT;

/*******************************************************************************
* 함수명 : ACCDCL01300_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function ACCDCL01300_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);

	http.Sync = true;

	//마감일 세팅
	div_search.cal_saleDt.Value = TODAY();

	uf_enableAgencyByAuth("div_search", "edt_agencyCdNm,edt_agencyCd,btn_agency", "edt_agencyUkeyCd");
	uf_enableDealByAuth("div_search", "edt_dealNm,edt_dealCd,btn_dealCo");

	http.Sync = false;

}

/*******************************************************************************
* 함수명 : f_callBack
* 기능명 : 트랜잭션 콜백
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if(sSvcID == "svcSearch")
	{
		if(ds_dayClsList.rowcount > 0){

			ds_dayClsList.Sort("DEAL_CO_NM");

		}

		return;
	}

	if(sSvcID == "svcDetailSearch")
	{
		//ds_dayClsDetail.Sort("SALE_PLC_NM,SALE_DTM");
		//cf_exportExcel(grd_dayClsDetail);
		f_drawGrid(); // Grid 그리기
		cf_exportExcel(grd_dayClsDetail);
	}

}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search()
{

	if(uf_isNull(ds_condition.GetColumn(0,"POS_AGENCY")) == true){
		alert(cf_getMessage(MSG_00135, "대리점"));
		div_search_btn_agency_OnClick();
		return false;
	}

	if(toNumber(ds_condition.GetColumn(0,"SALE_DTM")) < 20100701){
		alert("2010년 7월 이후로 조회가능합니다.");
		return false;
	}

	FV_CLOSE_DT = div_search.cal_saleDt.Value;

	var sSvcID = "svcSearch";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_dayClsList=ds_dayClsList";
	var sArg   = "sktst.acc.DCL#getMcGeneralSaleListStatus";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

/*******************************************************************************
 * Desc   : M채널 수수료 추정 일마감 리스트 클릭시 이벤트 발생
 * Auth   : 전승호 (2009.01.15)
*******************************************************************************/
function grd_dayClsList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var oGrdDs = object(obj.BindDataset);

	if(oGrdDs.getColumn(nRow,"VIRTUAL_FLAG") == "N") {

		var sArg  = "SALE_DTM="+quote(oGrdDs.getColumn(nRow,"CLS_DTM"));
			sArg += " DEAL_CO_NM=" + quote(oGrdDs.getColumn(nRow,"DEAL_CO_NM"));
			sArg += " SL_CL=" + quote(oGrdDs.getColumn(nRow,"SL_CL"));
			sArg += " SALE_PLC=" + quote(oGrdDs.getColumn(nRow,"SALE_PLC"));
			sArg += " CLS_DT=" + quote(ds_condition.getColumn(0,"CLS_DT"));
			sArg += " POS_AGENCY=" + quote(oGrdDs.getColumn(nRow,"POS_AGENCY"));
			sArg += " POS_AGENCY_NM=" + quote(oGrdDs.getColumn(nRow,"POS_AGENCY_NM"));
			sArg += " ACC_MONTH_CLS=" + quote(ds_AccMonthCls.GetColumn(0,"ACC_MONTH_CLS"));
			sArg += " DEAL_CO_CL=" + quote(oGrdDs.GetColumn(nRow,"DEAL_CO_CL"));

		var rtn = dialog("ACC::ACCDCL01310.xml", sArg, 1024, 600);

		if(rtn == "1"){

			df_search();

		}

	}else{
		alert("거래내역이 없습니다.");
	}
}

/*******************************************************************************
 * @description    :  대리점 버튼 클릭 이벤트
*******************************************************************************/
function div_search_btn_agency_OnClick(obj)
{
	var sArg  = "org_id="+quote(gds_session.GetColumn(0,"orgId")); // 필수

	cf_callAgencyPop(sArg, div_search, "edt_agencyCdNm,edt_agencyCd,edt_agencyUkeyCd");
}

/*******************************************************************************
 * @description    : 대리점 key down - 이벤트
*******************************************************************************/
function div_search_edt_agencyCdNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	uf_clearControls("div_search.edt_agencyCd,div_search.edt_agencyUkeyCd");

	if(nChar == "13"){

		var sArg  = "org_id="+quote(gds_session.GetColumn(0,"orgId")); // 필수
			sArg += " org_cl="+quote(gds_session.GetColumn(0, "orgcl"));

		cf_callAgencyPop(sArg, div_search, "edt_agencyCdNm,edt_agencyCd,edt_agencyUkeyCd");

	}
}

/*******************************************************************************
 * @description : 정산처   팝업
 ******************************************************************************/
function div_search_btn_dealCo_OnClick(obj)
{
	if(uf_isNull(ds_condition.GetColumn(0,"POS_AGENCY")) == true){
		alert(cf_getMessage(MSG_00135, "대리점"));
		div_search_btn_agency_OnClick();
		return false;
	}

	var dealCd = ds_condition.GetColumn(0, "POS_AGENCY");

	var sArg  = "deal_co_cl1="+quote("C1"); // 필수 - 복수시 A1,A2,A3 형태의 콤바구분자로 설정
		sArg += " pos_agency="+quote(dealCd);

	cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd");
}

/*******************************************************************************
 * @description : 정산처 EnterKeydwon
 ******************************************************************************/
function div_search_edt_dealNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar==13) {

		if(uf_isNull(ds_condition.GetColumn(0,"POS_AGENCY")) == true){
			alert(cf_getMessage(MSG_00135, "대리점"));
			div_search_btn_agency_OnClick();
			return false;
		}

		var dealCd = ds_condition.GetColumn(0, "POS_AGENCY");

		var sArg  = "deal_co_cl1="+quote("C1"); // 필수 - 복수시 A1,A2,A3 형태의 콤바구분자로 설정
			sArg += " pos_agency="+quote(dealCd);

		cf_callDealPop(sArg, div_search, "edt_dealNm,edt_dealCd");
	}
}

/*******************************************************************************
 * @description : 정산처 OnCharChanged
 ******************************************************************************/
function div_search_edt_dealNm_OnCharChanged(obj,strPreText,strPostText)
{
		div_search.edt_dealCd.Text = "";
		ds_condition.SetColumn(0,"STL_PLC",null);
}

/*******************************************************************************
* 함수명 : btn_export_OnClick
* 기능명 :  엑셀 export
*******************************************************************************/
function btn_export_OnClick(obj)
{
	cf_exportExcel(grd_dayClsList);
}

/*******************************************************************************
* 함수명 : btn_allExport_OnClick
* 기능명 :  엑셀 export
*******************************************************************************/
function btn_allExport_OnClick(obj)
{

	if(uf_isNull(ds_condition.GetColumn(0,"POS_AGENCY")) == true){
		alert(cf_getMessage(MSG_00135, "대리점"));
		div_search_btn_agency_OnClick();
		return false;
	}

	var sSvcID = "svcDetailSearch";
	var sInDs  = "ds_condition=ds_condition";
	var sOutDs = "ds_Pol=rs_Pol ds_Pol2=rs_Pol2 ds_dayClsDetail=ds_dayClsDetail";
	var sArg   = "sktst.acc.DCL#getAllMcGeneralSaleDetailStatus";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}

