/*******************************************************************************
* 함수명 : btn_oldRtnEquipExcelUpload_OnClick
* 기능명 :  엑셀 export
*******************************************************************************/
function btn_oldRtnEquipExcelUpload_OnClick(obj)
{
	if(ds_monthClsList.rowcount == 0){
		alert("조회된 정산처가 없습니다.");
		return false;
	}

	var oGrdDs = object(obj.BindDataset);

	var sArg  = "ACC_MTH=" + quote(ds_condition.getColumn(0,"ACC_MTH"));
		sArg += " POS_AGENCY_NM=" + quote(div_search.edt_agencyCdNm.Text);
		sArg += " POS_AGENCY=" + quote(ds_condition.getColumn(0,"POS_AGENCY"));
		sArg += " SL_CL=" + quote(ds_condition.getColumn(0,"SL_CL"));
		sArg += " PARENT_GRDDS=" + quote("ds_monthClsList");

	var rtn = dialog("ACC::ACCACO00400.xml", sArg, 850, 490);

	if(rtn == 1){
		btn_desc.Visible = true;
		return;
	}
}

/*******************************************************************************
* 함수명 : btn_addSubExcel_OnClick
* 기능명 :  엑셀 export
*******************************************************************************/
function btn_addSubExcel_OnClick(obj)
{
	if(ds_monthClsList.rowcount == 0){
		alert("조회된 정산처가 없습니다.");
		return false;
	}

	var oGrdDs = object(obj.BindDataset);

	var sArg  = "ACC_MTH=" + quote(ds_condition.getColumn(0,"ACC_MTH"));
		sArg += " POS_AGENCY_NM=" + quote(div_search.edt_agencyCdNm.Text);
		sArg += " POS_AGENCY=" + quote(ds_condition.getColumn(0,"POS_AGENCY"));
		sArg += " SL_CL=" + quote(ds_condition.getColumn(0,"SL_CL"));
		sArg += " PARENT_GRDDS=" + quote("ds_monthClsList");

	var rtn = dialog("ACC::ACCACO00200.xml", sArg, 850, 490);

	if(rtn == 1){
		btn_desc.Visible = true;
		return;
	}
}

