/*******************************************************************************
* 함수명 : f_allGridCheck
* 기능명 :  그리드의 HEAD  클릭시
*******************************************************************************/
function f_allGridCheck(obj,nCell,nX,nY,nPivotIndex)
{
	if( nCell==1 )
	{
		f_gridCheckAll(obj,ds_monthClsList);  //전부 체크

	}else{
		cf_markGridSort(obj, nCell); //sort
	}
}

/*******************************************************************************
* 함수명 : f_gridCheckAll
* 기능명 :  그리드의 확정 CHECK 버튼 클릭시
*******************************************************************************/
function f_gridCheckAll(grid,ds)
{
	ds.fireEvent = false;

	// Check Box 전체 선택
	var nCount = ds.rowcount;
	if( grid.GetCellProp("head", 1, "Text") == "1" ) {

		if(confirm(cf_getMessage(MSG_00136, "모든 정산처 초기화")) == 1){

			ds_oldRtnEquip.ClearData();
			ds_cmmsAddSubD.ClearData();
			ds_accMtchDtlD.ClearData();

			for ( var irow=0; irow < nCount; irow++ ) {

				var ACC_PLC = ds.GetColumn(irow,"ACC_PLC");
				var ACC_PLC_COPY = ds_copyMonthClsList.FindRow("ACC_PLC",ACC_PLC);

				ds.setColumn(irow,"UNOLDRTN_RPAY_AMT",toNumber(ds_copyMonthClsList.GetColumn(ACC_PLC_COPY,"UNOLDRTN_RPAY_AMT")));
				ds.setColumn(irow,"CMMS_ADD_SUB_AMT",toNumber(ds_copyMonthClsList.GetColumn(ACC_PLC_COPY,"CMMS_ADD_SUB_AMT")));
				ds.setColumn(irow,"ACC_MTCH_AMT",toNumber(ds_copyMonthClsList.GetColumn(ACC_PLC_COPY,"ACC_MTCH_AMT")));

				ds.setColumn(irow,"UNOLDRTN_RPAY_DTL_YN","N");
				ds.setColumn(irow,"ADD_SUB_DTL_YN","N");
				ds.setColumn(irow,"MTCH_DTL_YN","N");

				//확정된 정산처가 ERP 전송이 완료된 건은 해제불가
				if(ds.GetColumn(irow,"ERP_FIX_YN") != "Y"){
					ds.SetColumn(irow,"CHK","0");
				}

				grid.SetCellProp("head",1,"text","0");

			}

		}

	} else {

		for ( var irow=0; irow < nCount; irow++ ){

			//확정된 정산처가 ERP 전송이 완료된 건은 해제불가
			//판매수수료 확정여부가 Y 가 아닌 일반판매단말기의 정산처가 존재하면 확정 불가
			if(ds.GetColumn(irow,"ERP_FIX_YN") != "Y" &&
			   ds.GetColumn(irow,"SALE_CMMS_FIX_YN") == "Y")
			{
				ds.SetColumn(irow,"CHK","1");
			}

			grid.SetCellProp("head",1,"text","1");

		}

	}

	ds.fireEvent = true;
}

/*******************************************************************************
* 함수명 : btn_desc_OnClick
* 기능명 :  중고반납/수수료가감/정산상계 내역 조회.
*******************************************************************************/
function btn_desc_OnClick(obj)
{
	div_desc.grd_desc.Redraw = false;

	div_desc.Visible = true;

	ds_desc.DeleteAll();

	for(var nRow=0;nRow<ds_oldRtnEquip.rowcount;nRow++){
        ds_desc.AddRow();
        ds_desc.CopyRow(ds_desc.lastrow,ds_oldRtnEquip,nRow);
	}

	for(var nRow=0;nRow<ds_cmmsAddSubD.rowcount;nRow++){
        ds_desc.AddRow();
        ds_desc.CopyRow(ds_desc.lastrow,ds_cmmsAddSubD,nRow);
	}

	for(var nRow=0;nRow<ds_accMtchDtlD.rowcount;nRow++){
        ds_desc.AddRow();
        ds_desc.CopyRow(ds_desc.lastrow,ds_accMtchDtlD,nRow);
	}

	ds_desc.Sort('DEAL_CO_NM,ACC_MTCH_ITM,CMMS_ADD_SUB_CL,UNOLDRTN_RPAY_ADD_SUB_AMT');

	div_desc.grd_desc.Redraw = true;
}

