var FV_LOGIN_ID 	= gds_session.GetColumn(0, "loginId");	//사용자계정
var FV_USER_NM		= gds_session.GetColumn(0, "userNm");	//사용자명 
var F_row = "";
var F_ACC_DT = "";
var F_ACC_FIX_YN = "";
var F_INS_DTM = "";
var F_ACC_LAST_DAY = "";
var F_MTH_CLOSE_DAY = "";
var F_FIX_SETOFF_PRP_AMT ="";
var F_FIX_SETOFF_CASA_AMT ="";
var F_TOT_ETC_AMT = "";  // 정산등록일까지의 기타계좌 잔액 (전월 기타계좌잔액 +당월 정산일까지의 기타계좌 입금액)

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function ACCSAF00600_OnLoadCompleted(obj)
{

	ds_condition.ClearData();

	// 권한 설정
	cf_setAuth(pFrmNum);

	if(length(ACC_MTH) ==6){
		div_search.cal_acc_mth.Text = substr(ACC_MTH,0,4)+"-"+substr(ACC_MTH,4,2);
	}else{
		div_search.cal_acc_mth.Text = ACC_MTH;
	}

	//정산일 변수설정
	F_ACC_DT =  Mid(cf_getDate(),0,8);
	//F_ACC_DT =  "20100309";


	//정산월 기준 가장 마지막날 구하기 
	F_ACC_LAST_DAY = ACC_MTH+GetLastDay(ACC_MTH);

	div_search.edt_ordPlcNm.Text = AGENCY_NM ;
	div_search.edt_salePlcNm.Text = DEAL_CO_NM;


	var sErrMsg = "";

	//도매수수료 정산이 전체완료되지 않았을 경우 정산처 상계 저장 못함

	if(SAVE_FLAG ==true && ERP_TRMS_YN != "1"){
		grd_prodList.Editable = true;
		grd_pay_list.Editable = true;
		btn_save.Enable = true;

	}else{

		if(ERP_TRMS_YN == "1"){
			sErrMsg = "M채널 수수료 정산이 ERP전송이 완료되어 저장할 수 없습니다. ";
		}else{		
			sErrMsg = "M채널 수수료정산이 전체완료되지 않아 저장할 수 없습니다. ";
		}

		grd_prodList.Editable = false;
		grd_pay_list.Editable = false;
		btn_save.Enable = false;

	}

	if(sErrMsg != ""){
		alert(sErrMsg);
	}


	ds_condition.AddRow();

	cf_setParam(ds_condition, "ACC_MTH", ACC_MTH); 
	cf_setParam(ds_condition, "STL_PLC", STL_PLC); 
	cf_setParam(ds_condition, "POS_AGENCY", POS_AGENCY); 
	cf_setParam(ds_condition, "INS_DTM", INS_DTM); 	
	cf_setParam(ds_condition, "DEAL_CL", DEAL_CL); 
	cf_setParam(ds_condition, "ACC_DT", F_ACC_DT); 

	f_initSearchSet();		
}

/*******************************************************************************
 * @description    : 도매 수수료 상계 대상 세부내역 조회
*******************************************************************************/

function f_initSearchSet()
{
	var sSvcID 	= "svcSearch";
	var sInDs  	= "nc_input_fieldDs=ds_condition";
	var sOutDs 	= "ds_list=rs_list ds_fixList=rs_fixList ds_dpstList=rs_dpstList";
	var sArg	= "sktst.acc.SAF#getMcCmmsSetOffDetailList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg,null,true,true);
}



/*******************************************************************************
 * @description    :  콜백
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcSearch"){

		F_ACC_FIX_YN = ds_fixList.GetColumn(0,"FNSH_YN");
		//F_INS_DTM = ds_fixList.GetColumn(0,"INS_DTM");

		F_MTH_CLOSE_DAY = ds_fixList.GetColumn(0,"CLOSE_DAY");

		//정산일까지의 기타계좌 잔액 
		F_TOT_ETC_AMT = toNumber(ds_fixList.GetColumn(0,"CASA_SETOFF_AMT")) + toNumber(ds_fixList.GetColumn(0,"SUM_AMT"));

		F_FIX_SETOFF_PRP_AMT = toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_PRP_AMT"));
		F_FIX_SETOFF_CASA_AMT = toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_CASA_AMT"));

    }else if(sSvcID == "svcSaveDpstAcc"){
		alert(cf_getMessage(MSG_00006, "M채널 수수료 상계 등록이"));

		gf_movenCall("100000627", "df_search");
	    this.Close();
    }
}


