/*******************************************************************************
 * @description    : 상계 세부내역 확정 클릭시 상계확정 목록의 skt 요금 세팅
*******************************************************************************/

function ds_list_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if(strColumnID =="CASH_DPST_FIX_YN"){
		if(varNewValue =="0" && varOldValue =="1"){

			//선택수납건의 상계등록일이 마감일보다 작거나 같으면 상계 해제 못함
			if(ds_list.GetColumn(nRow, "CASH_DPST_FIX_YN") =="1" && ds_list.GetColumn(nRow, "CASH_DPST_FIX_DT") <= F_MTH_CLOSE_DAY){
				//ds_list.SetColumn(nRow,"CASH_DPST_FIX_YN","0");
					alert("마감일 이전 상계등록 건은 상계 해제할 수 없습니다. ");
					return false;			
			}
		}
	}
}



/*******************************************************************************
 * @description    : 확정체크한 수납대행 세부내역 현금수납확정여부 'Y' update
*******************************************************************************/
function f_setChargeFixYn(){

	var nTotCnt = ds_list.GetCount();

	for(var nRow=0; nRow<nTotCnt; nRow++){

	   if(ds_list.GetColumn(nRow,"CASH_DPST_FIX_YN") =="1" && ds_list.GetColumn(nRow,"CASH_DPST_FIX_DT") ==null){
			//세부내역에서 확정 선택한 수납대행건을 dataset에 넣어서 tsal_skt_charge의 cash_dpst_fix_yn = 'Y' update	

			ds_list.SetColumn(nRow, "CASH_DPST_FIX_DT", F_ACC_DT);

			ds_list_0.AddRow();
			ds_list_0.CopyRow(ds_list_0.lastrow,ds_list,nRow);
			ds_list_0.SetColumn(ds_list_0.lastrow, "CASH_DPST_FIX_YN", "1");
			ds_list_0.SetColumn(ds_list_0.lastrow, "CASH_DPST_FIX_DT", F_ACC_DT);
		}else if(ds_list.GetColumn(nRow,"CASH_DPST_FIX_YN") =="0" && ds_list.GetColumn(nRow,"CASH_DPST_FIX_YN_TMP") =="1"){
			//상계등록  수정 시 기존에 상계확정한 수납대행 건을 확정 취소할 떄 tsal_skt_charge의 cash_dpst_fix_yn 를 'Y'->'N' update	
			ds_list_0.AddRow();
			ds_list_0.CopyRow(ds_list_0.lastrow,ds_list,nRow);
			ds_list_0.SetColumn(ds_list_0.lastrow, "CASH_DPST_FIX_YN", "0");
			ds_list_0.SetColumn(ds_list_0.lastrow, "CASH_DPST_FIX_DT", null);
		}
	}

	var sInFixSetOffPrpAmt = toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_PRP_AMT"));
	var sInFixSetOffCasaAmt = toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_CASA_AMT"));

	for(var k=0;k<ds_dpstList.GetCount();k++){

		var sOutCashDpstFixDt = ds_dpstList.GetColumn(k,"ACC_DT");

		ds_dpstList.SetColumn(k,"SETOFF_SKT_AMT", ds_list.TotalCaseSum("cash_dpst_fix_yn==\"1\"&&cash_dpst_fix_dt='"+sOutCashDpstFixDt+"'","cash_pay_amt"));

		if(sInFixSetOffPrpAmt != F_FIX_SETOFF_PRP_AMT || sInFixSetOffCasaAmt != F_FIX_SETOFF_CASA_AMT){
			if(sOutCashDpstFixDt == F_ACC_DT){
				//ds_dpstList.SetColumn(k,"SETOFF_SKT_PRPRC",(sInFixSetOffPrpAmt-F_FIX_SETOFF_PRP_AMT));
				//ds_dpstList.SetColumn(k,"SETOFF_CASH_SALE",(sInFixSetOffCasaAmt-F_FIX_SETOFF_CASA_AMT));
				ds_dpstList.SetColumn(k,"SETOFF_SKT_PRPRC",(toNumber(ds_dpstList.GetColumn(k,"SETOFF_SKT_PRPRC"))+(sInFixSetOffPrpAmt-F_FIX_SETOFF_PRP_AMT)));
				ds_dpstList.SetColumn(k,"SETOFF_CASH_SALE",(toNumber(ds_dpstList.GetColumn(k,"SETOFF_CASH_SALE"))+(sInFixSetOffCasaAmt-F_FIX_SETOFF_CASA_AMT)));
			}
		}
	}

}


