/*******************************************************************************
 * @description    : 헤더부분 클릭시 SKT 수납대행 내역 전체 확정 선택
*******************************************************************************/
function grd_prodList_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(SAVE_FLAG == true){
		if(nCell >0 ){
			cf_markGridSort(grd_prodList, nCell, nX, nY, nPivotIndex);
		}else{		

			var sValue = '0';
			obj.Redraw = false;
			var sChkValue = obj.GetSubCellProp("head", nCell, 0, "text");		

			if (sChkValue == 1) sValue='0';
			else sValue='1';		

			obj.SetSubCellProp("head", nCell, 0, "text", sValue);

			for (var i=0; i<ds_list.rowcount; i++)
			{		
				if(ds_list.GetColumn(i, "CASH_DPST_FIX_YN_ORG") != "Y"){
					if(sValue == "1"){

						if(ds_list.GetColumn(i, "CASH_DPST_FIX_DT") ==null || toNumber(ds_list.GetColumn(i, "CASH_DPST_FIX_DT")) <= toNumber(F_ACC_LAST_DAY)){
							ds_list.SetColumn(i, "CASH_DPST_FIX_YN", "1");
						}
					}else if(sValue == "0"){

						if(ds_list.GetColumn(i, "CASH_DPST_FIX_DT") ==null || toNumber(ds_list.GetColumn(i, "CASH_DPST_FIX_DT")) <= toNumber(F_ACC_LAST_DAY)){
							ds_list.SetColumn(i, "CASH_DPST_FIX_YN", "0");
						}
					}
				}
			}		

			obj.Redraw = true;		
		}
	}
}


/*******************************************************************************
 * @description    : 상계 확정금액 변경시 정산확정금액 변경 Event
*******************************************************************************/
function ds_fixList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireEvent = false;

	if(strColumnID =="FIX_SETOFF_CHAG_AMT"){
		ds_fixList.SetColumn(0, "FIX_ACC_AMT", toNumber(ds_fixList.GetColumn(0, "FIX_ACC_AMT"))-(toNumber(varNewValue)-toNumber(varOldValue)));

	}else if(strColumnID =="FIX_SETOFF_PRP_AMT"){

		if(varNewValue != varOldValue){

			if(toNumber(varNewValue) > toNumber(ds_fixList.GetColumn(0, "SKT_PRPRC_REQ_AMT"))){
				ds_fixList.SetColumn(0, "FIX_SETOFF_PRP_AMT",toNumber(varOldValue));
				alert("예수금 청구금액보다 작은 값으로 입력해주십시오.");

			}else{
				ds_fixList.SetColumn(0, "CASA_SETOFF_BAMT", toNumber(ds_fixList.GetColumn(0, "CASA_SETOFF_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_PRP_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_CASA_AMT")) );
				ds_fixList.SetColumn(0, "FIX_ACC_AMT", toNumber(ds_fixList.GetColumn(0, "FIX_ACC_AMT"))+(toNumber(varNewValue)-toNumber(varOldValue)));		
			}
		}
	}else if(strColumnID =="FIX_SETOFF_CASA_AMT"){

		if(varNewValue != varOldValue){
			ds_fixList.SetColumn(0, "CASA_SETOFF_BAMT", toNumber(ds_fixList.GetColumn(0, "CASA_SETOFF_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_PRP_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_CASA_AMT")) );
			ds_fixList.SetColumn(0, "FIX_ACC_AMT", toNumber(ds_fixList.GetColumn(0, "FIX_ACC_AMT"))+(toNumber(varNewValue)-toNumber(varOldValue)));

		}

		///// 최지선 씨 요청으로 잠깐 일시적으로 확정현금매출보다 큰 금액도 상게할 수 있도록 수정  100222 
		/*
		if(varNewValue != varOldValue){
			if(toNumber(varNewValue) > toNumber(ds_fixList.GetColumn(0, "FIX_CASH_SALE"))){
				ds_fixList.SetColumn(0, "FIX_SETOFF_CASA_AMT",toNumber(varOldValue));
				alert("확정현금매출보다 작은 값으로 입력해주십시오.");
			}else{
				ds_fixList.SetColumn(0, "CASA_SETOFF_BAMT", toNumber(ds_fixList.GetColumn(0, "CASA_SETOFF_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_PRP_AMT"))-toNumber(ds_fixList.GetColumn(0, "FIX_SETOFF_CASA_AMT")) );
				ds_fixList.SetColumn(0, "FIX_ACC_AMT", toNumber(ds_fixList.GetColumn(0, "FIX_ACC_AMT"))+(toNumber(varNewValue)-toNumber(varOldValue)));
			}
		}
		*/
	}

	obj.fireEvent = true;
}



/************************************************************************************
 * @description    : 예수금, 현금매출 상계확정 금액 입력시 마이너스 금액 insert방지
************************************************************************************/

function grd_pay_list_OnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam,nRow,nCell,nPivotIndex)
{
	if(nCell ==8){

		if(nChar==45){
			alert(cf_getMessage(MSG_00056, "")); 
			return;
		}

	}else if(nCell ==9){

		if(nChar==45){
			alert(cf_getMessage(MSG_00056, "")); 
			return;
		}

	}	
}


/************************************************************************************
 * @description    : 정산월의 맨마지막 날짜를 찾는 함수
************************************************************************************/
function GetLastDay(str_yyyymm)
{
	var   int_year, int_month;
	var		len;
	var		yy, mm, last_day, dd;
	var		c;

	int_year = ToInteger(substr(str_yyyymm, 0, 4));
	int_month = ToInteger(substr(str_yyyymm, 4, 2));
	if( int_month < 1 || int_month > 12 )
	{
		return -1;
	}
	if ( int_month == 2 )
	{
		if ( (int_year%4) == 0 && (int_year%100) != 0 || (int_year%400) == 0 )
		{
			last_day = 29;
		}
		else
		{
			last_day = 28;
		}
	}
	else if ( int_month == 4 || int_month == 6 || int_month == 9 || int_month == 11 )
	{
		last_day = 30;
	}
	else
	{
		last_day = 31;
	}

	return last_day;
}



/************************************************************************************
 * @description    : 상계확정 체크시 skt요금 상계확정금액과 정산확정금액 수정
************************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="CASH_DPST_FIX_YN"){
		if(varNewValue =="1"){

			ds_fixList.SetColumn(0,"FIX_SETOFF_CHAG_AMT",(toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_CHAG_AMT"))+toNumber(ds_list.GetColumn(nRow,"CASH_PAY_AMT"))));
			ds_fixList.SetColumn(0, "PMAG_SETOFF_BAMT", toNumber(ds_fixList.GetColumn(0, "PMAG_SETOFF_BAMT"))-toNumber(ds_list.GetColumn(nRow, "CASH_PAY_AMT")));
		}else if(varNewValue =="0"){
			ds_fixList.SetColumn(0,"FIX_SETOFF_CHAG_AMT",(toNumber(ds_fixList.GetColumn(0,"FIX_SETOFF_CHAG_AMT"))-toNumber(ds_list.GetColumn(nRow,"CASH_PAY_AMT"))));
			ds_fixList.SetColumn(0, "PMAG_SETOFF_BAMT", toNumber(ds_fixList.GetColumn(0, "PMAG_SETOFF_BAMT"))+toNumber(ds_list.GetColumn(nRow, "CASH_PAY_AMT")));

		}
	}
}
