#include "lib::lib_commF.js"
var FV_ORG_ID; 
var FV_ORG_NM;   
/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function form_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);  

    FV_ORG_ID = gds_session.GetColumn(0,"orgId");
    FV_ORG_NM = gds_session.GetColumn(0,"orgNm");

    div_search.edt_orgId.value = FV_ORG_ID;
    div_search.edt_orgNm.value = FV_ORG_NM;	
    div_search.edt_orgCl.value = gds_session.GetColumn(0,"orgCl");

	cf_setGrdChecknSortEvent(grd_list);

	// 공통 코드 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_deal", "ZBAS_C_00240", "전체"); // 거래처 구분
	cf_setCommonCode("div_search.cmb_sale", "ZBAS_C_00110", "전체"); // 판매점 구분
	cf_setCommonCode("div_search.cmb_dealStatus", "ZBAS_C_00120", "전체"); // 거래상태
	cf_setCommonCode("div_search.cmb_saleGrp", "ZBAS_C_00130", "전체"); // 영업 그룹

	cf_checkCommonCode();

	// ps 조직
	//f_getOrg();	

	// 영업사원 콤부 초기 셋팅.
	ds_user.InsertRow(0);
	ds_user.SetColumn(0, "user_id", "");
	ds_user.SetColumn(0, "user_nm", "전체");	
	div_search.cmb_chrgrUser.Index = 0;	

    // 영업 사원 셋팅.
    f_setChrgUser();			
}

function f_setChrgUser(){

	//센터 인 경우.
	if(div_search.edt_orgCl.value == "3"){

		div_search.cmb_chrgrUser.Enable = true;

		// 조회 조건 설정
		cf_setParam(ds_condition, "org_id", div_search.edt_orgId.value);

		// 트랜잭션 설정
		var sSvcID = "svcUser";
		var sInDs = "nc_input_fieldDs=ds_condition";
		var sOutDs = "ds_user=output";
		var sArg = "sktst.bas.PRM#getSalEmpList";

		// 공통 트랜잭션 호출
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);		

	}else{
		// 영업사원 콤부 초기 셋팅.
		ds_user.ClearData();
		ds_user.InsertRow(0);
		ds_user.SetColumn(0, "user_id", "");
		ds_user.SetColumn(0, "user_nm", "전체");	
		div_search.cmb_chrgrUser.Index = 0;			
		div_search.cmb_chrgrUser.Enable = false;
	}
}

