/*******************************************************************************
 * @description    : 조회 함수 - 이벤트
*******************************************************************************/
function f_search()
{
	// 조회 조건 설정
	cf_setParam(ds_condition, "org_id", div_search.edt_orgId.value);
	cf_setParam(ds_condition, "deal_co_cl1", div_search.cmb_deal.value);

	// 조직 조건 셋팅 여부.(아래의 거래처는 조직을 조건으로 받을 수 없다.)
	var sOrgYn = "";

	if(div_search.cmb_deal.value=="10" || div_search.cmb_deal.value=="20" || div_search.cmb_deal.value=="30"
		|| div_search.cmb_deal.value=="40" || div_search.cmb_deal.value=="50" || div_search.cmb_deal.value=="60"
		|| div_search.cmb_deal.value=="70"){

		sOrgYn = "N";
	}else{
		sOrgYn = "Y";
	}

	cf_setParam(ds_condition, "org_yn", sOrgYn); 
	cf_setParam(ds_condition, "deal_co_cl2", div_search.cmb_sale.value);
	cf_setParam(ds_condition, "deal_co_nm",  div_search.edt_dealName.text); 
	cf_setParam(ds_condition, "deal_status",  div_search.cmb_dealStatus.value);
	cf_setParam(ds_condition, "sale_grp",  div_search.cmb_saleGrp.value);
	cf_setParam(ds_condition, "user_id",  div_search.cmb_chrgrUser.value);
	cf_setParam(ds_condition, "ukey_channel_cd",  div_search.edt_orgAreaNm.value);
	cf_setParam(ds_condition, "biz_num",  div_search.msk_bizNum.value);
	cf_setParam(ds_condition, "bill_plc_id",  div_search.edt_billPlcId.value);


	// 트랜잭션 설정
	var sSvcID = "svcList";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_list=output";
	var sArg = "sktst.bas.PRM#getDealCoList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}

/*******************************************************************************
 * @description            : 거래처 구분 표시값 설정
 * @param   normal_yn      : 정상
			deal_end_yn    : 거래 종료
			pay_stop_yn    : 수납 정지
			out_stop_yn    : 출고 정지
		   sale_stop_yn    : 판매 정지
*******************************************************************************/
function checkDealStatus(normal_yn, deal_end_yn, pay_stop_yn, out_stop_yn, sale_stop_yn)
{
	var sResult = "";

	if(pay_stop_yn != null)
	{
		if(toUpper(pay_stop_yn) == "Y")
		{
			sResult += ",수납정지";	
		}
	}

	if(out_stop_yn != null)
	{
		if(toUpper(out_stop_yn) == "Y")
		{
			sResult += ",출고정지";	
		}
	}

	if(sale_stop_yn != null)
	{
		if(toUpper(sale_stop_yn) == "Y")
		{
			sResult += ",판매정지";	
		}
	}

	if(normal_yn != null)
	{
		if(toUpper(normal_yn) == "Y")
		{
			sResult += ",정상";	
		}
	}	

	if(deal_end_yn != null)
	{
		if(toUpper(deal_end_yn) == "Y")
		{
			sResult += ",거래종료";	
		}
	}	

	return substr(sResult, 1);
}

