/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{	
	if(sSvcID == "svcOrgList")
	{
		ds_org.InsertRow(0);
		ds_org.SetColumn(0, "orgId", "");
		ds_org.SetColumn(0, "orgNm", "전체");
		div_search.cmb_org.Index = 0;	
	} 

	if(sSvcID == "svcList")
	{
		//trace(ds_list.SaveXML());
	}

	if(sSvcID == "svcUser")
	{
		// if(div_search.cmb_org.index != 0){
			// div_search.cmb_chrgrUser.Enable = true;
		// }else{
			// div_search.cmb_chrgrUser.Enable = false;
		// }

		ds_user.InsertRow(0);
		ds_user.SetColumn(0, "user_id", "");
		ds_user.SetColumn(0, "user_nm", "전체");	
		div_search.cmb_chrgrUser.Index = 0;		
	}	

	if(sSvcID == "getExcelDeal")
	{ 
		if (div_search.cmb_deal.value= 'A1' || //대리점
			div_search.cmb_deal.value= 'A2' || //직영점
			div_search.cmb_deal.value= 'A3' || //판매점
			div_search.cmb_deal.value= 'A4' || //mm
			div_search.cmb_deal.value= 'C1' || //M채널
			div_search.cmb_deal.value= 'B1' || //온라인직
 			div_search.cmb_deal.value= 'B2' || //온라인판매
			div_search.cmb_deal.value= '70' || //카드사
			div_search.cmb_deal.value= '50' || //온라인거래
			div_search.cmb_deal.value= ''  ) { //전체

			cf_exportExcel(grd_Sales);
		} else {

			cf_exportExcel(grd_else);

		}

	}

}

/*******************************************************************************
 * @description    : 거래처 구분 변경시- 이벤트
*******************************************************************************/
function div_search_cmb_deal_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var sCheckTarget1 = "A2,A3,B1,B2,C1";


	if(sCheckTarget1.indexOf(strCode) >= 0)
	{
		div_search.cmb_sale.Enable = true;		
		div_search.cmb_saleGrp.Enable = true;
	}
	else
	{
		div_search.cmb_sale.index = 0;
		div_search.cmb_sale.Enable = false;

		div_search.cmb_saleGrp.index = 0;
		div_search.cmb_saleGrp.Enable = false;		
	}
}

