/*******************************************************************************
 * @description    : 목록 Excel Export 내역 저장
*******************************************************************************/
function f_ExcelExportHst()
{
	var sCondition  = "조직 : "       + div_search.edt_orgId.Text       + "(" + div_search.edt_orgNm.Text       + ")  / ";
	    sCondition += "거래처구분 : " + div_search.cmb_deal.Value       + "(" + div_search.cmb_deal.Text        + ")  / ";
	    sCondition += "거래처유형 : " + div_search.cmb_sale.Value       + "(" + div_search.cmb_sale.Text        + ")  / ";
	    sCondition += "거래처상태 : " + div_search.cmb_dealStatus.Value + "(" + div_search.cmb_dealStatus.Text  + ")  / ";
	    sCondition += "거래처명 : "   + div_search.edt_dealName.Text    + "  / ";
	    sCondition += "정산처 : "     + div_search.edt_billPlcId.Text   + "(" + div_search.edt_billPlcNm.Text   + ")  / ";
	    sCondition += "영업사원 : "   + div_search.cmb_chrgrUser.Value  + "(" + div_search.cmb_chrgrUser.Text   + ")  / ";
	    sCondition += "영업그룹 : "   + div_search.cmb_saleGrp.Value    + "(" + div_search.cmb_saleGrp.Text     + ")  / ";
	    sCondition += "사업자번호 : " + div_search.msk_bizNum.Value     + "  / ";
	    sCondition += "채널코드 : "   + div_search.edt_orgAreaNm.Text;

	cf_setParam(ds_condition, "EXPORT_DTM",     GetDate());
	cf_setParam(ds_condition, "EXPORT_SCREEN",  pFrmNum);
	cf_setParam(ds_condition, "EXPORT_COND",    sCondition); 
	cf_setParam(ds_condition, "EXPORT_USER",    gds_session.GetColumn(0, "loginId"));
	cf_setParam(ds_condition, "USER_LOGIN_IP",  gds_session.GetColumn(0, "ip"));
	cf_setParam(ds_condition, "USER_ORG_CD",    gds_session.GetColumn(0, "orgId"));
	cf_setParam(ds_condition, "USER_AGENCY_CD", gds_session.GetColumn(0, "posAgencyId"));
	cf_setParam(ds_condition, "USER_USER_GRP",  gds_session.GetColumn(0, "userGrp"));
	cf_setParam(ds_condition, "EXPORT_CNT",     ds_ExcelSales.rowcount);

	// 트랜잭션 설정
	var sSvcID 	= "addExcelExportHst";
	var sInDs 	= "nc_input_fieldDs=ds_condition";
	var sOutDs 	= "";
	var sArg 	= "sktst.bas.PRM#addExcelExportHst";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);

}

/*******************************************************************************
 * @description    : 전체 조직 트리 팝업 호출- 이벤트
*******************************************************************************/
function div_search_btn_orgTree_OnClick(obj)
{
    // 조직 트리 팝업 호출
   var sCloseYn = cf_callOrgTree2("div_search.edt_orgId", "div_search.edt_orgNm","","div_search.edt_orgCl"); 

	// 조직 선택 후 선택한 조직이 센터인 경우 해당 영업사원을 조회한다.
	if(sCloseYn == "1"){
		f_setChrgUser();
	}
}

