/*******************************************************************************
 * @description    : org OnChar Changed 이벤트
*******************************************************************************/
function div_search_edt_orgId_OnCharChanged(obj,strPreText,strPostText)
{
    if(strPreText != strPostText){
        div_search.edt_orgNm.value = "";
    }   
}

function div_search_edt_billPlcNm_OnCharChanged(obj,strPreText,strPostText)
{
	div_search.edt_billPlcId.value = "";
}

function div_search_btn_bill_OnClick(obj)
{
	var sOrgId = div_search.edt_orgId.value;	
	var sArg    = "deal_co_cl1="+quote("A1,A2,A3,B1,B2,C1");

	if(uf_IsNull(sOrgId)) {
		alert(cf_getMessage(MSG_00083, "조직"));
		return;		
	}

	sArg += " org_id="+quote(sOrgId);
	sArg += " eff_dtm="+quote(today()); // 옵션		

	cf_callDealPop(sArg, div_search, "edt_billPlcNm,edt_billPlcId");		
}

function div_search_edt_billPlcNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13"){

		var sOrgId = div_search.edt_orgId.value;	
		var sArg    = "deal_co_cl1="+quote("A1,A2,A3,B1,B2,C1");

		if(uf_IsNull(sOrgId)) {
			alert(cf_getMessage(MSG_00083, "조직"));
			return;		
		}

		sArg += " org_id="+quote(sOrgId);
		sArg += " eff_dtm="+quote(today()); // 옵션		

		cf_callDealPop(sArg, div_search, "edt_billPlcNm,edt_billPlcId");					
	}	
}
