	var sSvcID = "svcList";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_list=list";
	var sArg   = "sktst.bas.PRM#getDealList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}


/*******************************************************************************
 * @description    : 조회 유효성 검사
*******************************************************************************/
function f_isValidSearch()
{
	if(uf_IsNull(div_search.edt_dealCoCd.Text) == true)
	{
		alert(cf_getMessage(MSG_00083, "거래처"));
		div_search.edt_dealCoCd.SetFocus();
		return false;
	}

	return true;	
}

