/*******************************************************************************
 * @description    :  데이터셋 변경시 데이터 유효성 검사 이벤트 
*******************************************************************************/
function ds_list_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

//trace("ds_list_CanColumnChange : " +nRow +","+ strColumnID + ", " + varNewValue);
	if(strColumnID == "DEAL_STA_DT" || strColumnID == "DEAL_END_DT" ||strColumnID == "EFF_STA_DTM" ||strColumnID == "EFF_END_DTM" )
	{	// 형식 유효성 검사
		var sFormat = "YYYYMMDD";

		if(strColumnID == "EFF_STA_DTM" || strColumnID == "EFF_END_DTM")
		{	
			sFormat = "YYYYMMDDHH24MISS";
		}
		if( f_CheckDate(varNewValue, sFormat) == false)
		{
			alert(cf_getMessage(MSG_00058, varNewValue + " "));
			ds_list.row = nRow;
			grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );
			return false;
		}
	}

	if(strColumnID == "DEAL_STA_DT")
	{
		if(varNewValue > ds_list.GetColumn(nRow,"DEAL_END_DT"))
		{
			alert(cf_getMessage(MSG_00094, "거래개시일;거래종료일"));
			ds_list.row = nRow;
			grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );
			return false;		
		}
	}
	else if(strColumnID == "DEAL_END_DT")
	{
		if(ds_list.GetColumn(nRow,"DEAL_STA_DT") > varNewValue)
		{
			alert(cf_getMessage(MSG_00095, "거래종료일;거래개시일"));
			ds_list.row = nRow;
			grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );			
			return false;		
		}	
	}
	else if(strColumnID == "EFF_STA_DTM")
	{
		if(0 == nRow)
		{	// 첫번째 로우일경우 
			if(varNewValue > ds_list.GetColumn(nRow,"EFF_END_DTM"))
			{trace("1");
				alert(cf_getMessage(MSG_00095, "유효시작일자;유효종료일자"));
				ds_list.row = nRow;
				grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );			
				return false;
			}
		}
		else
		{
			var preEffStaDtm = ds_list.GetColumn((nRow - 1),"EFF_STA_DTM");

			if(varNewValue < preEffStaDtm)
			{	// 이전 유효시작일자보다는 큰 값 입력할 경우
			trace("2");
				alert(cf_getMessage(MSG_00094, "해당 유효시작일자;이전 유효시작일자"));
				ds_list.row = nRow;
				grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );			
				return false;
			}	
			if(varNewValue > ds_list.GetColumn(nRow,"EFF_END_DTM"))
			{	// 유효종료일자보다 큰 값 입력할 경우
			trace("3");
				alert(cf_getMessage(MSG_00094, "유효시작일자;유효종료일자"));
				ds_list.row = nRow;
				grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );			
				return false;
			}				
		}
	}
	else if(strColumnID == "EFF_END_DTM")
	{
		var nRows = ds_list.GetRowCount();
		if((nRows - 1) != nRow)
		{
			var sNextEffEndDtm = ds_list.GetColumn((nRow + 1),"EFF_END_DTM");

			if(ds_list.GetColumn(nRow,"EFF_STA_DTM") > varNewValue)
			{
				alert(cf_getMessage(MSG_00095, "유효종료일자;유효시작일자"));
				ds_list.row = nRow;
				grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );
				return false;					
			}

			if(sNextEffEndDtm < varNewValue)
			{
				alert(cf_getMessage(MSG_00094, "해당 유효종료일자;다음 유효시작일자"));
				ds_list.row = nRow;
				grd_list.SetCellPos(f_getIndexByGridColId(grd_list, strColumnID) );
				return false;					
			}			
		}	
	}
	return true;
}


/*******************************************************************************
 * @description    :  데이터셋 값 변경시 뒷 처리 이벤트
*******************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "EFF_END_DTM")
	{
		var nRows = ds_list.GetRowCount();
		if((nRows - 1) != nRow)
		{
			ds_list.SetColumn((nRow + 1),"EFF_STA_DTM", varNewValue);
		}
	}
	if(strColumnID == "EFF_STA_DTM")
	{
		if(0 == nRow)
		{		
		}
		else
		{
			ds_list.SetColumn((nRow - 1),"EFF_END_DTM", varNewValue);				
		}
	}	
	if(strColumnID == "NORMAL_YN")
	{
		if(varNewValue == "Y")
		{
			ds_list.SetColumn(nRow, "DEAL_END_YN","N");
			ds_list.SetColumn(nRow, "PAY_STOP_YN","N");
			ds_list.SetColumn(nRow, "OUT_STOP_YN","N");
			ds_list.SetColumn(nRow, "SALE_STOP_YN","N");			
		}
	}
	else if(strColumnID == "NORMAL_YN" 
			|| strColumnID == "DEAL_END_YN"
			|| strColumnID == "PAY_STOP_YN"
			|| strColumnID == "OUT_STOP_YN"
			|| strColumnID == "SALE_STOP_YN")
	{
		if(varNewValue == "Y")
		{
			ds_list.SetColumn(nRow, "NORMAL_YN","N");			
		}
	}
}

/*******************************************************************************
 * @description    :  ColId로 인덱스 찾기
*******************************************************************************/
function f_getIndexByGridColId(objGrid, strGridColId, nCntColId)
{
	var nCntFind = 0; 

	if(nCntColId == null)
	{
		nCntColId = 0;
	}

	var nCntCols = objGrid.GetCellCount("BODY");

	for(var i=0; i < nCntCols; i++)
	{
		var strColId = objGrid.GetCellProp("BODY", i, "COLID");

		if(strColId == strGridColId)
		{
			var nColIndex = objGrid.GetCellProp("HEAD", i, "COL");

			if(nCntColId == nCntFind)
			{
				return toNumber(nColIndex);
			}
			nCntFind++;
		}
	}

	return -1;
}
/*******************************************************************************
 * @description    :  날짜 유효성검사
*******************************************************************************/
function f_CheckDate(sValue, sDateFormat)
{
     checkStr = uf_StripSpecialChar(sValue);

     sDateFormat = toUpper(sDateFormat);
     if( sDateFormat == "YYYYMMDD" )
     {
          if( length(checkStr) != 8 )
              return false;

          if(checkStr == "99991231") return true;

          if( AddDate(AddDate(checkStr, 1),-1) == checkStr)
              return true;
          else
              return false;
     }
     else if( sDateFormat == "YYYYMMDDHH24MISS" )
     {
          if( length(checkStr) != 14 )
              return false;

          var yyyymmdd = substr(checkStr,0,8);
          var hh       = parseInt(substr(checkStr,8,2));
          var mi       = parseInt(substr(checkStr,10,2));
          var ss       = parseInt(substr(checkStr,12,2));
          if(yyyymmdd != "99991231")
          {
			  if( AddDate(AddDate(yyyymmdd, 1),-1) == yyyymmdd )
			  {
				  if( 0 <= hh && hh < 25 && 0 <= mi && mi < 60 && 0 <= ss && ss < 60)
					  return true;
				  else
					  return false;
			  }
          }
          else
              return false;          
     }   
     return false;
}

/*******************************************************************************
 * @description    :  데이터셋 로우 변경 이벤트
*******************************************************************************/
function ds_list_CanRowPosChange(obj,nOldRow,nRow)
{
	if(nOldRow == -1) return true;
	if(f_vaidRow(nOldRow) == false) return false;
}


/*******************************************************************************
 * @description    :  데이터셋 로우 유효성 검사
*******************************************************************************/
function f_vaidRow(nRow)
{
	var sDeal = ds_list.GetColumn(nRow, "DEAL_END_YN");
	var sPay  = ds_list.GetColumn(nRow, "PAY_STOP_YN");
	var sOut  = ds_list.GetColumn(nRow, "OUT_STOP_YN");
	var sSale = ds_list.GetColumn(nRow, "SALE_STOP_YN");

	if(ds_list.GetColumn(nRow, "NORMAL_YN") == "Y")
	{	// 정상
		if(sDeal == "N" && sPay == "N" && sOut == "N" && sSale == "N")
		{	// 정상
			return true;
		}
		else
		{	// 비정상
			alert("거래처는 반드시 하나 이상의 상태값을 가져야합니다.");
			ds_list.row = nRow;
			grd_list.SetCellPos(f_getIndexByGridColId(grd_list, "NORMAL_YN") );
			return false;
		}
	}
	else
	{	// 비정상
		if(sDeal == "Y" || sPay == "Y" || sOut == "Y" || sSale == "Y")
		{	// 정상
			return true;
		}
		else
		{
			//alert("정상여부가 N일 나머지 항목 하나 이상 비정상상태값을 가져야 합니다.");
			alert("거래처는 반드시 하나 이상의 상태값을 가져야합니다.");
			ds_list.row = nRow;
			grd_list.SetCellPos(f_getIndexByGridColId(grd_list, "NORMAL_YN") );

			return false;
		}	
	}
}

/*******************************************************************************
 * @description    :  저장하기
*******************************************************************************/
function btn_saveMulti_OnClick(obj)
{
	var nRow = ds_list.currow;

	if(f_vaidRow(nRow) == false ) return false;

	if(ds_list.GetUpdate() == false)
	{
		alert(cf_getMessage(MSG_00071,""));
		return;
	}

	if(Confirm( "저장 하시겠습니까?" ) == false) return;

	ds_condition.Clear();

	// 넘길 파라메터 설정
	cf_setParam(ds_condition, "deal_co_cd", div_search.edt_dealCoCd.Text); 

	// 트랜잭션 설정
	var sSvcID = "svcSave";
	var sInDs  = "List=ds_list:u";
	var sOutDs = "";
	var sArg   = "sktst.bas.PRM#updateDealList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);	
}


