*******************************************************************************/
function BASPRM00800_OnLoadCompleted(obj)
{    
    cf_setAuth(pFrmNum);    // 권한 공통
//    cf_setGrdChecknSortEvent(grd_list);    // 그리드 정렬

    f_init();
}


/*******************************************************************************
 * @description    : 화면 로딩시
*******************************************************************************/
function f_init()
{
	http.Sync = true;

	// 판매점 증감현황에서 분기한 경우 처리
	var sCond = StrdYM;


	if (uf_IsNull(sCond)) {
        div_search.cal_StrdYM.Value = substr(TODAY(), 0, 6);
	} else {
        div_search.cal_StrdYM.Value = sCond;
	}

    f_setAuth();

    ds_list.ClearData(); // 조회된 자료 Clear
    f_SetQueryDate();    // 조회를 위한 주간일자 Setting
    f_drawGrid();        // 조회를 위한 Grid 다시 그리기

	if (!uf_IsNull(sCond)) {
        div_search_btn_search_OnClick();
	}

	http.Sync = false;
}

