/*******************************************************************************
 * @description    : 리스트 조회 - 이벤트
*******************************************************************************/
function div_search_btn_search_OnClick(obj)
{
    if(f_isValidSearch() == false) return;

    ds_Condition.Clear();

    // 넘길 파라메터 설정
    f_SetQueryDate();

    cf_setParam(ds_Condition, "FV_COUMN",      FV_COLUMN);
    cf_setParam(ds_Condition, "POS_AGENCY",    div_search.edt_agencyCd.Text);
    cf_setParam(ds_Condition, "FV_PREV_START", FV_PREV_START);
    cf_setParam(ds_Condition, "FV_PREV_END",   substr(FV_PREV_END, 0, 6));
    cf_setParam(ds_Condition, "FV_1ST_START",  FV_1ST_START);
    cf_setParam(ds_Condition, "FV_1ST_END",    FV_1ST_END);
    cf_setParam(ds_Condition, "FV_2ND_START",  FV_2ND_START);
    cf_setParam(ds_Condition, "FV_2ND_END",    FV_2ND_END);
    cf_setParam(ds_Condition, "FV_3RD_START",  FV_3RD_START);
    cf_setParam(ds_Condition, "FV_3RD_END",    FV_3RD_END);
    cf_setParam(ds_Condition, "FV_4TH_START",  FV_4TH_START);
    cf_setParam(ds_Condition, "FV_4TH_END",    FV_4TH_END);
    cf_setParam(ds_Condition, "FV_5TH_START",  FV_5TH_START);
    cf_setParam(ds_Condition, "FV_5TH_END",    FV_5TH_END);
    cf_setParam(ds_Condition, "FV_6TH_START",  FV_6TH_START);
    cf_setParam(ds_Condition, "FV_6TH_END",    FV_6TH_END);

    // 트랜잭션 설정
    var sSvcID  = "svcList";
    var sInDs   = "nc_input_fieldDs=ds_Condition";
    var sOutDs  = "ds_list=ds_list";
    var sArg    = "sktst.bas.PRM#getChgrgUserWeeklyDeal";

    // 공통 트랜잭션 호출
    cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
}



/*******************************************************************************
* 기능명 : 조회 유효성
*******************************************************************************/
function f_isValidSearch()
{
    if(uf_isNull(div_search.cal_StrdYM.Value) == true)
    {
        alert("조회년월를 선택하시오.");
        div_search.cal_StrdYM.SetFocus();
        return false;
    }
    return true;
}
