 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{    

    switch(sSvcID)
    {
    case "svcList":
	    f_drawGrid();
        f_setGridData();
        break;        

    default:
        break;
    }
}

