/*******************************************************************************
* 함수명 : f_SetQueryDate
* 기능명 : 판매점 증감현황조회를 위한 일자 Setting
*******************************************************************************/
function f_SetQueryDate()
{
    var sForLastMonth;

    // 전월
    FV_PREV_END      = AddDate(div_search.cal_StrdYM.Value + "01", -1);
    FV_PREV_START    = substr(FV_PREV_END, 0, 6) + "01";

    // 첫번째 주
    FV_1ST_START     = div_search.cal_StrdYM.Value + "01";
    sDueDate         = GetDay(FV_1ST_START);
    if (sDueDate    == 0) {
        FV_1ST_END   = FV_1ST_START;
    } else {
        FV_1ST_END   = AddDate(FV_1ST_START, 7 - sDueDate);
    }

    // 두번째 주
    FV_2ND_START     = AddDate(FV_1ST_END, 1);
    FV_2ND_END       = AddDate(FV_2ND_START, 6);

    // 세번째 주
    FV_3RD_START     = AddDate(FV_2ND_END, 1);
    FV_3RD_END       = AddDate(FV_3RD_START, 6);

    // 네번째 주
    FV_COLUMN        =  4;
    FV_4TH_START     = AddDate(FV_3RD_END, 1);
    FV_4TH_END       = AddDate(FV_4TH_START, 6);

    // 다섯번째 주
    FV_5TH_START     = AddDate(FV_4TH_END, 1);
    FV_5TH_END       = AddDate(FV_5TH_START, 6);

    if (substr(FV_5TH_START, 0, 6) == div_search.cal_StrdYM.Value) {
        FV_COLUMN    =  5;
    } else {
        FV_5TH_START = "";
        FV_5TH_END   = "";
    }

    if (substr(FV_5TH_START, 0, 6) == substr(FV_5TH_END, 0, 6)) {
    } else {
        sForLastMonth = ToInteger(substr(FV_5TH_END, 6, 2)) * (-1);
        FV_5TH_END    = AddDate(FV_5TH_END, sForLastMonth);
    }

    // 여섯번째 주
    FV_6TH_START     = AddDate(FV_5TH_END, 1);
    FV_6TH_END       = AddDate(FV_6TH_START, 6);

    if (substr(FV_6TH_START, 0, 6) == div_search.cal_StrdYM.Value) {
        FV_COLUMN    =  6;
    } else {
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (substr(FV_6TH_START, 0, 6) == substr(FV_6TH_END, 0, 6)) {
    } else {
        sForLastMonth = ToInteger(substr(FV_6TH_END, 6, 2)) * (-1);
        FV_6TH_END    = AddDate(FV_6TH_END, sForLastMonth);
    }

    if (FV_1ST_END  >= ToDay()) {
        FV_COLUMN    =  0;
        FV_1ST_START = "";
        FV_1ST_END   = "";
        FV_2ND_START = "";
        FV_2ND_END   = "";
        FV_3RD_START = "";
        FV_3RD_END   = "";
        FV_4TH_START = "";
        FV_4TH_END   = "";
        FV_5TH_START = "";
        FV_5TH_END   = "";
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (FV_2ND_END  >= ToDay()) {
        FV_COLUMN    =  1;
        FV_2ND_START = "";
        FV_2ND_END   = "";
        FV_3RD_START = "";
        FV_3RD_END   = "";
        FV_4TH_START = "";
        FV_4TH_END   = "";
        FV_5TH_START = "";
        FV_5TH_END   = "";
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (FV_3RD_END  >= ToDay()) {
        FV_COLUMN    =  2;
        FV_3RD_START = "";
        FV_3RD_END   = "";
        FV_4TH_START = "";
        FV_4TH_END   = "";
        FV_5TH_START = "";
        FV_5TH_END   = "";
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (FV_4TH_END  >= ToDay()) {
        FV_COLUMN    =  3;
        FV_4TH_START = "";
        FV_4TH_END   = "";
        FV_5TH_START = "";
        FV_5TH_END   = "";
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (FV_5TH_END  >= ToDay()) {
        FV_COLUMN    =  4;
        FV_5TH_START = "";
        FV_5TH_END   = "";
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }

    if (FV_6TH_END  >= ToDay()) {
        FV_COLUMN    =  5;
        FV_6TH_START = "";
        FV_6TH_END   = "";
    }
}

/*******************************************************************************
 * @description    : 조회된 자료를 Grid의 Dataset에 Setting
*******************************************************************************/
function f_setGridData()
{
    var pPreStl     = 0;
    var pPreTot     = 0;
    var pStlCnt     = 0;
    var pTotCnt     = 0;
    var pStlAdd     = 0;
    var pTotAdd     = 0;
    var pStlRate    = 0;
    var pTotRate    = 0;
    var pStlQty     = 0;
    var pStlAvg     = 0;
    var pStlSaleQty = 0;
    var pTotQty     = 0;
    var pTotAvg     = 0;
    var pTotSaleQty = 0;

    grd_list.Redraw = false;

    For(rCnt  = 0; rCnt < ds_list.rowcount; rCnt++) {

        // 전월대비 증감율
        // 최종 조회 주간 자료를 Temp Dataset에 복사하여 계산
        switch(FV_COLUMN)
        {
            case 1:
                pStlCnt = ds_list.GetColumn(rCnt, "FST_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "FST_TOT_CNT");
                break;
            case 2:
                pStlCnt = ds_list.GetColumn(rCnt, "SND_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "SND_TOT_CNT");
                break;
            case 3:
                pStlCnt = ds_list.GetColumn(rCnt, "TRD_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "TRD_TOT_CNT");
                break;
            case 4:
                pStlCnt = ds_list.GetColumn(rCnt, "FTH_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "FTH_TOT_CNT");
                break;
            case 5:
                pStlCnt = ds_list.GetColumn(rCnt, "FFH_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "FFH_TOT_CNT");
                break;
            case 6:
                pStlCnt = ds_list.GetColumn(rCnt, "STH_STL_CNT");
                pTotCnt = ds_list.GetColumn(rCnt, "STH_TOT_CNT");
                break;
        }

        pPreStl = ds_list.GetColumn(rCnt, "PREV_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "PREV_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "CUR_STL_CNT",      pStlCnt);
        ds_list.SetColumn(rCnt, "CUR_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "CUR_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "CUR_TOT_CNT",      pTotCnt);
        ds_list.SetColumn(rCnt, "CUR_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "CUR_TOT_ADD_RATE", pTotRate);

        // 1주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "PREV_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "PREV_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "FST_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "FST_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "FST_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "FST_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "FST_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "FST_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "FST_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "FST_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "FST_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "FST_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "FST_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "FST_TOT_SALE_AVG", pTotAvg);

        // 2주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "FST_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "FST_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "SND_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "SND_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "SND_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "SND_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "SND_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "SND_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "SND_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "SND_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "SND_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "SND_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "SND_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "SND_TOT_SALE_AVG", pTotAvg);

        // 3주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "SND_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "SND_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "TRD_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "TRD_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "TRD_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "TRD_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "TRD_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "TRD_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "TRD_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "TRD_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "TRD_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "TRD_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "TRD_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "TRD_TOT_SALE_AVG", pTotAvg);

        // 4주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "TRD_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "TRD_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "FTH_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "FTH_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "FTH_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "FTH_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "FTH_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "FTH_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "FTH_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "FTH_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "FTH_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "FTH_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "FTH_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "FTH_TOT_SALE_AVG", pTotAvg);

        // 5주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "FTH_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "FTH_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "FFH_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "FFH_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "FFH_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "FFH_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "FFH_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "FFH_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "FFH_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "FFH_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "FFH_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "FFH_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "FFH_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "FFH_TOT_SALE_AVG", pTotAvg);

        // 6주차 증감율 / 점당판매량
        pPreStl = ds_list.GetColumn(rCnt, "FFH_STL_CNT");
        pPreTot = ds_list.GetColumn(rCnt, "FFH_TOT_CNT");
        pStlCnt = ds_list.GetColumn(rCnt, "STH_STL_CNT");
        pTotCnt = ds_list.GetColumn(rCnt, "STH_TOT_CNT");
        pStlAdd = pStlCnt - pPreStl;
        pTotAdd = pTotCnt - pPreTot;
        if (pPreStl == 0) {
            pStlRate = 0;
        } else {
            pStlRate = round(pStlAdd * 100 / pPreStl, 2);
        }
        if (pPreTot == 0) {
            pTotRate = 0;
        } else {
            pTotRate = round(pTotAdd * 100 / pPreTot, 2);
        }
        ds_list.SetColumn(rCnt, "STH_STL_ADD_CNT",  pStlAdd);
        ds_list.SetColumn(rCnt, "STH_STL_ADD_RATE", pStlRate);
        ds_list.SetColumn(rCnt, "STH_TOT_ADD_CNT",  pTotAdd);
        ds_list.SetColumn(rCnt, "STH_TOT_ADD_RATE", pTotRate);

        //       점당 판매량
        pStlQty     = ds_list.GetColumn(rCnt, "STH_STL_SALE_CNT");
        pStlSaleQty = ds_list.GetColumn(rCnt, "STH_STL_SALE_QTY");
        pTotQty     = ds_list.GetColumn(rCnt, "STH_TOT_SALE_CNT");
        pTotSaleQty = ds_list.GetColumn(rCnt, "STH_TOT_SALE_QTY");
        if (pStlQty == 0) {
            pStlAvg = 0;
        } else {
            pStlAvg = round(pStlSaleQty / pStlQty);
        }
        if (pTotQty == 0) {
            pTotAvg = 0;
        } else {
            pTotAvg = round(pTotSaleQty / pTotQty);
        }
        ds_list.SetColumn(rCnt, "STH_STL_SALE_AVG", pStlAvg);
        ds_list.SetColumn(rCnt, "STH_TOT_SALE_AVG", pTotAvg);
    }

    grd_list.Redraw = true;
}

