function f_DateFormat(fSDate, fEDate)
{
    if (fEDate == "") {
	    return "";
	}

    var sReturnValue  = substr(fSDate, 0, 4) + "." + substr(fSDate, 4, 2) + "." + substr(fSDate, 6, 2) + " - ";
        sReturnValue += substr(fEDate, 0, 4) + "." + substr(fEDate, 4, 2) + "." + substr(fEDate, 6, 2);

    return sReturnValue;
}
