/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function form_OnLoadCompleted(obj)
{
	// 권한 설정
	cf_setAuth(pFrmNum);
	//btn_New.Visible = false;

    FV_ORG_ID   = gds_session.GetColumn(0,"orgId");
    FV_ORG_NM   = gds_session.GetColumn(0,"orgNm");
	FV_USER_GRP = gds_session.GetColumn(0, "userGrp");	// 소속구분;	// kimys

    div_search.edt_orgId.value = FV_ORG_ID;
    div_search.edt_orgNm.value = FV_ORG_NM;	

	// 그리드 체크박스 설정 및 정렬
	cf_setGrdChecknSortEvent(grd_list);

	// 공통 코드 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_UserGrp", "ZBAS_C_00250", "전체", null, false); // 사용자 그룹
	cf_setCommonCode("grd_list", "ZBAS_C_00210", null, 3, false); //직첵


	http.Sync = true;
	cf_checkCommonCode();

//  화면 Open시 바로 전체 조회하지 않음...
//	f_search();	
	http.Sync = false;
}


/*******************************************************************************
 * @description    : 목록 조회
*******************************************************************************/
function f_search()
{
	if(uf_isNull(div_search.edt_PortalID.Text) == false){
		cf_setParam(ds_condition, "PORTAL_USER_ID", div_search.edt_PortalID.Text);
	}

    var sSvcID = "getUserList";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_output_1=userList";
	var sArg   = "sktst.bas.USM#getUserList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}


/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if ( sSvcID == "getUserList" )
	{
		// hidden 조회조건 초기화
		//div_search.edt_UserId.Text = "";
		//cf_trDebug(sSvcID, 1);

		// PORTAL 사용자 정보 조회
		f_PortalUserInfo();
	}

}

/*******************************************************************************
 * @description    		: 선택 사용자 상세보기 - 이벤트
 * @param obj   		: Event가 발생된 Component
 * @param nRow   		: Event가 발생한 Cell의 Row Index
 * @param nCell   		: Event가 발생한 Cell의 Column Index
 * @param nX   			: Grid 내에서 Double Click된 마우스 포인트의 X 좌표
 * @param nY   			: Grid 내에서 Double Click된 마우스 포인트의 Y 좌표
 * @param nPivotIndex   : CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index
*******************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sArg  = "sInUserId="+quote(ds_output_1.GetColumn(nRow, "user_id"));
	sArg += " sInStatus="+quote("M");
	sArg += " sInModFlag="+quote("true");

	cf_openAuthPop("100000005", sArg , 700, 476);
}

