    // 그리드 체크박스 설정 및 정렬
	cf_setGrdChecknSortEvent(grd_list);

	http.Sync = true;
	cf_checkCommonCode();
	http.Sync = false;
}


/*******************************************************************************
 * @description    : 목록 조회
*******************************************************************************/
function f_search()
{
    var sSvcID = "getUserList";
	var sInDs  = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_PsMapping=userList";
	var sArg   = "sktst.bas.USM#getMMUserList";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}


/*******************************************************************************
 * @description    : 콜백
 * @param sSvcID   : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if ( sSvcID == "getUserList" )
	{
		// hidden 조회조건 초기화
		//div_search.edt_UserId.Text = "";
		//cf_trDebug(sSvcID, 1);
	}

	if ( sSvcID == "svcUpdateMapping" )
	{
		alert("판매점 사용자 Mapping이 완료되었습니다.");
		//Close();
	}

}

/*******************************************************************************
 * @description           : 특정 파라미터 값 취득
 * @param sDsId           : 파라미터 이름
		sArg              : 지정할 파라메터
*******************************************************************************/
function f_getParamValue(paramName, sArg)
{
	var arrParams = sArg.split(" ", "webstyle");
	var returnVal = "";
	for(var i=0, n=arrParams.length; i<n; i++)
	{
		var arrParamInfo= arrParams[i].split("=","webstyle");
		var sParamName = arrParamInfo[0];
		var sparamValue = arrParamInfo[1];
		if(paramName == sParamName)
		{
			returnVal = sparamValue;
		}
	}

	return returnVal;

}

/*******************************************************************************
 * @description    : 조회 버튼 클릭- 이벤트
*******************************************************************************/
function btn_search_OnClick(obj)
{
	f_search();
}

/*******************************************************************************
 * @description    : 공통 엥터키 다운 - 이벤트
*******************************************************************************/
function f_commonKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13)
	{
		applyData();
		f_search();
	}
}

function grd_PortalMapping_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	alert(obj.GetCellProp("body", nCell, "colid"));
	alert(nY);
	alert(nCell);
	if(nCell == 0){
		var sChkValue = obj.GetCellProp("head", nCell, "text");	
		var ix = nX;

		if (sChkValue == 1) {
			for(var i = 0 ; i < ds_output_1.rowcount ; i++){

				if( ix != i){
					ds_output_1.SetColumn(i,"chk","0");
				}
			}
		}

		obj.Redraw = true;
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//alert(obj.GetCellValue(nRow,nCell));
	if(obj.GetCellValue(nRow,nCell) == 0){
		for(var i = 0 ; i < ds_output_1.rowcount ; i++){
			if( nRow != i){
				ds_output_1.SetColumn(i,"chk","0");
			}
		}
	}
}
