// 글로벌 세션정보
var FV_USER_ID;					// 사용자ID
var FV_USER_NM;					// 사용자명

var	FV_ORG_ID;					// 소속 
var	FV_ORG_NM;					// 소속명
var	FV_ORG_CL;					// 소속구분
var	FV_AGENCY_ID;           	// 소속 팀ID
var	FV_AGENCY_NM;           	// 소속 팀명
var	FV_AGENCY_UKEY_ID;      	// 소속 팀UKEY ID	

var	FV_ORG_AREA_ID;				// 근무지 ID
var	FV_ORG_AREA_NM;				// 근무지명
var	FV_ORG_AREA_POS_AGENCY_CD;	// 근무지 
var	FV_ORG_AREA_UKEY_ID;     	// 근무지ukeyid
var	FV_ORG_AREA_SUB_CD;      	// 근무지subcd
var	FV_ORG_AREA_CHANNEL_CD;  	// 근무지 channelcd	
var	FV_USER_GRP;			 	// 유저그룹

var	FV_DUTY;					// 사용자 직책
var	FV_ORG_AREA_CL1;			// 근무지 구분

var	FV_CON_MGMT_NO = "";
var	FV_PROD_CD = "";
var	FV_CON_DT = "";
var	FV_JOIN_WAY = "";
var	FV_PRC_GB = "";

var FV_SETOFF_AMT;
var FV_DAY;
var FV_DAY_AMT;
var FV_STANDARD_AMT;
var FV_RATE;
var FV_PREFER_AMT;
var FV_GRADE;
var FV_GRADE_AMT = "";
var FV_DDCT_AMT = "";
var FV_CON_AMT;
var FV_RATE_CD;
var FV_PLUS_AMT;
