var FV_PARENT_NUM;
var ADD_AMT;
var FV_INCV;

/*******************************************************************************
 * @description    : 최초 폼 로드시 발생 - 이벤트
*******************************************************************************/
function CSTADV00200_OnLoadCompleted(obj)
{

	http.Sync = true;

	f_getSession();
	f_setCombo();

	FV_PARENT_NUM = vFrmNum;

	lf_formVarInit();
	div_conInfo1.edt_conMgmtNo = FV_CON_MGMT_NO;
	if(FV_JOIN_WAY == "C"){
		div_conInfo8.sta_joinWay.Value = "기변";
	}

	// 고객의사 결정
	if ( FV_PRC_GB == "C" ) {
		btn_save.Visible = false;
	} else {
		btn_save.Visible = true;
		div_conInfo5.msk_adjAmt.Enable = true;
	}

	btn_cancel.Visible = false;
	if (!uf_IsNull(FV_CON_MGMT_NO)){
		f_getConsult_m();
		FV_NEW_YN = 'N';
	} else {
		div_conInfo1.edt_userId = FV_USER_ID;
		div_conInfo1.edt_userNm = FV_USER_NM;
		FV_NEW_YN = 'Y';
		f_getRateList();	
		//f_rdo_setting();
	}

	div_conInfo8.edt_conMgmtNo.SetFocus();
	div_conInfo8.edt_conMgmtNo.SetSel(0);
	div_conInfo2.cmb_comAgency.Index = 0;

	http.Sync = false;	

}

/*******************************************************************************
* 함수명 : df_reSet
* 기능명 : 화면 초기화
*******************************************************************************/
function df_reSet(sArg)
{
	http.Sync = true;

	f_getSession();
	f_setCombo();

	FV_PARENT_NUM = vFrmNum;
	FV_CON_MGMT_NO = "";
	FV_PROD_CD = "";
	FV_JOIN_WAY = "";
	FV_PRC_GB = "";
	cf_setArg("ds_reset", sArg);
	FV_CON_MGMT_NO = ds_reset.GetColumn(0,"p_con_mgmt_no");

	FV_PROD_CD = ds_reset.GetColumn(0,"p_prod_cd");
	FV_JOIN_WAY = ds_reset.GetColumn(0,"p_join_way");
	FV_PRC_GB = ds_reset.GetColumn(0,"p_prc_gb");

	if ( FV_PRC_GB == "C" ) {
		btn_save.Visible = false;
	} else {
		btn_save.Visible = true;
		div_conInfo5.msk_adjAmt.Enable = true;
	}	

	btn_cancel.Visible = false;
	if (!uf_IsNull(FV_CON_MGMT_NO)){
		f_getConsult_m();
		FV_NEW_YN = 'N';
	} else {
		div_conInfo1.edt_userId = FV_USER_ID;
		div_conInfo1.edt_userNm = FV_USER_NM;
		FV_NEW_YN = 'Y';
		f_getRateList();	
		//f_rdo_setting();
	}
	http.Sync = false;
}

function f_rdo_setting()
{
	div_conInfo3.rdo_corr_yn.value = 'N';
	div_conInfo3.rdo_power_yn.value = 'N';
	div_conInfo3.rdo_data_yn.value = 'N';
	div_conInfo3.rdo_call_yn.value = 'N';
	div_conInfo3.rdo_dis_yn.value = 'N';
	div_conInfo3.rdo_func_yn.value = 'N';
	div_conInfo3.rdo_lost_yn.value = 'N';
	div_conInfo3.rdo_return_yn.value = 'N';
	div_conInfo3.rdo_lease_yn.value = 'N';
	div_conInfo3.rdo_flood_yn.value = 'N';
	div_conInfo3.rdo_usim_yn.value = 'N';
	div_conInfo3.rdo_other_yn.value = 'N';
	div_conInfo5.cmb_buyCl.Index = 0;
}

/*******************************************************************************
 * @description         : 로컬 변수 초기화 초기화
*******************************************************************************/
function lf_formVarInit()
{    
    //상담이력조회 선택
    if(IsExistVar("p_con_mgmt_no",true)){ FV_CON_MGMT_NO = p_con_mgmt_no; } 
    if(IsExistVar("p_prod_cd",true)){ FV_PROD_CD = p_prod_cd; }  
    if(IsExistVar("p_con_dt",true)){ FV_CON_DT = p_con_dt; }   
    if(IsExistVar("p_join_way",true)){ FV_JOIN_WAY = p_join_way; }   
    if(IsExistVar("p_prc_gb",true)){ FV_PRC_GB = p_prc_gb; }  
}

function f_setCombo()
{
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_conInfo6.cmb_bankCd"       , "ZBAS_C_00060", "선택");	
//	cf_setCommonCode("div_conInfo6.cmb_comAgency"    , "ZBAS_C_00900", "선택");
//	cf_setCommonCode("div_conInfo6.cmb_policy"       , "ZBAS_C_00480", "선택");	
	cf_checkCommonCode(); 
}

/*******************************************************************************
 * @description    : 글로벌 섹션 정보
*******************************************************************************/
function f_getSession()
{
	FV_USER_ID        = gds_session.GetColumn(0, "loginid");						// 사용자ID
	FV_USER_NM        = gds_session.GetColumn(0, "usernm");							// 사용자명

	FV_ORG_ID         = gds_session.GetColumn(0, "orgid");							// 소속 
	FV_ORG_NM         = gds_session.GetColumn(0, "orgnm");							// 소속명
	FV_ORG_CL         = gds_session.GetColumn(0, "orgcl");							// 소속구분
	FV_AGENCY_ID      = gds_session.GetColumn(0, "posAgencyId");        			// 소속 팀ID
	FV_AGENCY_NM      = gds_session.GetColumn(0, "posAgencyNm");        			// 소속 팀명
	FV_AGENCY_UKEY_ID = gds_session.GetColumn(0, "posAgencyUkeyId");    			// 소속 팀UKEY ID	

	FV_ORG_AREA_ID            = gds_session.GetColumn(0, "orgareaid");				// 근무지 ID
	FV_ORG_AREA_NM            = gds_session.GetColumn(0, "orgareanm");				// 근무지명
	FV_ORG_AREA_POS_AGENCY_CD = gds_session.GetColumn(0, "orgareaposagencycd");		// 근무지 
	FV_ORG_AREA_UKEY_ID       = gds_session.GetColumn(0, "orgareaukeyid");      	// 근무지ukeyid
	FV_ORG_AREA_SUB_CD        = gds_session.GetColumn(0, "orgareaukeysubcd");   	// 근무지subcd
	FV_ORG_AREA_CHANNEL_CD    = gds_session.GetColumn(0, "orgareaukeychannelcd"); 	// 근무지 channelcd	
	FV_USER_GRP               = gds_session.GetColumn(0, "usergrp");				// 유저그룹

	FV_ORG_AREA_CL1           = gds_session.GetColumn(0, "orgareacl1");				// 근무지 구분
	FV_DUTY                   = gds_session.GetColumn(0, "duty");	   		    	// 직책		
}

/*******************************************************************************
 * @description : 팀 팝업 
 ******************************************************************************/
function btn_agency_OnClick(obj)
{
	var sArg  = "org_id="+quote(FV_ORG_ID); 
		sArg += " org_cl="+quote(FV_ORG_CL); 

	cf_callAgencyPop(sArg, ds_consult_m, "DEAL_CO_NM,POS_AGENCY,UKEY_AGENCY_CD" );
}

/*******************************************************************************
 * @description : 팀 editbox 변경시 
 ******************************************************************************/ 
function div_conInfo1_edt_agencyNm_OnChanged(obj,strText)
{
	ds_consult_m.SetColumn(0, "POS_AGENCY", "");
	ds_consult_m.SetColumn(0, "UKEY_AGENCY_CD", "");
	ds_cmbUser.Clear();
}

/*******************************************************************************
 * @description : 팀 editbox 엔터키 입력시 팀 팝업 
 ******************************************************************************/ 
function div_conInfo1_edt_agencyNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		ApplyData();
		var sArg  = "org_id="+quote(FV_ORG_ID); 
			sArg += " org_cl="+quote(FV_ORG_CL); 
		cf_callAgencyPop(sArg, ds_consult_m, "DEAL_CO_NM,POS_AGENCY,UKEY_AGENCY_CD" );
	}
}

function div_conInfo1_btn_dealCo_OnClick(obj)
{
	http.Sync = true;
	div_conInfo1.edt_dealCd.Text = "";

	var sArg  = "deal_co_cl1="+quote("A2,A3,A4,B1,B2,C1,Z1"); 
		sArg += " pos_agency="+quote(div_conInfo1.edt_posAgency.Text); // 소속 팀	옵션
		sArg += " only_stl_plc='Y'";

	cf_callDealPop(sArg, div_conInfo1, "edt_dealNm, edt_dealCd", 0, "Y");

    http.Sync = false;
}

/*******************************************************************************
 * @description : 상담처 editbox 변경시 
 ******************************************************************************/ 
function div_conInfo1_edt_dealNm_OnChanged(obj,strText)
{
	ds_consult_m.SetColumn(0, "CONPLC_CD", "");
}

/*******************************************************************************
 * @description : 상담처 editbox 엔터키 입력시 팀 팝업 
 ******************************************************************************/ 
function div_conInfo1_edt_dealNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		http.Sync = true;
		ApplyData();
		var sArg  = "deal_co_cl1=" + quote("A2,A3,A4,A5,B1,B2,C1,Z1");
			sArg += " pos_agency=" + div_conInfo1.edt_posAgency.Value; 

		cf_callDealPop(sArg, div_conInfo1, "edt_dealNm,edt_dealCd");

		http.Sync = false;
	}
}

