/*******************************************************************************
 * @description : 금액 초기화
 ******************************************************************************/
function f_clear()
{
	div_conInfo2.msk_standardAmt = 0;
	div_conInfo2.cal_runDtmFrom = "";
	div_conInfo2.cmb_color.Index = 0;
	div_conInfo2.cmb_rate.Index = 0;

	div_conInfo4.edt_day = 0;
	div_conInfo4.msk_dayAmt = 0;
	div_conInfo4.msk_standardAmt = 0;
	div_conInfo4.msk_perferAmt = 0;
	div_conInfo4.edt_eqpSt = "";
	div_conInfo4.edt_eqpStRate = 0;
	div_conInfo4.msk_eqpStAmt = 0;

	div_conInfo5.msk_conAmt.value = 0;
	div_conInfo5.msk_adjAmt.value = 0;
	div_conInfo5.msk_addAmt.value = 0;
	div_conInfo5.msk_prchAmt.value = 0;
	div_conInfo2.sta_rmk.Text = "";
	div_conInfo2.sta_comp_cl.Text = "";

	FV_DAY = "";
	FV_DAY_AMT = "";
	FV_STANDARD_AMT = "";
	FV_PREFER_AMT = "";
	FV_GRADE = "";
	FV_GRADE_AMT = "";
}

/*******************************************************************************
 * @description    :  단말기등급조회
*******************************************************************************/
function f_getRateList()
{
	ds_rate.ClearData();

	var sSvcID 	= "getRateList";
	var sInDs  	= "nc_input_fieldDs=ds_prod_m";
	var sOutDs 	= "ds_rate=ds_rate";
	var sArg	= "sktst.cst.ADV#getRateMgmt";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);


}

/*******************************************************************************
 * @description    :  단말기색상조회
*******************************************************************************/
function f_getColorList()
{
	ds_color.ClearData();

	var sSvcID 	= "getColorList";
	var sInDs  	= "nc_input_fieldDs=ds_consult_m";
	var sOutDs 	= "ds_color=ds_color ds_prod_m=ds_prod_m ds_consult_d=ds_consult_d ds_rate=ds_rate";
	var sArg	= "sktst.cst.ADV#getColorList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);


}

/*******************************************************************************
 * @description    :  조정금액
*******************************************************************************/
function f_getAddAmt()
{
	ds_add_amt.ClearData();
	cf_setParam(ds_add_amt, "CON_AMT" , FV_CON_AMT ); 

	var sSvcID 	= "getAddAmt";
	var sInDs  	= "nc_input_fieldDs=ds_add_amt";
	var sOutDs 	= "ds_add_amt=ds_add_amt";
	var sArg	= "sktst.cst.ADV#getAddAmt";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);

	ADD_AMT = 0;
	ADD_AMT = ds_add_amt.GetColumn(0,"ADD_AMT");
//	ADD_AMT = truncate(toNumber(ds_add_amt.GetColumn(0,"ADD_AMT"))/1000,0)*1000;
}



