/*******************************************************************************
 * @description    :  상담등록내역조회
*******************************************************************************/
function f_getConsult_m()
{
	http.Sync = true;
	ds_consult_m.ClearData();

    cf_setParam(ds_consult_m, "PROD_CD" , FV_PROD_CD ); 
    cf_setParam(ds_consult_m, "CON_MGMT_NO" , FV_CON_MGMT_NO ); 
    cf_setParam(ds_consult_m, "CON_DT" , FV_CON_DT ); 
    cf_setParam(ds_consult_m, "JOIN_WAY" , FV_JOIN_WAY ); 
    cf_setParam(ds_consult_m, "PRC_GB", FV_PRC_GB );

	var sSvcID 	= "getConsultM";
	var sInDs  	= "nc_input_fieldDs=ds_consult_m";
	var sOutDs 	= "ds_consult_m=ds_consult_m ds_color=ds_color ds_prod_m=ds_prod_m ds_consult_d=ds_consult_d ds_rate=ds_rate";
	var sArg	= "sktst.cst.ADV#getConsultM";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
	http.Sync = false;		
}

function f_getConsult_mgmt()
{
	http.Sync = true;
	ds_consult_m.ClearData();

    cf_setParam(ds_consult_m, "CON_MGMT_NO" , FV_CON_MGMT_NO ); 

	var sSvcID 	= "getConsultMgmt";
	var sInDs  	= "nc_input_fieldDs=ds_consult_m";
	var sOutDs 	= "ds_consult_m=ds_consult_m ds_consult_d=ds_consult_d ds_color=ds_color ds_prod_m=ds_prod_m ds_rate=ds_rate";
	var sArg	= "sktst.cst.ADV#getConsultMgmtNo";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
	http.Sync = false;		
}

/*******************************************************************************
 * @description    :  callBack
*******************************************************************************/
function f_callBack(sSvcID)
{
http.Sync = true;
    if (sSvcID == "getColorList") {
		if (ds_prod_m.GetColumn(0,"MKTG_DT") < '20070101' && ds_consult_m.GetColumn(0,"CON_DT") > '20120307') {						
			ds_consult_d_Changed();
		}
        if (ds_color.rowcount > 0) {
			div_conInfo2.cmb_color.Index = 0;
			if (ds_prod_m.GetColumn(0,"COMP_CL") == '1') {
				div_conInfo2.sta_comp_cl.Value = "자사";
			}else{
				div_conInfo2.sta_comp_cl.Value = "타사";
			}
		}	
		if (ds_prod_m.GetColumn(0,"MKTG_DT") < '20080101'){
//			alert("출시일이 2008년 1월 1일이전 중고단말기입니다.");
			FV_DAY_AMT = 0;
			FV_CON_AMT = ds_prod_m.GetColumn(0,"MIN_PRICE");
			div_conInfo5.msk_conAmt.value = FV_CON_AMT;

			f_getAddAmt();
			div_conInfo5.msk_addAmt.value = ADD_AMT;
			if(ADD_AMT < div_conInfo5.msk_adjAmt.value && div_conInfo2.cmb_rate.text != "E"){
				alert("조정금액이 조정한도액을 초가하였습니다.");
				div_conInfo5.msk_adjAmt.value = 0;
			}

			div_conInfo5.msk_prchAmt.value = FV_CON_AMT+toNumber(div_conInfo5.msk_adjAmt.value);
		}else{	
			//상계계수금액 계산
			FV_SETOFF_AMT = Abs(toNumber(div_conInfo4.msk_fixAmt.value) * (toNumber(div_conInfo4.edi_setoffNum.value)/100));
			div_conInfo4.msk_setoffAmt.value = FV_SETOFF_AMT;
		}
/*			
		if (div_conInfo2.cmb_rate.text == "A"){
			div_conInfo2.cmb_rateDtl.Visible = true;
			ds_rate_dtl.DeleteRow(3);
			div_conInfo2.cmb_rateDtl.Index = 0;
		} else if(div_conInfo2.cmb_rate.text == "B") {
			div_conInfo2.cmb_rateDtl.Visible = true;
			ds_rate_dtl.DeleteRow(3);
			ds_rate_dtl.DeleteRow(1);
			ds_rate_dtl.DeleteRow(0);
			div_conInfo2.cmb_rateDtl.Index = 0;
		} else {
			div_conInfo2.cmb_rateDtl.Visible = false;
		}		
*/		
		div_conInfo2.cmb_color.Index = 0;

    } else if (sSvcID == "getConsultM") {

		if (ds_consult_m.rowcount > 0) {

			//색상setting
			div_conInfo2.cmb_color.Value = ds_consult_m.GetColumn(0,"COLOR_CD");

			//등급setting
			div_conInfo2.cmb_rate.Text = ds_consult_m.GetColumn(0,"EQP_ST");
			FV_GRADE = div_conInfo2.cmb_rate.value;

			//상계계수setting
			div_conInfo4.edt_eqpSt = div_conInfo2.cmb_rate.Text;
			div_conInfo4.edt_eqpStRate = FV_GRADE;

			if (ds_prod_m.GetColumn(0,"MKTG_DT") < '20070101' && ds_consult_m.GetColumn(0,"CON_DT") > '20120307') {						
				ds_consult_d_Changed();
			}

			if (ds_prod_m.GetColumn(0,"FIX_AMT_YN") == 'N') {			
				//상계계수금액 계산
				FV_SETOFF_AMT = Abs(toNumber(div_conInfo4.msk_fixAmt.value) * (toNumber(div_conInfo4.edi_setoffNum.value)/100));
				div_conInfo4.msk_setoffAmt.value = FV_SETOFF_AMT;

				//일수 계산
				if (uf_IsNull(div_conInfo2.cal_runDtmFrom.value)){
					FV_DAY = 0;
					div_conInfo4.edt_day.value = FV_DAY;
				}else{	
					FV_DAY = (DateTime(Mid(cf_getDate(),0,8)) - DateTime(div_conInfo2.cal_runDtmFrom.value));
					div_conInfo4.edt_day.value = FV_DAY;
				}
				if (FV_PRC_GB != "S"){
					f_eqpStAmt_set();
					f_conAmt_set("N");
				}
				// 매입미확정 || 매입구분이 'N'인경우
				if ( FV_PRC_GB == "N" || (FV_PRC_GB == "P" && ds_consult_m.GetColumn(0,"BUY_CL") == 'N')) {
				} else {
					div_conInfo5.msk_conAmt.value = toNumber(div_conInfo5.msk_prchAmt.value) - toNumber(div_conInfo5.msk_adjAmt.value);
					if ( FV_PRC_GB != "C" ) {
						div_conInfo2.edt_ProdCd.Enable = false;
						div_conInfo2.btn_ProdCd.Enable = false;
						div_conInfo2.cmb_color.Enable = false;
						div_conInfo2.cal_runDtmFrom.Enable = false;
						div_conInfo2.edt_serNm.Enable = false;
						div_conInfo2.edt_imei.Enable = false;
						if (FV_PRC_GB == "S"){
							div_conInfo2.cmb_rate.Enable = false;
							div_conInfo2.cmb_buyCl.Enable = false;
						}	
						div_conInfo3.Enable = false;
						grd_dtlList.Enable = false;
						div_conInfo7.txa_cpnt.Enable = false;
						div_conInfo5.msk_adjAmt.Enable = false;
					}	
				}
			} else {
			    // 고객의사 저장
				// 매입미확정 || 매입구분이 'N'인경우
				if ( FV_PRC_GB == "N" || (FV_PRC_GB == "P" && ds_consult_m.GetColumn(0,"BUY_CL") == 'N') ||
					ds_consult_m.GetColumn(0,"PRC_ST") == '13') {
					if (ds_consult_m.GetColumn(0,"PRC_ST") == '13'){
						btn_save.Visible = true;
					}
					f_getAddAmt();			
					div_conInfo5.msk_addAmt.value = ADD_AMT;
					div_conInfo5.msk_conAmt.value = div_conInfo2.cmb_rate.value;
				// 고객의사 미저장	
				} else {
					div_conInfo5.msk_conAmt.value = toNumber(div_conInfo5.msk_prchAmt.value) - toNumber(div_conInfo5.msk_adjAmt.value);
					if ( FV_PRC_GB != "C" ) {
						div_conInfo2.edt_ProdCd.Enable = false;
						div_conInfo2.btn_ProdCd.Enable = false;
						div_conInfo2.cmb_color.Enable = false;
						div_conInfo2.cal_runDtmFrom.Enable = false;
						div_conInfo2.edt_serNm.Enable = false;
						div_conInfo2.edt_imei.Enable = false;
						if (FV_PRC_GB == "S"){
							div_conInfo2.cmb_rate.Enable = false;
							div_conInfo2.cmb_buyCl.Enable = false;
						}	
						div_conInfo3.Enable = false;
						grd_dtlList.Enable = false;
						div_conInfo7.txa_cpnt.Enable = false;
						div_conInfo5.msk_adjAmt.Enable = false;
					}
				}
				FV_CON_AMT = div_conInfo5.msk_conAmt.value;	


			}	
			if (ds_prod_m.GetColumn(0,"COMP_CL") == '1') {
				div_conInfo2.sta_comp_cl.Value = "자사";
			}else{
				div_conInfo2.sta_comp_cl.Value = "타사";
			}
			var sYear = toNumber(Mid(cf_getDate(),0,4))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),0,4));
			var sMonth = toNumber(Mid(cf_getDate(),4,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),4,2));
			var sDay = toNumber(Mid(cf_getDate(),6,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),6,2));
			if ( sDay >= 0 ) {
				sMonth = sMonth + 1;
			}
			div_conInfo2.sta_rmk.Value = (sYear*12)+sMonth + "개월";	
			f_getAddAmt();
			div_conInfo5.msk_addAmt.value = ADD_AMT;
			div_conInfo6_chk_polYn_OnClick();
/*			
			if (div_conInfo2.cmb_rate.text == "A"){
				div_conInfo2.cmb_rateDtl.Visible = true;
				ds_rate_dtl.DeleteRow(3);
				div_conInfo2.cmb_rateDtl.Index = 0;
			} else if(div_conInfo2.cmb_rate.text == "B") {
				div_conInfo2.cmb_rateDtl.Visible = true;
				ds_rate_dtl.DeleteRow(3);
				ds_rate_dtl.DeleteRow(1);
				ds_rate_dtl.DeleteRow(0);
				div_conInfo2.cmb_rateDtl.Index = 0;
			} else {
				div_conInfo2.cmb_rateDtl.Visible = false;
			}
*/	
		}	

		//div_conInfo2.cmb_color.Index = 0;
    } else if (sSvcID == "getRateList") {
		if (ds_rate.rowcount > 0) {
			//등급setting
			div_conInfo2.cmb_rate.Index = 0;
			FV_GRADE = div_conInfo2.cmb_rate.value;
			//상계계수setting
			div_conInfo4.edt_eqpSt = div_conInfo2.cmb_rate.Text;
			div_conInfo4.edt_eqpStRate = FV_GRADE;
/*			
			if (div_conInfo2.cmb_rate.text == "A"){
				div_conInfo2.cmb_rateDtl.Visible = true;
				ds_rate_dtl.DeleteRow(3);
				div_conInfo2.cmb_rateDtl.Index = 0;
			} else if(div_conInfo2.cmb_rate.text == "B") {
				div_conInfo2.cmb_rateDtl.Visible = true;
				ds_rate_dtl.DeleteRow(3);
				ds_rate_dtl.DeleteRow(1);
				ds_rate_dtl.DeleteRow(0);
				div_conInfo2.cmb_rateDtl.Index = 0;
			} else {
				div_conInfo2.cmb_rateDtl.Visible = false;
			}			
*/	
		}
    }else if (sSvcID == "saveConsultMgmt") {

		alert("저장 되었습니다");
		gf_movenCall(FV_PARENT_NUM, "btn_search_onClick");
		this.Close();

    }else if (sSvcID == "deleteConsult") {
		alert("취소 되었습니다");
		ds_consult_m.ClearData();
		ds_consult_d.ClearData();
		ds_consult_m.AddRow();
		Parent.btn_search_onClick();
		this.Close();
    }else if (sSvcID == "getConsultMgmt") {

//trace(ds_consult_d.SaveXML());  
		if (ds_consult_m.rowcount > 0) {

			//색상setting
			div_conInfo2.cmb_color.Value = ds_consult_m.GetColumn(0,"COLOR_CD");

			//등급setting
			div_conInfo2.cmb_rate.Text = ds_consult_m.GetColumn(0,"EQP_ST");
			FV_GRADE = div_conInfo2.cmb_rate.value;

			//상계계수setting
			div_conInfo4.edt_eqpSt = div_conInfo2.cmb_rate.Text;
			div_conInfo4.edt_eqpStRate = FV_GRADE;

			if (ds_prod_m.GetColumn(0,"MKTG_DT") < '20070101' && ds_consult_m.GetColumn(0,"CON_DT") > '20120307') {						
				ds_consult_d_Changed();
			}

			if (ds_prod_m.GetColumn(0,"FIX_AMT_YN") == 'N') {			
				//상계계수금액 계산
				FV_SETOFF_AMT = Abs(toNumber(div_conInfo4.msk_fixAmt.value) * (toNumber(div_conInfo4.edi_setoffNum.value)/100));
				div_conInfo4.msk_setoffAmt.value = FV_SETOFF_AMT;

				//일수 계산
				if (uf_IsNull(div_conInfo2.cal_runDtmFrom.value)){
					FV_DAY = 0;
					div_conInfo4.edt_day.value = FV_DAY;
				}else{	
					FV_DAY = (DateTime(Mid(cf_getDate(),0,8)) - DateTime(div_conInfo2.cal_runDtmFrom.value));
					div_conInfo4.edt_day.value = FV_DAY;
				}
				if (FV_PRC_GB != "S"){
					f_eqpStAmt_set();
					f_conAmt_set("N");
				}
			} else {
			    f_getAddAmt();			
				div_conInfo5.msk_addAmt.value = ADD_AMT;
				div_conInfo5.msk_conAmt.value = div_conInfo2.cmb_rate.value;
			}	
			if (ds_prod_m.GetColumn(0,"COMP_CL") == '1') {
				div_conInfo2.sta_comp_cl.Value = "자사";
			}else{
				div_conInfo2.sta_comp_cl.Value = "타사";
			}
			var sYear = toNumber(Mid(cf_getDate(),0,4))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),0,4));
			var sMonth = toNumber(Mid(cf_getDate(),4,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),4,2));
			var sDay = toNumber(Mid(cf_getDate(),6,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),6,2));
			if ( sDay >= 0 ) {
				sMonth = sMonth + 1;
			}
			div_conInfo2.sta_rmk.Value = (sYear*12)+sMonth + "개월";	
			f_getAddAmt();
			div_conInfo5.msk_addAmt.value = ADD_AMT;
			div_conInfo6_chk_polYn_OnClick();

		}
		//div_conInfo8.edt_conMgmtNo.SetSel(0);
	}
http.Sync = false;    
}

/*******************************************************************************
 * @description : 개통일자 editbox 변경시
 ******************************************************************************/ 
function div_conInfo2_cal_runDtmFrom_OnChanged(obj,strOldText,strNewText)
{
	if (strNewText == ''){
		f_clear();
	} else {
	http.Sync = true;
		FV_GRADE = div_conInfo2.cmb_rate.value;
		div_conInfo4.edt_eqpSt = div_conInfo2.cmb_rate.Text;
		div_conInfo4.edt_eqpStRate = FV_GRADE;		
		//일수 계산(금일 - 개통일) 
		FV_DAY = (DateTime(Mid(cf_getDate(),0,8)) - DateTime(strNewText));
		div_conInfo4.edt_day.value = FV_DAY;

		if (ds_prod_m.GetColumn(0,"FIX_AMT_YN") == 'N') {
			// 상담금액 계산
			f_eqpStAmt_set();
			f_conAmt_set("Y");
		}	
	http.Sync = false;	
	}	
}

/*******************************************************************************
 * @description : 단말기등급 combobox 변경시
 ******************************************************************************/ 
function div_conInfo2_cmb_rate_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
http.Sync = true;

	if (ds_prod_m.GetColumn(0,"FIX_AMT_YN") == 'N') {
		FV_GRADE = div_conInfo2.cmb_rate.value;
		div_conInfo4.edt_eqpSt = div_conInfo2.cmb_rate.Text;
		div_conInfo4.edt_eqpStRate = FV_GRADE;	

		// 등급급액 계산(선호도금액 * ( 등급(%) / 100 )
		FV_GRADE_AMT = Round(toNumber(FV_PREFER_AMT) * (toNumber(FV_GRADE) / 100));
		div_conInfo4.msk_eqpStAmt.value = FV_GRADE_AMT;
	}else{

		if (div_conInfo2.cmb_rate.Text == "선택") {
			FV_CON_AMT = 0;
			div_conInfo5.msk_conAmt.value = 0;
		} else {
			FV_CON_AMT = div_conInfo2.cmb_rate.value;
			div_conInfo5.msk_conAmt.value = div_conInfo2.cmb_rate.value;
		}	

		for(var i = 0 ; i < ds_rate.rowcount ; i++) {

			if (div_conInfo2.cmb_rate.Text == ds_rate.GetColumn(i,"GRADE")) {
				FV_INCV = ds_rate.GetColumn(i,"INCV_AMT");
			}
		}	

		div_conInfo5.msk_incvAmt.value = FV_INCV;

		f_getAddAmt();	
		div_conInfo5.msk_addAmt.value = ADD_AMT;
		if(ADD_AMT < div_conInfo5.msk_adjAmt.value && div_conInfo2.cmb_rate.text != "E"){
			alert("조정금액이 조정한도액을 초가하였습니다.");
			div_conInfo5.msk_adjAmt.value = 0;
		}

		div_conInfo5.msk_conAmt.value = FV_CON_AMT;		
		div_conInfo5.msk_prchAmt.value = toNumber(FV_CON_AMT)+toNumber(div_conInfo5.msk_adjAmt.value);
	}	

/*	
	ds_rate_dtl.Reset();
	if (div_conInfo2.cmb_rate.text == "A"){
		div_conInfo2.cmb_rateDtl.Visible = true;
//		ds_rate_dtl.DeleteRow(4);
		ds_rate_dtl.DeleteRow(3);
		div_conInfo2.cmb_rateDtl.Index = 0;
	} else if(div_conInfo2.cmb_rate.text == "B") {
		div_conInfo2.cmb_rateDtl.Visible = true;
		ds_rate_dtl.DeleteRow(3);
		ds_rate_dtl.DeleteRow(1);
		ds_rate_dtl.DeleteRow(0);
		div_conInfo2.cmb_rateDtl.Index = 0;
	} else {
		div_conInfo2.cmb_rateDtl.Visible = false;
	}
*/	
	f_conAmt_set("Y");

http.Sync = false;	
}

/*******************************************************************************
 * @description : 구성품정보 보유수량 grid 변경시
 ******************************************************************************/ 
function ds_consult_d_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// 보유수량 변경시에만 계산한다.
	if (strColumnID == "HLD_QTY") {
		if ( ds_consult_d.GetColumn(nRow,"QTY") <= ds_consult_d.GetColumn(nRow,"HLD_QTY") ){
			ds_consult_d.SetColumn(nRow,"DDCT_AMT","0");
		} else if ( ds_consult_d.GetColumn(nRow,"HLD_QTY") == 0 ) {
			ds_consult_d.SetColumn(nRow,"DDCT_AMT",toNumber(ds_consult_d.GetColumn(nRow,"PRICE")) * toNumber(ds_consult_d.GetColumn(nRow,"QTY")));
		} else {
			ds_consult_d.SetColumn(nRow,"DDCT_AMT",toNumber(ds_consult_d.GetColumn(nRow,"PRICE")) * toNumber(ds_consult_d.GetColumn(nRow,"HLD_QTY")));
		}	
		f_conAmt_set("Y");
	}
}

function ds_consult_d_Changed()
{
	for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
		ds_consult_d.SetColumn(i,"PRICE","0");
		ds_consult_d.SetColumn(i,"UNIT_PRC","0");
		ds_consult_d.SetColumn(i,"SALE_PRICE","0");
		ds_consult_d.SetColumn(i,"DDCT_AMT","0");
	}
}

/*******************************************************************************
 * @description    :  상담금액 구하기(구성품정보 차감금액 제외)
*******************************************************************************/
function f_eqpStAmt_set() {
http.Sync = true;
	//감가상각금액 계산
//	if (ds_prod_m.GetColumn(0,"MKTG_YN") == 'N') {
	if (ds_prod_m.GetColumn(0,"MKTG_DT") >= '20080101'){
		FV_DAY_AMT = Round(toNumber(div_conInfo4.msk_fixAmt.value) * (1 - (toNumber(div_conInfo4.edt_day.value) * 0.000913)));
		if (ds_prod_m.GetColumn(0,"MIN_PRICE") > FV_DAY_AMT){
			FV_DAY_AMT = ds_prod_m.GetColumn(0,"MIN_PRICE");
		}
	} else {
		FV_DAY_AMT = 0;
//		FV_CON_AMT = 10000;
		FV_CON_AMT = ds_prod_m.GetColumn(0,"MIN_PRICE");
		div_conInfo5.msk_conAmt.value = FV_CON_AMT;	

		f_getAddAmt();	
		div_conInfo5.msk_addAmt.value = ADD_AMT;
		if(ADD_AMT < div_conInfo5.msk_adjAmt.value && div_conInfo2.cmb_rate.text != "E"){
			alert("조정금액이 조정한도액을 초가하였습니다.");
			div_conInfo5.msk_adjAmt.value = 0;
		}

		div_conInfo5.msk_prchAmt.value = FV_CON_AMT+toNumber(div_conInfo5.msk_adjAmt.value);
	}	
	div_conInfo4.msk_dayAmt.value = FV_DAY_AMT;

	// 기준가 (감가상각금액 와 상계계수금액 중 작은금액이 기준가가 된다.
	if ( toNumber(div_conInfo4.msk_dayAmt.value) > toNumber(div_conInfo4.msk_setoffAmt.value) ) {
		if (FV_SETOFF_AMT < 0 ) {
			FV_SETOFF_AMT = 0;
		}
		FV_STANDARD_AMT = FV_SETOFF_AMT;
		div_conInfo4.msk_standardAmt.value = FV_STANDARD_AMT;
		div_conInfo2.msk_standardAmt.value = FV_STANDARD_AMT;
	} else {
		if (FV_DAY_AMT < 0 ) {
			FV_DAY_AMT = 0;
		}	
		FV_STANDARD_AMT = FV_DAY_AMT;
		div_conInfo4.msk_standardAmt.value = FV_STANDARD_AMT;
		div_conInfo2.msk_standardAmt.value = FV_STANDARD_AMT;
	}

	//선호도금액 계산
	FV_RATE = ds_prod_m.GetColumn(0,"RATE");
	FV_PREFER_AMT = Round(toNumber(FV_STANDARD_AMT) * (toNumber(FV_RATE) / 100));
	div_conInfo4.msk_perferAmt.value = FV_PREFER_AMT;
	//등급(상태)금액 계산
	FV_GRADE_AMT = Round(toNumber(FV_PREFER_AMT) * (toNumber(FV_GRADE) / 100));
	div_conInfo4.msk_eqpStAmt.value = FV_GRADE_AMT;
	// 배터리 감가상각 계산
	var sMinusAmt = 0;
	for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
		if( ds_consult_d.GetColumn(i,"DTL_CL") == "1" ||
			ds_consult_d.GetColumn(i,"DTL_CL") == "2" ) {
			sMinusAmt = Round(toNumber(ds_consult_d.GetColumn(i,"PRICE")) * (1 - (toNumber(div_conInfo4.edt_day.value) * 0.000913)));	
			//감가상각금액이 2,500 이상 일경우 2,500원으로 고정
			if ( sMinusAmt < 2500 && ds_prod_m.GetColumn(0,"MKTG_YN") == 'N') {
				sMinusAmt = 2500;
			}
			ds_consult_d.SetColumn(i,"PRICE",sMinusAmt);
			if ( ds_consult_d.GetColumn(i,"QTY") <= ds_consult_d.GetColumn(i,"HLD_QTY") ){
				ds_consult_d.SetColumn(i,"DDCT_AMT","0");
			} else if ( ds_consult_d.GetColumn(i,"HLD_QTY") == 0 ) {
				ds_consult_d.SetColumn(i,"DDCT_AMT",toNumber(ds_consult_d.GetColumn(i,"PRICE")) * toNumber(ds_consult_d.GetColumn(i,"QTY")));
			} else {	
				ds_consult_d.SetColumn(i,"DDCT_AMT",toNumber(ds_consult_d.GetColumn(i,"PRICE")) * toNumber(ds_consult_d.GetColumn(i,"HLD_QTY")));
			}
			sMinusAmt = 0;
		}	
	}
http.Sync = false;	
}

/*******************************************************************************
 * @description    :  상담금액 구하기(최종)
*******************************************************************************/
function f_conAmt_set(proc_cl) {
	if (ds_prod_m.GetColumn(0,"FIX_AMT_YN") == 'Y') {
		FV_GRADE_AMT = div_conInfo2.cmb_rate.value;
	}
	// 신규등록으로 open시
	if ( FV_GRADE_AMT == '' || FV_GRADE_AMT == 0 ) {

	// 목록조회에서 open시
	} else {
		FV_DDCT_AMT = 0;
		for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
			FV_DDCT_AMT = toNumber(FV_DDCT_AMT) + toNumber(ds_consult_d.GetColumn(i,"DDCT_AMT"));
		}
		if (div_conInfo2.cmb_rate.Text == 'D' || div_conInfo2.cmb_rate.Text == 'E'){
		    FV_CON_AMT = toNumber(FV_GRADE_AMT);
		} else {
			if (ds_consult_m.GetColumn(0,"CON_DT") > '20120419' && div_conInfo2.cmb_rate.Text == 'B'){
				FV_CON_AMT = toNumber(FV_GRADE_AMT);
			} else {
				FV_CON_AMT = toNumber(FV_GRADE_AMT) - toNumber(FV_DDCT_AMT);
			}
		}
		if (ds_prod_m.GetColumn(0,"MAX_PRICE") < FV_CON_AMT) {
//			alert("상담중인 중고단말기 구입 최고가는 "+ ds_prod_m.GetColumn(0,"MAX_PRICE")+"원 입니다.");
			FV_CON_AMT = ds_prod_m.GetColumn(0,"MAX_PRICE");
		}

		f_getAddAmt();			
		div_conInfo5.msk_addAmt.value = ADD_AMT;
		if(ADD_AMT < div_conInfo5.msk_adjAmt.value && div_conInfo2.cmb_rate.text != "E"){
			alert("조정금액이 조정한도액을 초가하였습니다.");
			div_conInfo5.msk_adjAmt.value = 0;
		}
		div_conInfo5.msk_conAmt.value = truncate(FV_CON_AMT/100,0)*100;	
		div_conInfo5.msk_prchAmt.value = truncate(FV_CON_AMT/100,0)*100+toNumber(div_conInfo5.msk_adjAmt.value);
	}

}

/*******************************************************************************
 * @description    : 계좌등록 Popup Open
