	dialog("CST::CSTADV00300.xml","", 320,200);
}

/*******************************************************************************
 * @description    : 상담내역 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{

	if ( !lf_saveValidationCheck() ) {
		return;
	}

    ds_consult_m.SetColumn(0, "COLOR_CD", 		div_conInfo2.cmb_color.value);
	ds_consult_m.SetColumn(0, "EQP_ST_TXT", 	div_conInfo2.cmb_rate.text);
	ds_consult_m.SetColumn(0, "COM_AGENCY", 	div_conInfo2.cmb_comAgency.value);
	ds_consult_m.SetColumn(0, "PRC_GB", 	    FV_PRC_GB);
	FV_RATE_CD = ds_consult_m.GetColumn(0,"EQP_ST");

//    trace( " new EQP_ST  ======== " + ds_consult_m.GetColumn(0,"EQP_ST"));

    ds_inMaster.InsertRow(0);
	ds_inMaster.SetColumn(0, "in_schd_dt", 		DateTime(Mid(cf_getDate(),0,8)));
	ds_inMaster.SetColumn(0, "pos_agency_id", 	div_conInfo1.edt_posAgency.value);
	ds_inMaster.SetColumn(0, "prchs_plc_id", 	div_conInfo1.edt_dealCd.value);
	ds_inMaster.SetColumn(0, "in_cl", 			"114");
	ds_inMaster.SetColumn(0, "in_fix_qty", 		"1");
	ds_inMaster.SetRowType(0,"Insert");

	for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
		if (ds_consult_d.GetColumn(i,"HLD_QTY") > 0) {
			ds_prodSerNumList.AddRow();
			ds_prodSerNumList.SetColumn(i, "color_cd", 		"99");
			ds_prodSerNumList.SetColumn(i, "prod_cd", 		ds_consult_d.GetColumn(i,"CMP_PROD_CD"));
			ds_prodSerNumList.SetColumn(i, "prod_cl", 		ds_consult_d.GetColumn(i,"PROD_CL"));
			ds_prodSerNumList.SetColumn(i, "in_qty", 		ds_consult_d.GetColumn(i,"HLD_QTY"));
			ds_prodSerNumList.SetColumn(i, "in_amt", 		ds_consult_d.GetColumn(i,"PRICE"));
		}	
	}

	//단말기정보 추가
	ds_prodSerNumList.insertRow(0);
	ds_prodSerNumList.SetColumn(0, "ser_num" , div_conInfo2.edt_serNm.Text );
	ds_prodSerNumList.SetColumn(0, "color_cd", div_conInfo2.cmb_color.Value );
	ds_prodSerNumList.SetColumn(0, "prod_cd" , div_conInfo2.edt_prodCd.Text );
	ds_prodSerNumList.SetColumn(0, "prod_cl" , "1" );
	ds_prodSerNumList.SetColumn(0, "in_qty"  , "1" );
	//ds_prodSerNumList.SetColumn(0, "in_amt"  , div_conInfo5.msk_conAmt.Value );
	ds_prodSerNumList.SetColumn(0, "in_amt"  , div_conInfo5.msk_prchAmt.Value );
	ds_prodSerNumList.SetColumn(0, "eqp_st"  , div_conInfo2.cmb_rate.Text );

 	var sSvcID = "saveConsultMgmt";
	var sInDs  = "ds_consult_m=ds_consult_m ds_consult_d=ds_consult_d ds_inMaster=ds_inMaster:A ds_prodSerNumList=ds_prodSerNumList:U";
	//var sInDs  = "ds_consult_m=ds_consult_m ds_consult_d=ds_consult_d";
	var sOutDs = "ds_prchsMgmtNo=nc_output_fieldDs";
	var sArg   = "";

	if(gds_session.GetColumn(0, "loginid") == 'OPS0040002'){
		alert("FV_NEW_YN:"+FV_NEW_YN+" | FV_PRC_GB:"+FV_PRC_GB+" | sArg:"+sArg+" | st:"+ ds_consult_m.GetColumn(0,"PRC_ST") );
	}

	if ( FV_NEW_YN == 'N' ){
		if (FV_PRC_GB == "S"){
			sArg = "sktst.cst.ADV#updatePrchsCust";
		} else {
			sArg = "sktst.cst.ADV#savePrchs";
		}
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
	} else if (uf_IsNull(FV_PRC_GB)){
		if (ds_consult_m.GetColumn(0,"PRC_ST") == '11'){
			sArg = "sktst.cst.ADV#savePrchs";
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
		} else if (ds_consult_m.GetColumn(0,"PRC_ST") == '13' ||
				   ds_consult_m.GetColumn(0,"PRC_ST") == '14' ||
				   ds_consult_m.GetColumn(0,"PRC_ST") == '23' ||
				   ds_consult_m.GetColumn(0,"PRC_ST") == '2C' ) {
				   sArg = "sktst.cst.ADV#updatePrchsCust";
				   cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
		} else {
			alert("검수대상이 아닙니다. 진행상태("+ds_consult_m.GetColumn(0,"PRC_NM")+")를 확인하세요.");
		}		   
	} else {
//		sArg = "sktst.cst.ADV#saveConsultMgmt";
		alert("error");
	}

	return;
}

/*******************************************************************************
 * @description    :  상담등록 저장시 validation check
*******************************************************************************/
function lf_saveValidationCheck()
{
    var bFlag = true;

	if ( div_conInfo5.cmb_buyCl.value == "Y" && div_conInfo6.chk_polYn.Value == '1' 
		&& toNumber(div_conInfo5.msk_prchAmt.Value) > 50000 ) {
		alert("본단말기는 정책 대상이 아닙니다. 정책대상여부를 확인하세요.");
		return false;		
	}	

	if ( div_conInfo5.cmb_buyCl.Value == "C") {
		alert("검수구분을 선택하세요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.value == "Y" && uf_IsNull(div_conInfo9.edt_slaeEqp.Text)) {
		alert("판매등급를 선택해 주십시요.");
		return false;
	}	

    if ( uf_IsNull(div_conInfo1.edt_dealNm.Text) || uf_IsNull(div_conInfo1.edt_dealCd.Text) ) {
		alert("상담처를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.edt_serNm.Text) ) {
		alert("일련번호를 입력해 주십시요.");
		return false;
	}

	if ( length(div_conInfo2.edt_serNm.Text) != 6 && 
	     length(div_conInfo2.edt_serNm.Text) != 7 && 
		 length(div_conInfo2.edt_serNm.Text) != 11 &&
		 length(div_conInfo2.edt_serNm.Text) != 12) {
		alert("일련번호는 7자리 또는 11자리 또는 12자리만 입력가능합니다.");
		return false;
	}


	if ( uf_IsNull(div_conInfo1.edt_userId.Text) )	{
		alert("처리자를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.edt_ProdCd.Text) || uf_IsNull(div_conInfo2.edt_ProdNm.Text) ) {
		alert("상품을 선택해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.cmb_color.Value) || div_conInfo2.cmb_color.Text == "") {
		alert("색상을 선택해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_conInfo2.cmb_comAgency.Value) ) {
		alert("통신사를 선택해 주십시요.");
		return false;
	}

	if (uf_IsNull(div_conInfo2.edt_imei.Text)) {
		alert("IMEI를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.value == "Y" &&
		(uf_IsNull(div_conInfo2.cmb_rate.Value) ||
		 div_conInfo2.cmb_rate.Value == "100")) {
		alert("단말기등급을 선택해 주십시요.");
		return false;
	}
/*	
	if ( div_conInfo5.cmb_buyCl.value == "Y") {
		for(var i = 0 ; i < ds_consult_d.rowcount ; i++) {
			if( ds_consult_d.GetColumn(i,"DTL_CL") == "1" && ds_consult_d.GetColumn(i,"HLD_QTY") == 0 ) {
				alert("기본배터리는 필수구성품 입니다.");
				return false;
			}	
		}
    }
 */   
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo6.edt_custNm.Text) ) {
		alert("고객명을 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo6.edt_telNo.Text) ) {
		alert("연락처를 입력해 주십시요.");
		return false;
	}	

	if ( div_conInfo6.chk_chagDedtYn.value == '0' && 
	    (div_conInfo5.cmb_buyCl.Value == 'Y' && 
		(uf_IsNull(div_conInfo6.cmb_bankCd.Value)||div_conInfo6.cmb_bankCd.Value==" " ))) {
		alert("은행을 선택해 주십시요.");
		return false;
	}

	if ( div_conInfo6.chk_chagDedtYn.value == '0' && 
		(div_conInfo5.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo6.edt_accNo.Text))) {
		alert("계좌번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo6.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo6.edt_svcNo.Text) ) {
		alert("서비스번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo6.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo6.edt_svcMgmtNo.Text) ) {
		alert("서비스관리번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo6.chk_chagDedtYn.value == '1' && length(div_conInfo6.edt_svcMgmtNo.Text) != 10) {
		alert("서비스관리번호는 10자리만 입력가능합니다.");
		return false;
	}

/*
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_call_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.통품테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_corr_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.내부점검테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_data_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다. Data성능테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_dis_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.화면테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_flood_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.침수여부테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_func_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.기능테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_lease_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.분실여부 점검을 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_lost_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.분실여부 점검을 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_other_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.타사/해외 단말 점검을 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_power_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.전원장치테스트를 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_return_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.보상반납대상점검을 확인하세요.");
		return false;
	}						
	if ( div_conInfo5.cmb_buyCl.Value == 'Y' && div_conInfo3.rdo_usim_yn.Value == 'N' 
			&& ds_prod_m.GetColumn(0,"MFACT_ID") != '56449'
			&& div_conInfo6.chk_polYn.Value != '1') {
		alert("구매가 불가능합니다.USIM기변점검을 확인하세요.");
		return false;
	}								
*/

    return bFlag;
}


/*******************************************************************************
 * @description    :  상품 editbox 엔터키 입력시 상품 팝업 
*******************************************************************************/
function div_conInfo2_edt_ProdCd_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		f_clear();
		cf_commPopProd("div_conInfo2.edt_ProdCd", "div_conInfo2.edt_ProdNm", 13);

		if ( uf_IsNull(div_conInfo2.edt_ProdCd.Text) ) {
		} else {
			if ( ds_prod_m.GetColumn(0,"MFACT_ID") == '56449' ) {	
				div_conInfo2.edt_imei.Enable = true;
			} else {
				div_conInfo2.edt_imei.Enable = false;
			}
			f_getColorList();
		}
	}
}

/*******************************************************************************
 * @description    : 상담내역 취소
*******************************************************************************/
function btn_cancel_OnClick(obj)
{
	var sSvcID = "deleteConsult";
	var sInDs  = "ds_consult_m=ds_consult_m ds_consult_d=ds_consult_d";
	var sOutDs = "";
	var sArg   = "sktst.cst.ADV#deletePrchs";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

	return;
}

/*******************************************************************************
 * @description : FULLSET checkbox 클릭시
 ******************************************************************************/ 
function div_conInfo5_chk_fullSetYn_OnClick(obj,strValue)
{
	if (ds_prod_m.GetColumn(0,"MKTG_YN") == 'N') {
		if ( div_conInfo5.chk_fullSetYn.value == "Y" ) {
			//상담가격에 10%
			FV_PLUS_AMT = Round(toNumber(FV_CON_AMT) * 0.1);
			// 30,000원 이상시 30,000원으로 고정
			if ( FV_PLUS_AMT > 30000 ) {
				FV_CON_AMT = toNumber(FV_CON_AMT) + 30000;
			} else {
				FV_CON_AMT = toNumber(FV_CON_AMT) + toNumber(FV_PLUS_AMT);
			}
			div_conInfo5.msk_conAmt.value = FV_CON_AMT;	
		} else {
			f_conAmt_set("Y");
		}
	}	
}

