 * @description    : 상담자 콤보 셋팅
*******************************************************************************/
function f_getSaleChgrgId()
{
    sf_getChgrgIdList("ds_input", "ds_cmbConUserId", div_conInfo1.edt_dealCd.Text );  
}

/*******************************************************************************
 * @description    : 닫기버튼 클릭
*******************************************************************************/
function btn_close_OnClick(obj)
{
	this.close();
}


function div_search_btn_zipCd_OnClick(obj)
{
	cf_commPopPost("div_conInfo6.edt_zipCd1","div_conInfo6.edt_zipCd2","div_conInfo6.edt_addr");	
}

function div_conInfo5_msk_adjAmt_OnChanged(obj,strText)
{
	if(toNumber(ADD_AMT) < toNumber(div_conInfo5.msk_adjAmt.value) && div_conInfo2.cmb_rate.text != "E"){
		alert("조정금액이 조정한도액을 초가하였습니다.");
		div_conInfo5.msk_adjAmt.value = 0;
	}
	div_conInfo5.msk_prchAmt.value = toNumber(div_conInfo5.msk_conAmt.value) + toNumber(div_conInfo5.msk_adjAmt.value);
}

function btn_all_OnClick(obj)
{
	div_conInfo3.rdo_call_yn.Value = 'Y';
	div_conInfo3.rdo_corr_yn.Value = 'Y';
	div_conInfo3.rdo_data_yn.Value = 'Y';
	div_conInfo3.rdo_dis_yn.Value = 'Y'; 
	div_conInfo3.rdo_flood_yn.Value = 'Y'; 
	div_conInfo3.rdo_func_yn.Value = 'Y'; 
	div_conInfo3.rdo_lease_yn.Value = 'Y'; 
	div_conInfo3.rdo_lost_yn.Value = 'Y'; 
	div_conInfo3.rdo_other_yn.Value = 'Y'; 
	div_conInfo3.rdo_power_yn.Value = 'Y'; 
	div_conInfo3.rdo_return_yn.Value = 'Y'; 
	div_conInfo3.rdo_usim_yn.Value = 'Y'; 
}

function div_conInfo6_chk_polYn_OnClick(obj,strValue)
{
	if(div_conInfo6.chk_polYn.Value == "1") {
		div_conInfo6.cmb_policy.Visible = true;
	} else {
		div_conInfo6.cmb_policy.Value = "";
		div_conInfo6.cmb_policy.Visible = false;
	}	
}

function div_conInfo8_edt_conMgmtNo_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		FV_CON_MGMT_NO = div_conInfo8.edt_conMgmtNo.Text; 
		f_getConsult_mgmt();
	}
}

function btn_sale_eqp_OnClick(obj)
{
	var sArg = " p_con_mgmt_no=" + quote( div_conInfo8.edt_conMgmtNo.Text );
	    sArg += " p_join_way=" + quote( FV_JOIN_WAY );
	    sArg += " p_skn_eqp_st=Z";
	    sArg += " p_prc_gb=A";

	if (FV_JOIN_WAY == "C") {    
		dialog("CST::CSTADV01900.xml", sArg , -1, -1,"Scroll=true");     
	} else {
		dialog("CST::CSTADV01800.xml", sArg , -1, -1,"Scroll=true");     
	}
}


function f_saleEqpSt(saleEqpSt,eqpSt,eqpE) 
{
	cf_setParam(ds_consult_m, "SALE_EQP_ST" , saleEqpSt ); 
/*	
	if (eqpSt == 'A'){
		div_conInfo2.cmb_rate.Index = 2;
	}
	if (eqpSt == 'B'){
		div_conInfo2.cmb_rate.Index = 3;
	}
	if (eqpSt == 'E'){
		div_conInfo2.cmb_rate.Index = 4;
	}
*/
	if (eqpSt == 'A'){
		div_conInfo2.cmb_rate.Index = 2;
	}

	if(FV_JOIN_WAY == "C"){
		if (eqpSt == 'E'){
			div_conInfo2.cmb_rate.Index = 3;
		}
	} else {
		if (eqpSt == 'B'){
			div_conInfo2.cmb_rate.Index = 3;
		}
		if (eqpSt == 'E'){
			div_conInfo2.cmb_rate.Index = 4;
		}
	}

	div_conInfo2_cmb_rate_OnChanged();
}
