/*******************************************************************************
 * @description    :  상담등록내역조회
*******************************************************************************/
function f_getPrchs()
{
	http.Sync = true;
	ds_consult_m.ClearData();

    cf_setParam(ds_consult_m, "PROD_CD" , FV_PROD_CD ); 
    cf_setParam(ds_consult_m, "CON_MGMT_NO" , FV_CON_MGMT_NO ); 
    cf_setParam(ds_consult_m, "PRCHS_MGMT_NO" , FV_PRCHS_MGMT_NO ); 

	var sSvcID 	= "getPrchs";
	var sInDs  	= "nc_input_fieldDs=ds_consult_m";
	var sOutDs 	= "ds_consult_m=ds_consult_m ds_color=ds_color ds_prod_m=ds_prod_m ds_consult_d=ds_consult_d ds_rate=ds_rate ds_prchsCustRmks_List=ds_rmk_list";
	var sArg	= "sktst.cst.ADV#getPrchsCustRmksList";

	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);
	http.Sync = false;		
}

/*******************************************************************************
 * @description    :  callBack
*******************************************************************************/
function f_callBack(sSvcID)
{
http.Sync = true;
    if (sSvcID == "getPrchs") {
		if (ds_consult_m.rowcount > 0) {
			//색상setting
			div_conInfo2.cmb_color.Value = ds_consult_m.GetColumn(0,"COLOR_CD");
			FV_EQP_ST = ds_consult_m.GetColumn(0,"EQP_ST");
if(gds_session.GetColumn(0, "loginid") == 'OPS0040002'){
//alert("1.EQP_ST:"+ds_consult_m.GetColumn(0,"EQP_ST"));
//  alert("FV_EQP_ST:"+FV_EQP_ST);
}		
			//등급setting
			div_conInfo2.cmb_rate.Text = ds_consult_m.GetColumn(0,"EQP_ST");

			if (ds_prod_m.GetColumn(0,"COMP_CL") == '1') {
				div_conInfo2.sta_comp_cl.Value = "자사";
			}else{
				div_conInfo2.sta_comp_cl.Value = "타사";
			}
			var sYear = toNumber(Mid(cf_getDate(),0,4))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),0,4));
			var sMonth = toNumber(Mid(cf_getDate(),4,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),4,2));
			var sDay = toNumber(Mid(cf_getDate(),6,2))-toNumber(Mid(DateTime(div_conInfo2.cal_runDtmFrom.value),6,2));
			if ( sDay >= 0 ) {
				sMonth = sMonth + 1;
			}
			div_conInfo2.sta_rmk.Value = (sYear*12)+sMonth + "개월";	
			div_conInfo5_chk_polYn_OnClick();

			div_conInfo2.edt_ProdCd.Enable = false;
			div_conInfo2.btn_ProdCd.Enable = false;
			div_conInfo2.cmb_color.Enable = false;
			div_conInfo2.cal_runDtmFrom.Enable = false;
			div_conInfo2.edt_serNm.Enable = false;
			div_conInfo2.edt_imei.Enable = false;
//			div_conInfo2.cmb_rate.Enable = false;
			div_conInfo2.cmb_buyCl.Enable = false;
			grd_dtlList.Enable = false;		
			div_conInfo4.txa_cpnt.Enable = false;

			ds_prchsCustRmks.InsertRow(0);

			cf_setCommonCodeDsEnv();

			if (ds_consult_m.GetColumn(0,"PRC_ST") == '31' ||
				ds_consult_m.GetColumn(0,"PRC_ST") == '32' ||
				ds_consult_m.GetColumn(0,"PRC_ST") == '33' ||
				ds_consult_m.GetColumn(0,"PRC_ST") == '34' ){
				div_conInfo5.chk_polYn.Enable = false;
				div_conInfo5.chk_chagDedtYn.Enable = false;
				div_conInfo7.cmb_custYn.Enable = false;
				div_conInfo7.cmb_custYnDtl.Enable = false;
				div_conInfo7.chk_rmksOnly.Value = "1";
				div_conInfo7.chk_rmksOnly.Enable = false;
			}

			if (ds_consult_m.GetColumn(0,"BUY_CL") == 'N') {
				cf_setCommonCode("div_conInfo7.cmb_custYn"       , "ZBAS_C_00540", "선택");	
			} else if (ds_consult_m.GetColumn(0,"POL_YN") == '1') {
				cf_setCommonCode("div_conInfo7.cmb_custYn"       , "ZBAS_C_00520", "선택");	
				cf_setCommonCode("div_conInfo7.cmb_custYnDtl"    , "ZBAS_C_00530", "선택");	
			} else {
				cf_setCommonCode("div_conInfo7.cmb_custYn"       , "ZBAS_C_00500", "선택");	
				cf_setCommonCode("div_conInfo7.cmb_custYnDtl"    , "ZBAS_C_00510", "선택");				
			}

			cf_checkCommonCode();

			div_conInfo7.cust_Dt.Value = Left(cf_getDate(), 8);
			div_conInfo7.cmb_custYn.Index = 0;
			div_conInfo7.cmb_custYnDtl.Index = 0;

			if (FV_PRC_GB == 'N') {
				div_conInfo9.edt_conMgmtNo.SetSel(0);
			}	

		}

    }  else if (sSvcID == "savePrchsCustRmks") {
		alert("저장 되었습니다");
		if (FV_PRC_GB == 'N') {
			f_getPrchsNo();
		}else{
			f_getPrchs();
		}
	}	
http.Sync = false;    
}

/*******************************************************************************
 * @description    : 상담내역 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{

	if ( !lf_saveValidationCheck() ) {
		return;
	}

	if (FV_PRC_GB == 'N') {
		ds_prchsCustRmks.SetColumn(0, "PRCHS_MGMT_NO",    ds_consult_m.GetColumn(0,"PRCHS_MGMT_NO"));
	} else {
		ds_prchsCustRmks.SetColumn(0, "PRCHS_MGMT_NO",    FV_PRCHS_MGMT_NO);
	}

	ds_prchsCustRmks.SetColumn(0, "SEQ", 	          FV_RMKS_SEQ);
	ds_prchsCustRmks.SetColumn(0,"RETURN_CL",         div_conInfo7.div_conInfo9.rdo_divReqYn.value);

	var sSvcID = "savePrchsCustRmks";
	var sInDs  = "ds_prchsCustRmks=ds_prchsCustRmks ds_consult_m=ds_consult_m";
	var sOutDs = "";
	var sArg   = "sktst.cst.ADV#savePrchsCustRmks";
//trace(ds_consult_m.SaveXML()); 
//trace(ds_prchsCustRmks.SaveXML()); 

//	if (SubStr(ds_consult_m.GetColumn(0,"PRC_ST"), 0, 1 ) == '3') {
//		alert("검수대상이 아닙니다. 진행상태("+ds_consult_m.GetColumn(0,"PRC_NM")+")를 확인하세요.");
//	} else {
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
//	}

	return;
}

/*******************************************************************************
 * @description    :  상담등록 저장시 validation check
*******************************************************************************/
function lf_saveValidationCheck()
{
    var bFlag = true;

	if (FV_RMKS_USER_ID != "" && FV_RMKS_USER_ID != FV_USER_ID){
		alert("확인자가 상이하여 상담의견을 변경할수 없습니다.");
		return false;
	}
	if (div_conInfo7.cmb_custYn.Index == 0 && div_conInfo7.chk_rmksOnly.value != '1'){
		alert("고객의사를 선택하십시오.");
		return false;
	}
	if ((div_conInfo7.cmb_custYn.Value == '22' || div_conInfo7.cmb_custYn.Value == '26') &&
		div_conInfo7.cmb_custYnDtl.Index == 0 && div_conInfo7.chk_rmksOnly.value != '1'){
		alert("고객의사를 선택하십시오.");
		return false;
	}
	if (uf_IsNull(div_conInfo7.txa_prchs_rmks.Text)){
		alert("상담의견을 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo2.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo5.edt_custNm.Text) ) {
		alert("고객명을 입력해 주십시요.");
		return false;
	}

//	if ( div_conInfo2.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo5.edt_resNo.Text)) {
//		alert("주민번호를 입력해 주십시요.");
//		return false;
//	}

	if ( div_conInfo2.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo5.edt_telNo.Text) ) {
		alert("연락처를 입력해 주십시요.");
		return false;
	}	

//	if ( div_conInfo2.cmb_buyCl.Value == 'Y' && (uf_IsNull(div_conInfo5.edt_zipCd1.Text) || uf_IsNull(div_conInfo5.edt_zipCd2.Text))) {
//		alert("우편번호을 입력해 주십시요.");
//		return false;
//	}

//	if ( div_conInfo2.cmb_buyCl.Value == 'Y' && 
//	    (uf_IsNull(div_conInfo5.edt_addr.Text) || uf_IsNull(div_conInfo5.edt_dtlAddr.Text))) {
//		alert("주소를 입력해 주십시요.");
//		return false;
//	}		

	if ( div_conInfo5.chk_chagDedtYn.value == '0' && 
	    (div_conInfo2.cmb_buyCl.Value == 'Y' && 
		(uf_IsNull(div_conInfo5.cmb_bankCd.Value)||div_conInfo5.cmb_bankCd.Value==" " ))) {
		alert("은행을 선택해 주십시요.");
		return false;
	}

	if ( div_conInfo5.chk_chagDedtYn.value == '0' && 
		(div_conInfo2.cmb_buyCl.Value == 'Y' && uf_IsNull(div_conInfo5.edt_accNo.Text))) {
		alert("계좌번호를 입력해 주십시요.");
		return false;
	}

	if (div_conInfo5.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo5.edt_svcNo.Text) ) {
		alert("서비스번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.chk_chagDedtYn.value == '1' && uf_IsNull(div_conInfo5.edt_svcMgmtNo.Text) ) {
		alert("서비스관리번호를 입력해 주십시요.");
		return false;
	}

	if ( div_conInfo5.chk_chagDedtYn.value == '1' && length(div_conInfo5.edt_svcMgmtNo.Text) != 10) {
		alert("서비스관리번호는 10자리만 입력가능합니다.");
		return false;
	}

	if (ds_prod_m.GetColumn(0,"COMP_CL") != '1' && uf_IsNull(div_conInfo2.edt_imei.Text)) {
		alert("IMEI를 입력해 주십시요.");
		return false;
	}

    return bFlag;
}


/*******************************************************************************
 * @description    :  상품 editbox 엔터키 입력시 상품 팝업 
*******************************************************************************/
function div_conInfo2_edt_ProdCd_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		f_clear();
		cf_commPopProd("div_conInfo2.edt_ProdCd", "div_conInfo2.edt_ProdNm", 13);

		if ( uf_IsNull(div_conInfo2.edt_ProdCd.Text) ) {
		} else {
			if (div_conInfo2.edt_ProdCd.Text == "KGW1" ||
				div_conInfo2.edt_ProdCd.Text == "KGW2" ||
				div_conInfo2.edt_ProdCd.Text == "KGW3" ) {
				div_conInfo2.edt_imei.Enable = true;
			} else {
				div_conInfo2.edt_imei.Enable = false;
			}
			f_getColorList();
		}
	}
}

