		div_conInfo5.cmb_policy.Visible = false;
	}	
}

function div_conInfo7_cmb_custYn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (div_conInfo7.cmb_custYn.Value == "22" || div_conInfo7.cmb_custYn.Value == "26"){
		div_conInfo7.cmb_custYnDtl.Visible = true;
	} else {
		div_conInfo7.cmb_custYnDtl.Visible = false;
		div_conInfo7.cmb_custYnDtl.Value = "";
	}
	if (div_conInfo7.cmb_custYn.Value == "28") {
		div_conInfo7.div_conInfo9.Visible = true;
		div_conInfo7.div_conInfo9.left = 212;
	} else {
		div_conInfo7.div_conInfo9.Visible = false;
		div_conInfo7.div_conInfo9.left = 341;	
	}
}

function grd_rmkList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	div_conInfo7.cmb_custYn.Value = ds_prchsCustRmks_List.GetColumn(nRow,"PRC_ST");
	div_conInfo7_cmb_custYn_OnChanged();
	if(ds_prchsCustRmks_List.GetColumn(nRow,"PRC_ST") == '22' ||
	   ds_prchsCustRmks_List.GetColumn(nRow,"PRC_ST") == '26' ||
	   ds_prchsCustRmks_List.GetColumn(nRow,"PRC_ST") == '28' ){
	    div_conInfo7.cmb_custYnDtl.Value = ds_prchsCustRmks_List.GetColumn(nRow,"PRC_ST_DTL");
	    div_conInfo7.div_conInfo9.Visible = true;
	    div_conInfo7.div_conInfo9.rdo_divReqYn.Value = ds_prchsCustRmks_List.GetColumn(nRow,"RETURN_CL");
	} else {	
		div_conInfo7.cmb_custYnDtl.Value = "";
		div_conInfo7.div_conInfo9.Visible = false;
	}	
	div_conInfo7.txa_prchs_rmks.Value = ds_prchsCustRmks_List.GetColumn(nRow,"RMKS");
	div_conInfo7.cust_Dt.Value = ds_prchsCustRmks_List.GetColumn(nRow,"CUST_DT");
	FV_RMKS_SEQ = ds_prchsCustRmks_List.GetColumn(nRow,"SEQ");
	FV_RMKS_USER_ID = ds_prchsCustRmks_List.GetColumn(nRow,"INS_USER_ID");
	div_conInfo7.cmb_custYn.Enable = false;
	div_conInfo7.cmb_custYnDtl.Enable = false;
}

function btn_crear_OnClick(obj)
{
	div_conInfo7.cust_Dt.Value = Left(cf_getDate(), 8);
	div_conInfo7.cmb_custYn.Index = 0;
	div_conInfo7.cmb_custYnDtl.Index = 0;
	div_conInfo7.txa_prchs_rmks.Value = "";
	div_conInfo7.cmb_custYnDtl.Visible = false;
	if (ds_consult_m.GetColumn(0,"PRC_ST") == '31' ||
		ds_consult_m.GetColumn(0,"PRC_ST") == '32' ||
		ds_consult_m.GetColumn(0,"PRC_ST") == '33' ||
		ds_consult_m.GetColumn(0,"PRC_ST") == '34'){
		div_conInfo7.chk_rmksOnly.Value = "1";
	} else {
		div_conInfo7.chk_rmksOnly.Value = "0";
		div_conInfo7.cmb_custYn.Enable = true;
		div_conInfo7.cmb_custYnDtl.Enable = true;
	}
	FV_RMKS_SEQ = "";
	FV_RMKS_USER_ID = "";
}

function div_conInfo7_cmb_custYnDtl_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
