	if (div_conInfo7.cmb_custYnDtl.Value == "24" || div_conInfo7.cmb_custYnDtl.Value == "27") {
		div_conInfo7.div_conInfo9.Visible = true;
	} else {
		div_conInfo7.div_conInfo9.Visible = false;
	}
}

