/*******************************************************************************
 * @description    :  반송선택
*******************************************************************************/
function Div55_chk_returnYn_OnClick(obj,strValue)
{
	if(Div55.chk_returnYn.Value == "1") {
		div_conInfo8.edt_saleEqpSt.Text = '반송';
	} else {
		Div54.rdo_return.value = "";
		f_st_set();
	}	
}


/*******************************************************************************
 * @description    :  판매등급내역조회
*******************************************************************************/
function f_getSale_eqp()
{

	http.Sync = true;

	ds_saleEqpSt.ClearData();

    cf_setParam(ds_saleEqpSt, "CON_MGMT_NO" , FV_CON_MGMT_NO ); 

	var sSvcID 	= "getSaleEqp";
	var sInDs  	= "nc_input_fieldDs=ds_saleEqpSt";
	var sOutDs 	= "ds_saleEqpSt=ds_saleEqpSt";
alert("aaaaaa");	
	var sArg	= "sktst.cst.SKN#getSaleEqpC";
alert(sArg);	
	// 공통 트랜잭션 호출
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, null, true, true);

	http.Sync = false;		

}


/*******************************************************************************
 * @description    : 판매등급내역 저장
*******************************************************************************/
function btn_save_OnClick(obj)
{

	if ( !lf_saveValidationCheck() ) {
		return;
	}

	var sSvcID = "saveSaleEqpC";
	var sInDs  = "ds_saleEqpSt=ds_saleEqpSt";
	var sOutDs = "";
	var sArg   = "sktst.cst.SKN#saveSaleEqpC";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
//parent.f_saleEqpSt(div_conInfo8.edt_saleEqpSt.Text,div_conInfo8.edt_eqpSt.Text,FV_EQP_E);
//		this.Close();

	return;	
}


/*******************************************************************************
 * @description    :  callBack
*******************************************************************************/
function f_callBack(sSvcID)
{
	if (sSvcID == "getSaleEqp") {
		if (uf_IsNull(ds_saleEqpSt.GetColumn(0,"SEQ"))){
			ds_saleEqpSt.ClearData();
			cf_setParam(ds_saleEqpSt, "CON_MGMT_NO" , FV_CON_MGMT_NO ); 
		} else {
		}
    }else if (sSvcID == "saveSaleEqp") {
		alert("저장 되었습니다.);
		parent.f_saleEqpSt(div_conInfo8.edt_saleEqpSt.Text,div_conInfo8.edt_eqpSt.Text,FV_EQP_E);
		this.Close();
	}
}


/*******************************************************************************
 * @description    : 닫기버튼 클릭
*******************************************************************************/
function btn_close_OnClick(obj)
{

	this.close();
}


/*******************************************************************************
 * @description    :  판매등급등록 저장시 validation check
*******************************************************************************/
function lf_saveValidationCheck()
{

	var bFlag = true;

	if (uf_IsNull(div_choice1.rdo_power.value)){
		alert("기본점검 / 전원불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice2.rdo_charge.value)){
		alert("기본점검 / 충전불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice3.rdo_usim.value)){
		alert("기본점검 / USIM불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice6.rdo_cpnt.value)){
		alert("구성품 없음을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice4.rdo_bell.value)){
		alert("기능점검 / 벨소리불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice5.rdo_vibration.value)){
		alert("기능점검 / 진동불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice7.rdo_jodo.value)){
		alert("기능점검 / 조도센서불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice8.rdo_gyro.value)){
		alert("기능점검 / 자이로센서불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice9.rdo_proximity.value)){
		alert("기능점검 / 근접센서불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice10.rdo_camera.value)){
		alert("기능점검 / 카메라불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice11.rdo_battery.value)){
		alert("기능점검 / BT불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(div_choice12.rdo_afterimage.value)){
		alert("기능점검 / 잔상불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div19.rdo_touch.value)){
		alert("기능점검 / 터치불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div29.rdo_wifi.value)){
		alert("기능점검 / WIFI불량 을 선택하세요.");
		return false;
	}
    if (uf_IsNull(Div32.rdo_stain.value)){
		alert("화면점검 / 얼룩(흰색) 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div38.rdo_bruise.value)){
		alert("화면점검 / 멍(자주색/오렌지색) 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div35.rdo_squash.value)){
		alert("화면점검 / 눌림 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div20.rdo_liquefied.value)){
		alert("화면점검 / 액상유입 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div40.rdo_led.value)){
		alert("화면점검 / LED파손 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div11.rdo_button.value)){
		alert("외관점검 / 버튼불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div46.rdo_bottom.value)){
		alert("외관점검 / 하단터치메뉴불량 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div43.rdo_body.value)){
		alert("외관점검 / 몸체 미세 휨 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div23.rdo_dmb.value)){
		alert("외관점검 / DMB안테나 손상 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div47.rdo_glass.value)){
		alert("외관점검 / 액정유리(터치판넬) 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div26.rdo_teeth.value)){
		alert("외관점검 / 이나감 을 선택하세요.");
		return false;
	}
	if (uf_IsNull(Div50.rdo_window.value)){
		alert("외관점검 / 카메라 윈도우 손상 을 선택하세요.");
		return false;
	}	
	if (uf_IsNull(Div37.rdo_edge.value)){
		alert("외관점검 / 테두리 및 몸체 을 선택하세요.");
		return false;
	}	
	return bFlag;
}
