function POLBOP00320_OnLoadCompleted(obj)
{

	// 권한 설정
	cf_setAuth(pFrmNum);

    http.Sync = true;


	df_search();

	f_setCmCode();

	f_saleTyp();

	cf_setGrdChecknSortEvent(grd_mdlPrMnyGrd);	// 그리드 정렬

	f_crteDataSetGrd("ds_mdlPrMnyGrd");  //DataSet 생성


	http.Sync = false;

}



