/*-----------------------------------------------------------------------------------
* 기능명 : 그리드에서 셀을 더블클릭했을때
function tab_saleSuprtAmt_tab_prodInfo_grd_objtModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_objtModel.rowcount; i++ ){		
		if(ds_objtModel.GetColumn(i, "PROD_CD") == ds_aplyModel.GetColumn(nRow, "MDL_ID") ){
			return;
		}
    }

    f_moveShuttle(ds_objtModel, ds_aplyModel);    	
    ds_aplyModel.Sort("PROD_NM");  	
}

function tab_saleSuprtAmt_tab_prodInfo_grd_aplyModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_aplyModel.rowcount; i++ ){		
		if(ds_aplyModel.GetColumn(i, "MDL_ID") == ds_objtModel.GetColumn(nRow, "PROD_CD") ){
			return;
		}
    }

    f_moveShuttle(ds_aplyModel, ds_objtModel);    	
    ds_objtModel.Sort("PROD_NM"); 
}
*----------------------------------------------------------------------------------*/





/*-----------------------------------------------------------------------------------
* 함수명 : f_moveShuttle
* 기능명 : ource Dataset에 있는 row를 Target Dataset으로 이동함
*----------------------------------------------------------------------------------*/

function f_moveShuttle(ds, dsTarget)
{
	dsTarget.addRow();
	ds.copyToRow(ds.row, dsTarget.id, dsTarget.row );	
	ds.DeleteRow(ds.row);
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_saleSuprtAmt_tab_prodInfo_btn_toAplyModel_OnClick
* 기능명 : 대상모델에서 적용모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_saleSuprtAmt_tab_prodInfo_btn_toAplyModel_OnClick(obj)
{
	if(ds_objtModel.currow == -1 ){
	    //선택된 대상모델 없습니다.
		alert(cf_getMessage(MSG_00134, "대상모델"));
		return;
	}

	tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_objtModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_objtModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_aplyModel.AddRow();
			ds_aplyModel.CopyRow(ds_aplyModel.lastrow,ds_objtModel,nRow);
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "SELECTED", "0");
			ds_objtModel.DeleteRow(nRow);
	   }
	}

	ds_aplyModel.Sort("PROD_NM");
	tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.SetCellProp("head",0,"text","0");

	tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.Redraw  = true;
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_saleSuprtAmt_tab_prodInfo_btn_toObjtModel_OnClick
* 기능명 : 적용모델에서 대상모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_saleSuprtAmt_tab_prodInfo_btn_toObjtModel_OnClick(obj)
