	    //선택된 적용모델 없습니다.
		alert(cf_getMessage(MSG_00134, "적용모델"));
		return;
	}

	tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_aplyModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_aplyModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_objtModel.AddRow();
			ds_objtModel.CopyRow(ds_objtModel.lastrow,ds_aplyModel,nRow);
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "SELECTED", "0");
			ds_aplyModel.DeleteRow(nRow);
	   }
	}

	ds_objtModel.Sort("PROD_NM");
	tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.SetCellProp("head",0,"text","0");

	tab_saleSuprtAmt.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_saleSuprtAmt.tab_prodInfo.grd_aplyModel.Redraw  = true;
}





