/*-----------------------------------------------------------------------------------
 * Method : ds_slNet_OnColumnChanged
 * Desc   : 영업망 체크박스 클릭시
 *----------------------------------------------------------------------------------*/
function ds_slNet_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "SELECTED"){
		//if (varOldValue == 1 && varNewValue == 0) {
			//ds_slNet.SetColumn(nRow,strColumnID,varNewValue);
			ds_slNet.SetColumn(nRow,"QTY_INCLD_YN",varNewValue);
			ds_slNet.SetColumn(nRow,"PAY_INCLD_YN",varNewValue);
		//}
	} else if(strColumnID == "QTY_INCLD_YN"){
	    if(ds_slNet.GetColumn(nRow,"SELECTED") == "0" && varNewValue == 1) {
			ds_slNet.SetColumn(nRow,"SELECTED",varNewValue);
	    } else if(ds_slNet.GetColumn(nRow,"PAY_INCLD_YN") == "0" && varNewValue == 0) {
			ds_slNet.SetColumn(nRow,"SELECTED","0");
	    }
	} else if(strColumnID == "PAY_INCLD_YN"){
	    if(ds_slNet.GetColumn(nRow,"SELECTED") == "0" && varNewValue == 1) {
			ds_slNet.SetColumn(nRow,"SELECTED",varNewValue);
	    } else if(ds_slNet.GetColumn(nRow,"QTY_INCLD_YN") == "0" && varNewValue == 0) {
			ds_slNet.SetColumn(nRow,"SELECTED","0");
	    }
	}
}


/*-----------------------------------------------------------------------------------
 * Method : ds_dsNet_OnColumnChanged
 * Desc   : 유통망 체크박스 클릭시
 *----------------------------------------------------------------------------------*/
function ds_dsNet_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "SELECTED"){
		//if (varOldValue == 1 && varNewValue == 0) {
			//ds_slNet.SetColumn(nRow,strColumnID,varNewValue);
			ds_dsNet.SetColumn(nRow,"QTY_INCLD_YN",varNewValue);
			ds_dsNet.SetColumn(nRow,"PAY_INCLD_YN",varNewValue);
		//}
	} else if(strColumnID == "QTY_INCLD_YN"){
	    if(ds_dsNet.GetColumn(nRow,"SELECTED") == "0" && varNewValue == 1) {
			ds_dsNet.SetColumn(nRow,"SELECTED",varNewValue);
	    } else if(ds_dsNet.GetColumn(nRow,"PAY_INCLD_YN") == "0" && varNewValue == 0) {
			ds_dsNet.SetColumn(nRow,"SELECTED","0");
	    }
	} else if(strColumnID == "PAY_INCLD_YN"){
	    if(ds_dsNet.GetColumn(nRow,"SELECTED") == "0" && varNewValue == 1) {
			ds_dsNet.SetColumn(nRow,"SELECTED",varNewValue);
	    } else if(ds_dsNet.GetColumn(nRow,"QTY_INCLD_YN") == "0" && varNewValue == 0) {
			ds_dsNet.SetColumn(nRow,"SELECTED","0");
	    }
	}

}




