/*-----------------------------------------------------------------------------------
* 기능명 : 그리드에서 셀을 더블클릭했을때
function tab_onLineCmms_tab_prodInfo_grd_objtModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_objtModel.rowcount; i++ ){		
		if(ds_objtModel.GetColumn(i, "PROD_CD") == ds_aplyModel.GetColumn(nRow, "MDL_ID") ){
			return;
		}
    }

    f_moveShuttle(ds_objtModel, ds_aplyModel);    	
    ds_aplyModel.Sort("PROD_NM");  	
}

function tab_onLineCmms_tab_prodInfo_grd_aplyModel_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	for( var i=0; i<ds_aplyModel.rowcount; i++ ){		
		if(ds_aplyModel.GetColumn(i, "MDL_ID") == ds_objtModel.GetColumn(nRow, "PROD_CD") ){
			return;
		}
    }

    f_moveShuttle(ds_aplyModel, ds_objtModel);    	
    ds_objtModel.Sort("PROD_NM"); 
}
*----------------------------------------------------------------------------------*/





/*-----------------------------------------------------------------------------------
* 함수명 : f_moveShuttle
* 기능명 : ource Dataset에 있는 row를 Target Dataset으로 이동함
*----------------------------------------------------------------------------------*/

function f_moveShuttle(ds, dsTarget)
{
	dsTarget.addRow();
	ds.copyToRow(ds.row, dsTarget.id, dsTarget.row );	
	ds.DeleteRow(ds.row);
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_onLineCmms_tab_prodInfo_btn_toAplyModel_OnClick
* 기능명 : 대상모델에서 적용모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_onLineCmms_tab_prodInfo_btn_toAplyModel_OnClick(obj)
{
	if(ds_objtModel.currow == -1 ){
	    //선택된 대상모델 없습니다.
		alert(cf_getMessage(MSG_00134, "대상모델"));
		return;
	}

	tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_onLineCmms.tab_prodInfo.grd_objtModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_objtModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_objtModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_objtModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_aplyModel.AddRow();
			ds_aplyModel.CopyRow(ds_aplyModel.lastrow,ds_objtModel,nRow);
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "SELECTED", "0");
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "QTY_INCLD_YN", "1");
			ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "PAY_INCLD_YN", "1");
			ds_objtModel.DeleteRow(nRow);
	   }
	}

	//ds_aplyModel.Sort("PROD_NM");
	tab_onLineCmms.tab_prodInfo.grd_objtModel.SetCellProp("head",0,"text","0");

	tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_onLineCmms_tab_prodInfo_btn_toObjtModel_OnClick
* 기능명 : 적용모델에서 대상모델로 이동함
*----------------------------------------------------------------------------------*/
function tab_onLineCmms_tab_prodInfo_btn_toObjtModel_OnClick(obj)
{
	if(ds_aplyModel.currow == -1 ){
	    //선택된 적용모델 없습니다.
		alert(cf_getMessage(MSG_00134, "적용모델"));
		return;
	}

	tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = false;
	tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

	var sHead = tab_onLineCmms.tab_prodInfo.grd_aplyModel.GetCellProp("head",0,"text");
	var nTotCnt = ds_aplyModel.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_aplyModel.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_aplyModel.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_objtModel.AddRow();
			ds_objtModel.CopyRow(ds_objtModel.lastrow,ds_aplyModel,nRow);
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "SELECTED", "0");
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "QTY_INCLD_YN", "0");
			ds_objtModel.SetColumn(ds_objtModel.lastrow, "PAY_INCLD_YN", "0");
			ds_aplyModel.DeleteRow(nRow);
	   }
	}

	//ds_objtModel.Sort("PROD_NM");
	tab_onLineCmms.tab_prodInfo.grd_aplyModel.SetCellProp("head",0,"text","0");

	tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = true;
	tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;
}



/*-----------------------------------------------------------------------------------
 * Method : tab_onLineCmms_tab_prodInfo_div_process_rdo_mdlAdpt_OnClick
 * Desc   :  전모델 적용
 *----------------------------------------------------------------------------------*/

function tab_onLineCmms_tab_prodInfo_div_process_rdo_mdlAdpt_OnClick(obj)
{
	//tab_onLineCmms.tab_prodInfo.div_process.rdo_mdlAdpt.Value
	//alert(obj.Value);
    if ( obj.Value == "Y") {
        tab_onLineCmms.tab_prodInfo.div_process.cmb_mfact.index = 0;  //제조사 전체로 세팅
        tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic1.index = 0;  //상품특성1 전체로 세팅
        tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic2.index = 0;  //상품특성2 전체로 세팅
        tab_onLineCmms.tab_prodInfo.div_process.cmb_sktOperYn.index = 0;  //SK운영여부 전체로 세팅

        var strMfact       = tab_onLineCmms.tab_prodInfo.div_process.cmb_mfact.value;
        var strProdChrtic1 = tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic1.value;
        var strProdChrtic2 = tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic2.value;
        var strSktOperYn   = tab_onLineCmms.tab_prodInfo.div_process.cmb_sktOperYn.value;
		var sFilterDs      = "";

		//ds_objtModel.Unfilter();
		if(uf_isNull(strMfact)== false) {
			sFilterDs += " DEAL_CO_CD='" + strMfact + "' &&";
		}
		if (strProdChrtic1 != "") {
			sFilterDs += " PROD_CHRTIC_1_CD='" + strProdChrtic1 + "' &&";
		}
		if (strProdChrtic2 != "") {
			sFilterDs += " PROD_CHRTIC_2_CD='" + strProdChrtic2 + "' &&";
		}
		if (strSktOperYn != "") {
			sFilterDs += " SKT_OPER_YN='" + strSktOperYn + "' &&";
		}
			//sFilterDs += " 1=1";
			//alert(SubStr(sFilterDs,0,Length(sFilterDs)-2));
		ds_objtModel.Filter(SubStr(sFilterDs,0,Length(sFilterDs)-2));  //대상모델 전체 조회

		//f_gridcheckOneAll(obj,ds_objtModel);  //전부 체크

		//tab_onLineCmms_tab_prodInfo_btn_toAplyModel_OnClick();   //적용모델로 이동


		tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = false;
		tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = false;

		for(var iCnt=0; iCnt<=ds_aplyModel.GetCount(); iCnt++){
				ds_aplyModel.SetColumn(iCnt, "QTY_INCLD_YN", "1");
				ds_aplyModel.SetColumn(iCnt, "PAY_INCLD_YN", "1");
		}


		//var sHead = tab_onLineCmms.tab_prodInfo.grd_objtModel.GetCellProp("head",0,"text");
		var nTotCnt = ds_objtModel.GetCount();

        if( nTotCnt > 0 ) {
			if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

			for(var nRow=nTotCnt; nRow>=0; nRow--){
					ds_aplyModel.AddRow();
					ds_aplyModel.CopyRow(ds_aplyModel.lastrow,ds_objtModel,nRow);
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "SELECTED", "0");
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "QTY_INCLD_YN", "1");
					ds_aplyModel.SetColumn(ds_aplyModel.lastrow, "PAY_INCLD_YN", "1");
					ds_objtModel.DeleteRow(nRow);
			}

			ds_aplyModel.Sort("PROD_NM");
			//tab_onLineCmms.tab_prodInfo.grd_objtModel.SetCellProp("head",0,"text","0");

        }

		tab_onLineCmms.tab_prodInfo.grd_objtModel.Redraw = true;
		tab_onLineCmms.tab_prodInfo.grd_aplyModel.Redraw  = true;



    }

}




