/*-----------------------------------------------------------------------------------
 * Method : f_payInfoAddRow
 * Desc   : 지급정보 플러스 추가
 *----------------------------------------------------------------------------------*/
function f_payInfoAddRow(DSOBJ)
{
	object(DSOBJ).AddRow();
	object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "SEQ", object(DSOBJ).lastrow+1);
	object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "SELECTED", "0");
	object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "STRD_CL_CD", "01");
	object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "CMMS_UNIT_CD", "01");
	if(DSOBJ=="ds_payInfoPg") {
		object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "ACNT_CL_CD", "01");
		object(DSOBJ).SetColumn(object(DSOBJ).lastrow, "ACNT_TYP_CD", "1");
	}
	//ds_payInfoOn.SetColumn(ds_payInfoOn.lastrow, "ITVL_TRGT", "0");
	//ds_payInfoOn.SetColumn(ds_payInfoOn.lastrow, "GRD_INS_YN", "N");

}


