/*******************************************************************************
 * @description    : 공통 저장 함수
*******************************************************************************/
function df_save()
{

	// 마감여부 체크 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.
	cf_CheckCloseMnth('04',ds_condition.getColumn(0,"POL_YM"),'적용기간'); 
	//마감이 됬다면... 저장못함.
	if(FV_BOOLEAN_CLOSE) return;


	if (!(f_pre_save())) return;  //유효성 체크

	var sSvcID = "";
	var sInDs = "";
	var sOutDs = "";
	var sArg = "";
	var APLY_STA_DTM = "";
	var APLY_END_DTM = "";

	APLY_STA_DTM = div_search.cal_aplyStaDtm.Value+Right("0"+div_search.edt_hour.Text,2)+Right("0"+div_search.edt_min.Text,2);
	APLY_END_DTM = div_search.cal_aplyEndDtm.Value+Right("0"+div_search.edt_endHour.Text,2)+Right("0"+div_search.edt_endMin.Text,2);
	ds_onLineCmms.SetColumn(0,"APLY_STA_DTM",APLY_STA_DTM);
	ds_onLineCmms.SetColumn(0,"APLY_END_DTM",APLY_END_DTM);
	ds_onLineCmms.SetColumn(0,"POL_YM",div_search.cal_polYm.Value);
	//ds_onLineCmms.SetColumn(0,"ACC_SCHD_MTH",div_search.cmb_accSchdYear.Value+div_search.cmb_accSchdMonth.Value);
	//trace(ds_aplyModel.SaveXML());
    //alert(PRE_APLY_STA_DTM+"___"+APLY_STA_DTM);
	if(PRE_APLY_STA_DTM == APLY_STA_DTM){
	    //저장하시겠습니까?
		if(confirm(cf_getMessage(MSG_00063, "")) == 1){
			sSvcID = "svcSave";

			sInDs  = " ds_onLineCmms=ds_onLineCmms ";        //온라인수수료 기본정보
			sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
			sInDs += " ds_slNet=ds_slNet ";                  //영업망
			sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
			sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
			sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
			sInDs += " ds_payInfoAllot=ds_payInfoAllot ";	 //지급정보 할부판매수수료
			sInDs += " ds_payInfoCash=ds_payInfoCash ";		 //지급정보 현금판매수수료
			sInDs += " ds_payInfoPrchs=ds_payInfoPrchs ";	 //지급정보 현금매입수수료
			sInDs += " ds_payInfoPg=ds_payInfoPg ";		     //지급정보 현금결재 수수료
			sInDs += " ds_payInfoCrdt=ds_payInfoCrdt ";		 //지급정보 신용카드결재 수수료
			//sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		 //모델별장려금
			sInDs += " ds_aplyDealCo=ds_aplyDealCo ";		 //적용거래처

			sOutDs = "";

			sArg = "sktst.pol.OSP#saveOnLineCmms";
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
		}

	}else{
		if(uf_isNull(ds_onLineCmms.GetColumn(0,"APOL_ID"))== true) {   //리스트 클릭시 수정모드
						//새로운 온라인수수료 정책을 반영하시겠습니까?
						if(confirm(cf_getMessage(MSG_00136, "새로운 온라인수수료 정책을 반영")) == 1){

							sSvcID = "svcAdd";

							sInDs  = " ds_onLineCmms=ds_onLineCmms ";    //온라인수수료 기본정보
							sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
							sInDs += " ds_slNet=ds_slNet ";                  //영업망
							sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
							sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
							sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
							sInDs += " ds_payInfoAllot=ds_payInfoAllot ";		  //지급정보 할부판매수수료
							sInDs += " ds_payInfoCash=ds_payInfoCash ";		  //지급정보 현금판매수수료
							sInDs += " ds_payInfoPrchs=ds_payInfoPrchs ";	 //지급정보 현금매입수수료
							sInDs += " ds_payInfoPg=ds_payInfoPg ";		     //지급정보 현금결재 수수료
							sInDs += " ds_payInfoCrdt=ds_payInfoCrdt ";		 //지급정보 신용카드 수수료
							//sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
							sInDs += " ds_aplyDealCo=ds_aplyDealCo ";		 //적용거래처

							sOutDs = "";

							 sArg = "sktst.pol.OSP#addOnLineCmms";
							 cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
						}
		} else {
				//저장하시겠습니까?
				if(confirm(cf_getMessage(MSG_00136, "소급 변경 ")) == 1){
					sSvcID = "svcSave";

					sInDs  = " ds_onLineCmms=ds_onLineCmms ";    //온라인수수료 기본정보
					sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
					sInDs += " ds_slNet=ds_slNet ";                  //영업망
					sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
					sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
					sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
					sInDs += " ds_payInfoAllot=ds_payInfoAllot ";		  //지급정보 할부판매수수료
					sInDs += " ds_payInfoCash=ds_payInfoCash ";		  //지급정보 현금판매수수료
					sInDs += " ds_payInfoPg=ds_payInfoPg ";		     //지급정보 현금결재 수수료
					sInDs += " ds_payInfoPrchs=ds_payInfoPrchs ";	 //지급정보 현금매입수수료
					sInDs += " ds_payInfoCrdt=ds_payInfoCrdt ";		 //지급정보 신용카드결재 수수료
					//sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		 //모델별장려금
					sInDs += " ds_aplyDealCo=ds_aplyDealCo ";		 //적용거래처

					sOutDs = "";

					sArg = "sktst.pol.OSP#saveOnLineCmms";
					cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
				} else {
						//새로운 온라인수수료 정책을 반영하시겠습니까?
						if(confirm(cf_getMessage(MSG_00136, "새로운 온라인수수료 정책을 반영")) == 1){

							sSvcID = "svcAdd";

							sInDs  = " ds_onLineCmms=ds_onLineCmms ";    //온라인수수료 기본정보
							sInDs += " ds_orgInfo=ds_orgInfo ";              //조직정보
							sInDs += " ds_slNet=ds_slNet ";                  //영업망
							sInDs += " ds_dsNet=ds_dsNet ";                  //유통망
							sInDs += " ds_aplyModel=ds_aplyModel ";		     //적용모델
							sInDs += " ds_saleTyp=ds_saleTyp ";              //판매유형
							sInDs += " ds_payInfoAllot=ds_payInfoAllot ";		  //지급정보 할부판매수수료
							sInDs += " ds_payInfoCash=ds_payInfoCash ";		  //지급정보 현금판매수수료
							sInDs += " ds_payInfoPrchs=ds_payInfoPrchs ";	 //지급정보 현금매입수수료
							sInDs += " ds_payInfoPg=ds_payInfoPg ";		     //지급정보 현금결재 수수료
							sInDs += " ds_payInfoCrdt=ds_payInfoCrdt ";		 //지급정보 신용카드 수수료
							//sInDs += " ds_mdlPrCmms=ds_mdlPrCmms ";		     //모델별장려금
							sInDs += " ds_aplyDealCo=ds_aplyDealCo ";		 //적용거래처

							sOutDs = "";

							 sArg = "sktst.pol.OSP#addOnLineCmms";
							 cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
						}
				}
		}
	}


}

/*******************************************************************************
* 함수명 : f_pre_save
* 기능명 : 저장버튼 전 체크함수 
*******************************************************************************/
function f_pre_save()
{	
	//if(SAVE_FLAG = "N"){
	//	alert("변경사항이 없습니다.");
	//	return;
	//}

	//if(ds_pol_uplst_aply_mdl.rowcount < 1){
	//	alert("적용할 단말기가 없습니다.");
	//	return;
	//}

	if(length(div_search.edt_hour.Text) < 1 || div_search.edt_hour.Text > 24){
	    //적용기간 시작시간을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용기간 시작시간"));
		div_search.edt_hour.SetFocus();
		return;
	}

	if(length(div_search.edt_min.Text) < 1 || div_search.edt_min.Text > 59){
	    //적용기간 시작분을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용기간 시작분"));
		div_search.edt_min.SetFocus();
		return;
	}

    if(uf_isNull(div_search.edt_polNm.Text) == true) {
	    //정책명칭을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "정책명칭"));
		div_search.edt_polNm.SetFocus();
		return;
    }


    if (ds_aplyDealCo.rowcount == 0) {
	    //적용 온라인거래처를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용 온라인거래처를"));
		tab_onLineCmms.TabIndex = 0;
		return false;
    }


    if (ds_aplyModel.rowcount == 0) {
	    //적용모델을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "적용모델을"));
		return false;
    }


    var iCnt=0;
	for( var nRow=0; nRow<ds_slNet.rowcount; nRow++ ){
		if(ds_slNet.GetColumn(nRow,"SELECTED") == 1){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //영업망을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "영업망을"));
		tab_onLineCmms.TabIndex = 2;
		tab_onLineCmms.tab_saleInfo.grd_slNet.SetFocus();
		return;
    }

    iCnt=0;
	for( var nRow=0; nRow<ds_dsNet.rowcount; nRow++ ){
		if(ds_dsNet.GetColumn(nRow,"SELECTED") == 1){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //유통망을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "유통망을"));
		tab_onLineCmms.TabIndex = 2;
		tab_onLineCmms.tab_saleInfo.grd_dsNet.SetFocus();
		return;
    }

    iCnt=0;
	for( var nRow=0; nRow<ds_saleTyp.rowcount; nRow++ ){
		if(ds_saleTyp.GetColumn(nRow,"SELECT3") == "1"){
		    iCnt++;
		}
	}
    if (iCnt == 0) {
	    //판매유형을 선택하여주십시요.
		alert(cf_getMessage(MSG_00047, "판매유형을"));
		tab_onLineCmms.TabIndex = 2;
		tab_onLineCmms.tab_saleInfo.grd_saleTyp.SetFocus();
		return;
    }

	for( var nRow=0; nRow<ds_payInfoAllot.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfoAllot.GetColumn(nRow,"BAS_AMT")) == true){
			//판매가는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "할부판매가"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoAllot, nRow, 1);
			return false;
		}else if(uf_isNull(ds_payInfoAllot.GetColumn(nRow,"CMMS")) == true){
			//현금수수료는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "할부판매수수료"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoAllot, nRow, 4);
			return false;
		}
	}

	for( var nRow=0; nRow<ds_payInfoCash.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfoCash.GetColumn(nRow,"BAS_AMT")) == true){
			//판매가는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금판매가"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoCash, nRow, 1);
			return false;
		}else if(uf_isNull(ds_payInfoCash.GetColumn(nRow,"CMMS")) == true){
			//현금수수료는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금판매수수료"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoCash, nRow, 4);
			return false;
		}
	}

	for( var nRow=0; nRow<ds_payInfoPrchs.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfoPrchs.GetColumn(nRow,"BAS_AMT")) == true){
			//판매가는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금판매가"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoPrchs, nRow, 1);
			return false;
		}else if(uf_isNull(ds_payInfoPrchs.GetColumn(nRow,"CMMS")) == true){
			//현금수수료는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금판매수수료"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoPrchs, nRow, 4);
			return false;
		}
	}

	for( var nRow=0; nRow<ds_payInfoPg.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfoPg.GetColumn(nRow,"BAS_AMT")) == true){
			//판매가는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금결재가"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoPg, nRow, 1);
			return false;
		}else if(uf_isNull(ds_payInfoPg.GetColumn(nRow,"CMMS")) == true){
			//현금수수료는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "현금결재수수료"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoPg, nRow, 4);
			return false;
		}
	}
/**
	for( var nRow=0; nRow<ds_payInfoCrdt.rowcount; nRow++ ){
		if(uf_isNull(ds_payInfoCrdt.GetColumn(nRow,"CMMS_RT")) == true){
			//신용카드 수수료는 필수 사항입니다.
			alert(cf_getMessage(MSG_00135, "신용카드 수수료"));
			tab_onLineCmms.TabIndex = 3;
			f_set_gridFocus(grd_payInfoCrdt, nRow, 2);
			return false;
		}
	}




    if (ds_payInfoAllot.rowcount == 0) {
	    //지급정보를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "할부판매수수료 지급정보"));
		return false;
    }

    if (ds_payInfoCash.rowcount == 0) {
	    //지급정보를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "현금판매수수료 지급정보"));
		return false;
    }

    if (ds_payInfoPg.rowcount == 0) {
	    //지급정보를 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "현금결재수수료 지급정보"));
		return false;
    }
**/

    f_chk_block("ds_payInfoAllot");
    f_chk_block("ds_payInfoCash");
    f_chk_block("ds_payInfoPrchs");

    ds_payInfoPg.Filter( " ACNT_CL_CD='1' ");
	//trace(ds_payInfoPg.SaveXML());
    f_chk_block("ds_payInfoPg");

    ds_payInfoPg.Filter( " ACNT_CL_CD='2' ");
    f_chk_block("ds_payInfoPg");
	ds_payInfoPg.Unfilter();

    return true;


}


/*******************************************************************************
* 함수명 : f_chk_block
* 기능명 : 구간 입력정보 정합성 체크
*******************************************************************************/
function f_chk_block(DSOBJ) 
{	
    //지급정보의 구간 체크
    var sTrdClCd = "";     //STRD_CL_CD: 01(이상), 02(미만), 03(초과), 04(한정)
    var sMinQty = "";
    var sMaxQty = "";

	if( object(DSOBJ).GetColumn((object(DSOBJ).rowcount-1),"STRD_CL_CD") == "02" ) { 
	    //마지막 구간은 미만이 될 수 없습니다.
		alert(cf_getMessage(MSG_00083, "마지막 구간은 미만이 될 수"));
		return false;
	}


	for( var nRow=0; nRow<object(DSOBJ).rowcount; nRow++ ){
		if(object(DSOBJ).GetColumn(nRow,"STRD_CL_CD")=="01"){ //이상이면...
			//alert("마지막 구간은 이상으로 끝나야 합니다.");
				object(DSOBJ).SetColumn(nRow,  "MIN_AMT",object(DSOBJ).GetColumn(nRow,"BAS_AMT"));
				object(DSOBJ).SetColumn(nRow-1,"MAX_AMT",object(DSOBJ).GetColumn(nRow,"BAS_AMT")-1);
			if (nRow == (object(DSOBJ).rowcount-1)) {
				object(DSOBJ).SetColumn(nRow,"MAX_AMT","999999999999");
			}

		} else if (object(DSOBJ).GetColumn(nRow,"STRD_CL_CD")=="03"){ //초과면...
				object(DSOBJ).SetColumn(nRow,"MIN_AMT",object(DSOBJ).GetColumn(nRow,"BAS_AMT")+1);
				object(DSOBJ).SetColumn(nRow,"MAX_AMT","999999999999");
			if (object(DSOBJ).rowcount > 1) {
				//초과 조건에 다른 구간이 올 수 없습니다.
				alert(cf_getMessage(MSG_00083, "초과 조건에 다른 구간이 올 수"));
				return false;
			}

		} else if (object(DSOBJ).GetColumn(nRow,"STRD_CL_CD")=="04"){ //한정이면...
				object(DSOBJ).SetColumn(nRow,"MIN_AMT","1");
				object(DSOBJ).SetColumn(nRow,"MAX_AMT",object(DSOBJ).GetColumn(nRow,"BAS_AMT"));
			if (object(DSOBJ).rowcount > 1) {
				//한정 조건에 다른 구간이 올 수 없습니다.
				alert(cf_getMessage(MSG_00083, "한정 조건에 다른 구간이 올 수"));
				return false;
			}

		}

	}

	for( var nRow=0; nRow<object(DSOBJ).rowcount; nRow++ ){
		if (object(DSOBJ).GetColumn(nRow,"STRD_CL_CD")=="02"){ //미만이면...
				object(DSOBJ).SetColumn(nRow,"MIN_AMT",object(DSOBJ).GetColumn(nRow-1,"BAS_AMT"));
				object(DSOBJ).SetColumn(nRow,"MAX_AMT",object(DSOBJ).GetColumn(nRow,"BAS_AMT")-1);
			if (nRow == 0) {
				object(DSOBJ).SetColumn(nRow,"MIN_AMT","1");
			}
			if (nRow == (object(DSOBJ).rowcount-1)) {
				object(DSOBJ).SetColumn(nRow,"MAX_AMT","999999999999");
			}
		}

	}



	for( var nRow=1; nRow<object(DSOBJ).rowcount; nRow++ ){
		if ( object(DSOBJ).GetColumn(nRow,"MIN_AMT") != (object(DSOBJ).GetColumn(nRow-1,"MAX_AMT")+1) ) {
				//n번째 구간을 다시 입력하십시요.
				alert((nRow+1)+cf_getMessage(MSG_00140, "번째 구간"));
				return false;
		}
	}


	for( var nRow=0; nRow<object(DSOBJ).rowcount-1; nRow++ ){
		if ( object(DSOBJ).GetColumn(nRow,"MIN_AMT") >= object(DSOBJ).GetColumn(nRow,"MAX_AMT") ) {
			if(nRow == 0) {
				//n번째 구간을 다시 입력하십시요.
				alert((nRow+1)+cf_getMessage(MSG_00140, "번째 구간"));
			} else {
				//n번째 구간을 다시 입력하십시요.
				alert((nRow+2)+cf_getMessage(MSG_00140, "번째 구간"));
			}
			return false;
		}
	}


}


/*******************************************************************************
* 함수명 : f_set_gridFocus
* 기능명 : 그리드 포커스 이동
*******************************************************************************/
function f_set_gridFocus(gridObj, nRow, nCellIdx) 
{	
	var dataSetObj = object(gridObj.bindDataset);
	dataSetObj.row = nRow;
	gridObj.SetCellPos(nCellIdx);
	gridObj.SetFocus();
}


/*******************************************************************************
* 함수명 : df_delete
* 기능명 : 삭제
*******************************************************************************/
function df_delete(sButtonID)
{
	// 마감여부 체크 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.
	cf_CheckCloseMnth('04',ds_condition.getColumn(0,"POL_YM"),'적용기간'); 
	//마감이 됬다면... 저장못함.
	if(FV_BOOLEAN_CLOSE) return;

	if(confirm(cf_getMessage(MSG_00136, "온라인수수료를 삭제")) == 1){ //온라인수수료를 삭제 하시겠습니까?	
		var sSvcID = "svcDelete";
		var sInDs = "nc_input_fieldDs=ds_onLineCmms";
		var sOutDs = "";
		var sArg = "sktst.pol.OSP#deleteOnLineCmms";

		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
	}
}








/*******************************************************************************
* 함수명 : tab_onLineCmms_tab_orgInfo_grd_objtDealCo_OnHeadClick
* 기능명 : 온라인수수료 대상 거래처 전체선택
*******************************************************************************/
function tab_onLineCmms_tab_orgInfo_grd_objtDealCo_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(object(obj.bindDataset).getRowCount() > 0)
	{
		var sType = obj.GetCellProp("head", nCell, "display");
		if(sType == "checkbox")	
		{
			if( nCell==0 )
			{
				f_gridcheckOneAll(obj,ds_objtDealCo);  //전부 체크
			}
		}
		else
		{
			cf_markGridSort(obj, nCell, nX, nY, nPivotIndex);
		}
	}
}








/*******************************************************************************
* 함수명 : tab_onLineCmms_tab_orgInfo_grd_aplyDealCo_OnHeadClick
* 기능명 : 온라인수수료 적용 거래처 전체선택
*******************************************************************************/
function tab_onLineCmms_tab_orgInfo_grd_aplyDealCo_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(object(obj.bindDataset).getRowCount() > 0)
	{
		var sType = obj.GetCellProp("head", nCell, "display");
		if(sType == "checkbox")	
		{
			if( nCell==0 )
			{
				f_gridcheckOneAll(obj,ds_aplyDealCo);  //전부 체크
			}
		}
		else
		{
			cf_markGridSort(obj, nCell, nX, nY, nPivotIndex);
		}
	}
}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_onLineCmms_tab_orgInfo_btn_toAplyDealCo_OnClick
* 기능명 : 대상판매처에서 적용판매처로 이동함
*----------------------------------------------------------------------------------*/
function tab_onLineCmms_tab_orgInfo_btn_toAplyDealCo_OnClick(obj)
{
	var iCnt=0;
	for( var nRow=0; nRow<ds_orgInfo.rowcount; nRow++ ){
		if(ds_orgInfo.GetColumn(nRow,"SELECT4") == "1"){

			iCnt++;

		}
	}
	if (iCnt == 0) {
	    //선택된 조직정보가 없습니다.
		alert(cf_getMessage(MSG_00134, "조직정보"));
		return;
	}

	if(ds_objtDealCo.currow == -1 ){
	    //선택된 대상거래처가 없습니다.
		alert(cf_getMessage(MSG_00134, "대상거래처"));
		return;
	}

	ds_aplyDealCo.FireEvent = false;
	ds_objtDealCo.FireEvent = false;
	tab_onLineCmms.tab_orgInfo.grd_objtDealCo.Redraw = false;
	tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.Redraw  = false;



	var sHead = tab_onLineCmms.tab_orgInfo.grd_objtDealCo.GetCellProp("head",0,"text");
	var nTotCnt = ds_objtDealCo.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_objtDealCo.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_objtDealCo.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_objtDealCo.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			for( var iRow=0; iRow<ds_orgInfo.rowcount; iRow++ ){
				if(ds_orgInfo.GetColumn(iRow,"SELECT4") == "1"){

					ds_aplyDealCo.AddRow();
					ds_aplyDealCo.CopyRow(ds_aplyDealCo.lastrow,ds_objtDealCo,nRow);
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "ORG_ID", ds_orgInfo.GetColumn(iRow,"ORG_ID_4"));
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "SELECTED", "0");

				}
			}
			ds_objtDealCo.DeleteRow(nRow);
	   }
	}

	//ds_aplySalePlc.Sort("PROD_NM");
	tab_onLineCmms.tab_orgInfo.grd_objtDealCo.SetCellProp("head",0,"text","0");
	ds_aplyDealCo.Filter("ORG_ID='"+ds_aplyDealCo.GetColumn(0,"ORG_ID")+"'");

	ds_aplyDealCo.FireEvent = true;
	ds_objtDealCo.FireEvent = true;
	tab_onLineCmms.tab_orgInfo.grd_objtDealCo.Redraw = true;
	tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.Redraw  = true;

}

/*-----------------------------------------------------------------------------------
* 함수명 : tab_onLineCmms_tab_orgInfo_btn_toObjtDealCo_OnClick
* 기능명 : 적용판매처에서 대상판매처로 이동함
*----------------------------------------------------------------------------------*/
function tab_onLineCmms_tab_orgInfo_btn_toObjtDealCo_OnClick(obj)
{
	if(ds_aplyDealCo.currow == -1 ){
	    //선택된 적용거래처가 없습니다.
		alert(cf_getMessage(MSG_00134, "적용거래처"));
		return;
	}

	tab_onLineCmms.tab_orgInfo.grd_objtDealCo.Redraw = false;
	tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.Redraw  = false;
	ds_aplyDealCo.FireEvent = false;
	ds_objtDealCo.FireEvent = false;

	var sHead = tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.GetCellProp("head",0,"text");
	var nTotCnt = ds_aplyDealCo.GetCount();
	if( nTotCnt > 0 ) nTotCnt = nTotCnt -1;

	for(var nRow=nTotCnt; nRow>=0; nRow--){
	   var bCheck = false;
	   if( sHead == "1" ){
			bCheck = ds_aplyDealCo.GetColumn(nRow,"SELECTED");
	   }else{
			bCheck = ds_aplyDealCo.GetColumn(nRow,"SELECTED");
			if( bCheck == false || bCheck == null || length(bCheck) == 0){
				bCheck = ds_aplyDealCo.GetSelect(nRow);
			}
	   }

	   if( bCheck == true){
			ds_objtDealCo.AddRow();
			ds_objtDealCo.CopyRow(ds_objtDealCo.lastrow,ds_aplyDealCo,nRow);
			ds_objtDealCo.SetColumn(ds_objtDealCo.lastrow, "SELECTED", "0");
			//ds_objtDealCo.GetColumn(ds_objtDealCo.lastrow,"DEAL_CO_CD");
			ds_aplyDealCo.UnFilter();
			var itot = ds_aplyDealCo.rowcount;
			for( var iRow=ds_aplyDealCo.rowcount; iRow >= 0; iRow-- ){
//alert(ds_aplyDealCo.rowcount+"==="+ds_aplyDealCo.GetColumn(iRow,"DEAL_CO_CD") +"==="+ ds_objtDealCo.GetColumn(ds_objtDealCo.lastrow,"DEAL_CO_CD"));
			    if(ds_aplyDealCo.GetColumn(iRow,"DEAL_CO_CD") == ds_objtDealCo.GetColumn(ds_objtDealCo.lastrow,"DEAL_CO_CD")) {
					ds_aplyDealCo.DeleteRow(iRow);
			    }
			}
	   }
	}

	//ds_objtModel.Sort("PROD_NM");
	tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.SetCellProp("head",0,"text","0");
	ds_aplyDealCo.Filter("ORG_ID='"+ds_aplyDealCo.GetColumn(0,"ORG_ID")+"'");

	ds_aplyDealCo.FireEvent = true;
	ds_objtDealCo.FireEvent = true;
	tab_onLineCmms.tab_orgInfo.grd_objtDealCo.Redraw = true;
	tab_onLineCmms.tab_orgInfo.grd_aplyDealCo.Redraw  = true;


}




/*-----------------------------------------------------------------------------------
* 함수명 : f_moveAplyToObjtDealCo
* 기능명 : 조직정보 체크해제시 적용거래처에서 대상거래처로 이동
*----------------------------------------------------------------------------------*/
function f_moveAplyToObjtDealCo()
{
	if(ds_aplyDealCo.currow == -1 ){
		return;
	}

	var iCnt=0;
	for( var nRow=0; nRow<ds_orgInfo.rowcount; nRow++ ){
		if(ds_orgInfo.GetColumn(nRow,"SELECT4") == "1"){

			iCnt++;

		}
	}

	ds_aplyDealCo.FireEvent = false;
	if (iCnt == 0) {
		ds_aplyDealCo.UnFilter();
		for( var iRow=0;iRow<ds_aplyDealCo.rowcount; iRow++ ){
				ds_aplyDealCo.SetColumn(iRow,"SELECTED","1");
		}
		tab_onLineCmms_tab_orgInfo_btn_toObjtDealCo_OnClick();
	} else {
		ds_aplyDealCo.Filter("ORG_ID='"+ds_aplyDealCo.GetColumn(0,"ORG_ID")+"'");
        var sDEAL_CO = "";
		for( var iRow=0;iRow<ds_aplyDealCo.rowcount; iRow++ ){
		     sDEAL_CO = sDEAL_CO + ds_aplyDealCo.GetColumn(iRow, "DEAL_CO_CD") + "," + ds_aplyDealCo.GetColumn(iRow, "DEAL_CO_NM") + "," + ds_aplyDealCo.GetColumn(iRow, "SALE_GRP") + ";";
		}


		ds_aplyDealCo.ClearData();
		var arrDEAL_CO = sDEAL_CO.split(";", "webstyle");

		for(var i=0, n=arrDEAL_CO.length-1; i<n; i++)
		{		
			for( var nRow=0; nRow<ds_orgInfo.rowcount; nRow++ ){
				if(ds_orgInfo.GetColumn(nRow,"SELECT4") == "1"){		
					var arrDEALInfo= arrDEAL_CO[i].split(",","webstyle");
					ds_aplyDealCo.AddRow();
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "ORG_ID", ds_orgInfo.GetColumn(nRow,"ORG_ID_4"));
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "DEAL_CO_CD", arrDEALInfo[0]);
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "DEAL_CO_NM", arrDEALInfo[1]);
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "SALE_GRP",   arrDEALInfo[2]);
					ds_aplyDealCo.SetColumn(ds_aplyDealCo.lastrow, "SELECTED", "0");

				}
			}

		}

	}

	//ds_aplyDealCo.UnFilter();
	ds_aplyDealCo.Filter("ORG_ID='"+ds_aplyDealCo.GetColumn(0,"ORG_ID")+"'");
	ds_aplyDealCo.FireEvent = true;


}


