/*******************************************************************************
 * @description    :  확인
*******************************************************************************/
function btn_confirm_OnClick(obj)
{

    if (ds_aplyModel.rowcount == 0) {
	    //적용모델을 입력하여주십시요.
		alert(cf_getMessage(MSG_00083, "대상 정책을 선택하여 적용모델"));
		return false;
    }

    if (ds_objtModel.rowcount == 0) {
		parent.ds_onLineCmms.SetColumn(parent.ds_onLineCmms.lastrow,"ALL_MDL_APLY_YN","N");    
    } else {
		parent.ds_onLineCmms.SetColumn(parent.ds_onLineCmms.lastrow,"ALL_MDL_APLY_YN","N");    
    }

	//parent.ds_gnrlPrMny.SetColumn(parent.ds_gnrlPrMny.lastrow,"ALL_MDL_APLY_YN",ds_conditionModel.GetColumn(ds_conditionModel.lastrow,"ALL_MDL_APLY_YN"));    
	parent.tab_onLineCmms.tab_prodInfo.div_process.cmb_mfact.index = 0;  //제조사 전체로 세팅
	parent.tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic1.index = 0;  //상품특성1 전체로 세팅
	parent.tab_onLineCmms.tab_prodInfo.div_process.cmb_prodChrtic2.index = 0;  //상품특성2 전체로 세팅
	parent.tab_onLineCmms.tab_prodInfo.div_process.cmb_sktOperYn.index = 0;  //SK운영여부 전체로 세팅


	parent.ds_aplyModel.ClearData();
	parent.ds_objtModel.ClearData();
	parent.ds_aplyModel.Copy(ds_aplyModel);
	parent.ds_objtModel.Copy(ds_objtModel);
	close();		

}

