/*******************************************************************************
// 2. 폼 변수 정의
*******************************************************************************/
var FV_MENU_NO       = "100000072"; //메뉴번호 (100000072 : 판매가격표 조회)
var FV_ORG_ID;	//조직코드
var FV_ORG_CL;  //조직구분

/*******************************************************************************
* 함수명 : POLPSM00130_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function POLPSM00130_OnLoadCompleted(obj)
{
	// 조회기준일시 셋팅.
	strDate = GetDate();	
	div_search.cal_searchDtm.Text = Mid(strDate,0,8);
	div_search.edt_hour.Value = Mid(strDate,8,2);	
	div_search.edt_min.Value = Mid(strDate,10,2);

	// 권한.
	cf_setAuth(FV_MENU_NO);

    // 세션정보 취득.
    FV_ORG_ID = gds_session.GetColumn(0,"orgId");
    FV_ORG_CL = gds_session.GetColumn(0,"orgCl");	

    http.Sync = true;
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();
	//cf_setCommonCode("div_search.cmb_dsNetCd", "ZPOL_C_00110", "선택"); //유통망

	cf_setCommonCode("ds_00101",     "ZPOL_C_00101");  //영업망	 소매
	cf_setCommonCode("ds_00102",     "ZPOL_C_00102");  //영업망	 도매 
	cf_setCommonCode("ds_00103",     "ZPOL_C_00103");  //영업망	 온라인소매
	cf_setCommonCode("ds_00104",     "ZPOL_C_00104");  //영업망	 온라인도매
	cf_setCommonCode("ds_00105",     "ZPOL_C_00105");  //영업망	 M채널

	cf_setCommonCode("ds_agrmtPrd",  "ZPOL_C_00230");  //약정기간
	cf_setCommonCode("ds_dsNet",     "ZPOL_C_00110");  //유통망

	cf_setCommonCode("ds_prodChrtic1", "ZBAS_C_00020");  //상품특성1
	cf_setCommonCode("ds_prodChrtic2", "ZBAS_C_00030");  //상품특성2
	cf_setCommonCode("ds_prodChrtic3", "ZBAS_C_00460");  //상품특성1
	cf_setCommonCode("ds_prodChrtic4", "ZBAS_C_00470");  //상품특성2

	//cf_setCommonCode("div_search.cmb_slNetCd", "ZPOL_C_00102", "선택"); //영업망
	cf_checkCommonCode();

	//trace(ds_00101.SaveXML());

	ds_slNetCd.ClearData();
	//ds_slNetCd.AddRow();
	//ds_slNetCd.SetColumn(0,"COMM_CD_ID",""); 
	//ds_slNetCd.SetColumn(0,"COMM_CD_VAL",""); 
	//ds_slNetCd.SetColumn(0,"COMM_CD_VAL_NM","선택"); 
	var sale_cl = sf_convertDealCoClToSlCl(gds_session.GetColumn(0, "orgareacl1"));
    //alert(sale_cl);	
    if(sale_cl=="01") {
		ds_slNetCd.AppendDataset(ds_00101);
    } else if(sale_cl=="02") {
		ds_slNetCd.AppendDataset(ds_00102);
    } else if(sale_cl=="03") {
		ds_slNetCd.AppendDataset(ds_00103);
    } else if(sale_cl=="04") {
		ds_slNetCd.AppendDataset(ds_00104);
    } else if(sale_cl=="05") {
		ds_slNetCd.AppendDataset(ds_00105);
    } else {
		ds_slNetCd.AppendDataset(ds_00101);
		ds_slNetCd.AppendDataset(ds_00102);
		ds_slNetCd.AppendDataset(ds_00103);
		ds_slNetCd.AppendDataset(ds_00104);
		ds_slNetCd.AppendDataset(ds_00105);
    }


	//trace(ds_00101.SaveXML());

	ds_agrmtPrd.DeleteRow(0);  //약정기간 없음 삭제

	div_search.cmb_slNetCd.Index = 0;    //영업망
	div_search.cmb_agrmtPrd.Index = 0;    //약정기간
	div_search.cmb_amtUnit.Index = 0;    //금액단위
	http.Sync = false;
	// 그리드 체크 박스 및 정렬 - 옵션
	cf_setGrdChecknSortEvent(grd_polSplst);

	grd_polSplst.Format = "Default";
	grd_polSplst.AutoFit = false;



	// 대리점 화면 권한 / 세션 값 설정
	// @Param sDiv        : 대리점 컨트롤이 속한 DIV 컨트롤 아이디 문자열
	// @Param sAgency     : 대리점명, 대리점코드, 대리점버튼 순의 아이디 문자열
	// @Param sAgencyUkey : 대리점 Ukey 코드(생략가)

	//uf_enableAgencyByAuth("div_search", "edt_posAgencyNm,edt_posAgencyId,btn_agency");

	//alert(ds_slNetCd.GetColumn(0,"COMM_CD_ID"));
	ds_condition.SetColumn(0,"COMM_CD_ID",ds_slNetCd.GetColumn(0,"COMM_CD_ID"));
	//gds_session.GetColumn(0, "orgareacl1");	
	//f_getOrgLst();

}



/*******************************************************************************
* 함수명 : f_getOrg 
* 기능명 : 대리점 정보 가져오기
*******************************************************************************/
function f_getOrgLst()
{
		for( var nRow = 0; nRow < ds_dsNet.rowcount; nRow++ ) {
			aDs = "ds_polCpsLst" + "_" + ds_dsNet.GetColumn(nRow,"COMM_CD_VAL");
			for( var iRow = 0; iRow < ds_orgLst.rowcount; iRow++ ) {

				aDs = "ds_polCpsLst" + "_" + ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") + "_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4");
				if( IsValidObject(aDs) == false ) {

					Create("Dataset", aDs);

					object(aDs).AddColumn("SORT_SEQ");
					object(aDs).AddColumn("MFACT_ID");
					object(aDs).AddColumn("DEAL_CO_NM");
					object(aDs).AddColumn("PROD_CD");
					object(aDs).AddColumn("PROD_NM");
					object(aDs).AddColumn("PROD_CHRTIC_1");
					object(aDs).AddColumn("PROD_CHRTIC_2");
					object(aDs).AddColumn("PROD_CHRTIC_3");
					object(aDs).AddColumn("PROD_CHRTIC_4");

					object(aDs).AddColumn("FIX_CRDT_PRCHS_PRC");
					object(aDs).AddColumn("ALLOT_PR_MNY_S10101_0");
					object(aDs).AddColumn("ALLOT_SALE_PRC_S10101_0");
					object(aDs).AddColumn("NEW_AGRMT_AMT");
					object(aDs).AddColumn("CHG_AGRMT_AMT");

					object(aDs).AddColumn("ALLOT_S10101");
					object(aDs).AddColumn("ALLOT_S10201");
					object(aDs).AddColumn("ALLOT_S10202");
					object(aDs).AddColumn("ALLOT_S20101");
					object(aDs).AddColumn("ALLOT_S20102");
					object(aDs).AddColumn("ALLOT_S20103");
					object(aDs).AddColumn("ALLOT_S20201");

					object(aDs).AddColumn("CMMS_S10101");
					object(aDs).AddColumn("CMMS_S10201");
					object(aDs).AddColumn("CMMS_S10202");
					object(aDs).AddColumn("CMMS_S20101");
					object(aDs).AddColumn("CMMS_S20102");
					object(aDs).AddColumn("CMMS_S20103");
					object(aDs).AddColumn("CMMS_S20201");

					object(aDs).AddColumn("CASH_S10101");
					object(aDs).AddColumn("CASH_S10201");
					object(aDs).AddColumn("CASH_S10202");
					object(aDs).AddColumn("CASH_S20101");
					object(aDs).AddColumn("CASH_S20102");
					object(aDs).AddColumn("CASH_S20103");
					object(aDs).AddColumn("CASH_S20201");


				} else {
					object(aDs).ClearData();
				}

				aDs = "ds_prmny" + "_" + ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") + "_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4");
				if( IsValidObject(aDs) == false ) {

					Create("Dataset", aDs);
					//Trace(aDs);
					object(aDs).AddColumn("PROD_CD");

					object(aDs).AddColumn("PR_MNY_01_S10101");
					object(aDs).AddColumn("PR_MNY_01_S10201");
					object(aDs).AddColumn("PR_MNY_01_S10202");
					object(aDs).AddColumn("PR_MNY_01_S20101");
					object(aDs).AddColumn("PR_MNY_01_S20102");
					object(aDs).AddColumn("PR_MNY_01_S20103");
					object(aDs).AddColumn("PR_MNY_01_S20201");

					object(aDs).AddColumn("PR_MNY_04_S10101");
					object(aDs).AddColumn("PR_MNY_04_S10201");
					object(aDs).AddColumn("PR_MNY_04_S10202");
					object(aDs).AddColumn("PR_MNY_04_S20101");
					object(aDs).AddColumn("PR_MNY_04_S20102");
					object(aDs).AddColumn("PR_MNY_04_S20103");
					object(aDs).AddColumn("PR_MNY_04_S20201");


				} else {
					object(aDs).ClearData();
				}


		}


		}
		//alert(IsValidObject(object("ddd")));
}

/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	var sFilterDs = "";
	var aDs    = "ds_polCpsLst";

	if (sSvcID == "svcSearch_2") {
	    set_ds("1");
	} else if (sSvcID == "svcSearch_1") {
	    set_ds("0");
	} else if (sSvcID == "svcSearch_0") {

        //trace(ds_prmny_0_41001.SaveXML());	

		grd_polSplst.Redraw = false;
        aDs = "ds_polSplst";
		f_drawGrid();


		if( IsValidObject(aDs) == false ) {
			Create("Dataset", aDs);
		} else {
			object(aDs).Clear();
		}


		object(aDs).AddColumn("SORT_SEQ");
