			for( nRow = 0; nRow < object(aDsOrg).rowcount; nRow++ ) {
				if (ToNumber(object(aDsOrg).GetColumn(nRow,"NEW_AGRMT_AMT")) > 0 ) {
					set_org = ds_orgLst.GetColumn(iRow,"ORG_ID_4");
					break;
				}
		    }
		}
        //trace(set_org);

		for( var iRow = 0; iRow < ds_orgLst.rowcount; iRow++ ) {

			object(aDs).AddColumn("CASH_S10101_0_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4"));
