		//SubStr(sFilterDs,0,Length(sFilterDs)-nAUnit)

		//alert(Floor(ToNumber(ToNumber("273000")-ToNumber("-28000"))/ToNumber("10000")));

		//가격표에 사용 된 모델 세팅
		var nAUnit = div_search.cmb_amtUnit.value;
		var nExistRow = 0;

        //평균 구하기 변수
		var SET_S10101_0 = 0;
		var SET_S10201_0 = 0;
		var SET_S10202_0 = 0;
		var SET_S20101_0 = 0;
		var SET_S20102_0 = 0;
		var SET_S20103_0 = 0;
		var SET_S20201_0 = 0;

		var SET_S10101_1 = 0;
		var SET_S10201_1 = 0;
		var SET_S10202_1 = 0;
		var SET_S20101_1 = 0;
		var SET_S20102_1 = 0;
		var SET_S20103_1 = 0;
		var SET_S20201_1 = 0;

		var SET_S10101_2 = 0;
		var SET_S10201_2 = 0;
		var SET_S10202_2 = 0;
