				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10201"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S10201_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10201"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10202"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S10202_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10202"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20101"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20101_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20101"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20102"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20102_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20102"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20103"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20103_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20103"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20201"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20201_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_1_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20201"))/ToNumber(nAUnit)));
				    }

				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10101"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S10101_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10101"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10201"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S10201_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10201"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10202"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S10202_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S10202"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20101"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20101_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20101"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20102"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20102_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20102"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20103"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20103_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20103"))/ToNumber(nAUnit)));
				    }
				    if(uf_isNull(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20201"))== false) {
					object(aDs).SetColumn(object(aDs).lastrow, "PR_MNY_04_S20201_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4"),     Floor(ToNumber(object("ds_prmny_2_"+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")).GetColumn(iRow,"PR_MNY_04_S20201"))/ToNumber(nAUnit)));
				    }


					object(aDs).SetColumn(object(aDs).lastrow, "A_S10101_0", Floor(SET_S10101_0/CNT_S10101_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10201_0", Floor(SET_S10201_0/CNT_S10201_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10202_0", Floor(SET_S10202_0/CNT_S10202_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20101_0", Floor(SET_S20101_0/CNT_S20101_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20102_0", Floor(SET_S20102_0/CNT_S20102_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20103_0", Floor(SET_S20103_0/CNT_S20103_0));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20201_0", Floor(SET_S20201_0/CNT_S20201_0));

					object(aDs).SetColumn(object(aDs).lastrow, "A_S10101_1", Floor(SET_S10101_1/CNT_S10101_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10201_1", Floor(SET_S10201_1/CNT_S10201_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10202_1", Floor(SET_S10202_1/CNT_S10202_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20101_1", Floor(SET_S20101_1/CNT_S20101_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20102_1", Floor(SET_S20102_1/CNT_S20102_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20103_1", Floor(SET_S20103_1/CNT_S20103_1));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20201_1", Floor(SET_S20201_1/CNT_S20201_1));

					object(aDs).SetColumn(object(aDs).lastrow, "A_S10101_2", Floor(SET_S10101_2/CNT_S10101_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10201_2", Floor(SET_S10201_2/CNT_S10201_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S10202_2", Floor(SET_S10202_2/CNT_S10202_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20101_2", Floor(SET_S20101_2/CNT_S20101_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20102_2", Floor(SET_S20102_2/CNT_S20102_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20103_2", Floor(SET_S20103_2/CNT_S20103_2));
					object(aDs).SetColumn(object(aDs).lastrow, "A_S20201_2", Floor(SET_S20201_2/CNT_S20201_2));


				}




		}


		//alert(ds_dsNet.GetColumn(0,"COMM_CD_VAL")+"=="+ds_dsNet.GetColumn(0,"COMM_CD_VAL_NM"));
		//ds_dsNet.rowcount

		grd_polSplst.Format = "Default";
		grd_polSplst.BindDataset = aDs;
		object(aDs).Sort("SORT_SEQ"); 
		grd_polSplst.Redraw = true;

		//object(aDs).row = 0;
		//object(aDs).SelectRow(0,true);
		//object(aDs).SelectRow(object(aDs).lastrow,false);



	}

}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search(sButtonID)
{	


	var sArg  = "org_id="; // 필수

	var sHeight = 0;
	var sWidth  = 0;
	sWidth  = 1;
	sHeight = 1;
	var sResult = dialog("pol::POLPSM00130_01.xml", "", 490, 520);

    //alert(sResult);	
    if ( sResult == 1 ) {
		// 선택한 대리점 세팅
		f_getOrgLst();	

		// 조회값 선택 여부 확인 
		//cf_checkObjValues(div_search);

		// 적용조직 가져오기
		f_getSaleTypCdMin();

		set_ds("2");
    } else {
    }


}

function set_ds(sDS_NET_CD)
{	
    var aDs = "";
	//for( var nRow = 0; nRow < ds_dsNet.rowcount; nRow++ ) {
	    //alert(ds_dsNet.GetColumn(nRow,"COMM_CD_VAL"));

	ds_condition.SetColumn(0,"DS_NET_CD",sDS_NET_CD);

	for( var iRow = 0; iRow < ds_orgLst.rowcount; iRow++ ) {
		aDs = "ds_polCpsLst" + "_" + ds_condition.GetColumn(0,"DS_NET_CD") + "_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4");
		object(aDs).Clear();
	}
    ds_polSplst.Clear();

	var sSvcID = "svcSearch_"+ds_condition.GetColumn(0,"DS_NET_CD");
	var sInDs  = " ds_condition=ds_condition ";
		sInDs += " ds_orgLst=ds_orgLst ";                  //전대리점정보
		//sInDs += " ds_dsNet =ds_dsNet ";                  //전대리점정보
	var sOutDs = "ds_polSplstTmp=ds_polSplstTmp";

		aDs  = "ds_polCpsLst" + "_" + ds_condition.GetColumn(0,"DS_NET_CD");
		for( var iRow = 0; iRow < ds_orgLst.rowcount; iRow++ ) {
			aDs     = "ds_polCpsLst" + "_" + ds_condition.GetColumn(0,"DS_NET_CD") + "_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4");
			sOutDs += " "+aDs+"="+aDs+" ";                  //전대리점정보
			aDs    = "ds_prmny" + "_" + ds_condition.GetColumn(0,"DS_NET_CD") + "_" + ds_orgLst.GetColumn(iRow,"ORG_ID_4");
			sOutDs += " "+aDs+"="+aDs+" ";                  //전대리점정보
		}
		//alert(sOutDs);
	var sArg = "sktst.pol.PSM#getPolCpsLst";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg, "", true, true);
	//}
}
