function Div2_btn_excelDown_OnClick(obj)
{
	cf_exportExcel(grd_polSplst);	
}


/*******************************************************************************
* 함수명 : f_drawGrid
* 기능명 : 판매가격표 리스트 그리기 생성 
*******************************************************************************/
function f_drawGrid()
{
	var grdName = "grd_polSplst";
	//var dCnt = ds_dsNet.rowcount;

	var sContents = '<contents>\n';
	sContents += '<format id="Default">\n';

	var sContColums = '<columns>\n';
		sContColums	+=	'<col fix="left" width="60"/>\n';
		sContColums	+=	'<col fix="left" width="60"/>\n';
		sContColums	+=	'<col fix="left" width="50"/>\n';
		sContColums	+=	'<col fix="left" width="50"/>\n';
		sContColums	+=	'<col fix="left" width="50"/>\n';
		sContColums	+=	'<col fix="left" width="50"/>\n';
		sContColums	+=	'<col fix="left" width="50"/>\n';

		sContColums	+=	'<col width="13"/>\n';
		sContColums	+=	'<col width="13"/>\n';

		for( nRow = 0; nRow < ds_dsNet.rowcount; nRow++ ) {
			for( iRow = 0; iRow < ds_saleTypCdMin.rowcount; iRow++ ) {
				for( var sRow = 0; sRow < ds_orgLst.rowcount; sRow++ ) {
		             sContColums	+=	'<col width="13"/>\n';
		             sContColums	+=	'<col width="13"/>\n';
		             sContColums	+=	'<col width="13"/>\n';
   					 if ((sRow+1) == ds_orgLst.rowcount) {
		             sContColums	+=	'<col width="13"/>\n';
   					 }
				}
			}
		}
	sContColums	+=	'</columns>\n';
	sContents = sContents + sContColums;

	var sContHeads  = '<head>\n';

		sContHeads += '<cell bkcolor="user34" col="0" color="user35" display="text" font="돋움,8" rowspan="4" text="제조사"/>\n';
		sContHeads += '<cell bkcolor="user34" col="1" color="user35" display="text" font="돋움,8" rowspan="4" text="모델"/>\n';
		sContHeads += '<cell bkcolor="user34" col="2" color="user35" display="text" font="돋움,8" rowspan="4" text="상품&#10;특성1" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="3" color="user35" display="text" font="돋움,8" rowspan="4" text="상품&#10;특성2" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="4" color="user35" display="text" font="돋움,8" rowspan="4" text="상품&#10;특성3" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="5" color="user35" display="text" font="돋움,8" rowspan="4" text="상품&#10;특성4" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="6" color="user35" display="text" font="돋움,8" rowspan="4" text="기준&#10;판매가" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="7" color="user35" colspan="2" display="text" font="돋움,8" rowspan="2" text="T기본약정"/>\n';
		sContHeads += '<cell bkcolor="user34" col="7" color="user35" display="text" font="돋움,8" row="2" text="신규" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="8" color="user35" display="text" font="돋움,8" row="2" text="기변" wordwrap="exchar"/>\n';
		sContHeads += '<cell bkcolor="user34" col="7" color="user35" colspan="2" display="text" font="돋움,8" row="3" text="약정금액"/>\n';
	var colNum = 9;
		for( nRow = 0; nRow < ds_dsNet.rowcount; nRow++ ) {
			sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" colspan="'+(ds_saleTypCdMin.rowcount*(1+(ds_orgLst.rowcount*3)))+'" display="text" font="돋움,8" text="'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL_NM")+'"/>\n';
			for( iRow = 0; iRow < ds_saleTypCdMin.rowcount; iRow++ ) {
			sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" colspan="'+(1+(ds_orgLst.rowcount*3))+'" display="text" font="돋움,8" row="1" text="'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL_NM")+'" wordwrap="exchar"/>\n';
				for( var sRow = 0; sRow < ds_orgLst.rowcount; sRow++ ) {
					sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="2" text="'+ds_orgLst.GetColumn(sRow,"ORG_NM_4")+'"/>\n';
				    if (ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") = "1") {    //T할부지원
						sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="할부"/>\n';
				    } else if (ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") = "0") {  //무약정
				        if (ds_slNetCd.GetColumn(ds_slNetCd.SearchRow("COMM_CD_VAL ==" + quote(ds_condition.GetColumn(0,"SL_NET_CD"))),"COMM_CD_ID") = "ZPOL_C_00102") {    //도매  ds_condition  SL_NET_CD
						sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="할부"/>\n';
				        } else {
						sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="현금"/>\n';
				        }
				    } else {
						sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="현금"/>\n';
				    }
					colNum++;
					sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" colspan="2" display="text" font="돋움,8" row="2" text="PS&M"/>\n';
					sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="본사"/>\n';
					colNum++;
					sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="3" text="대리점"/>\n';
					colNum++;
					if ((sRow+1) == ds_orgLst.rowcount) {
						sContHeads += '<cell bkcolor="user34" col="'+colNum+'" color="user35" display="text" font="돋움,8" row="2" rowspan="2" text="평균"/>\n';
						colNum++;
					}
				}
			}
		}

	sContHeads += '</head>\n';
	sContents = sContents + sContHeads;

	var sContBodys = '<body>\n';

	sContBodys += '<cell                col="0" colid="DEAL_CO_NM"        display="text"   font="돋움,8"/>\n';
	sContBodys += '<cell align="left"   col="1" colid="PROD_NM"           display="text"   font="돋움,8"/>\n';
	sContBodys += '<cell align="center" col="2" colid="PROD_CHRTIC_1"     combocol="COMM_CD_VAL" combodataset="ds_prodChrtic1" combotext="COMM_CD_VAL_NM" display="combo"   font="돋움,8"/>\n';
	sContBodys += '<cell align="center" col="3" colid="PROD_CHRTIC_2"     combocol="COMM_CD_VAL" combodataset="ds_prodChrtic2" combotext="COMM_CD_VAL_NM" display="combo"   font="돋움,8"/>\n';
	sContBodys += '<cell align="center" col="4" colid="PROD_CHRTIC_3"     combocol="COMM_CD_VAL" combodataset="ds_prodChrtic3" combotext="COMM_CD_VAL_NM" display="combo"   font="돋움,8"/>\n';
	sContBodys += '<cell align="center" col="5" colid="PROD_CHRTIC_4"     combocol="COMM_CD_VAL" combodataset="ds_prodChrtic4" combotext="COMM_CD_VAL_NM" display="combo"   font="돋움,8"/>\n';
	sContBodys += '<cell align="center" col="6" colid="FIX_CRDT_PRCHS_PRC" display="number" font="돋움,8"/>\n';
	sContBodys += '<cell align="right"  col="7" colid="NEW_AGRMT_AMT"     display="number" bkcolor="user8" font="돋움,8"/>\n';
	sContBodys += '<cell align="right"  col="8" colid="CHG_AGRMT_AMT"     display="number" bkcolor="user8" font="돋움,8"/>\n';


	var colNum = 9;
	for( nRow = 0; nRow < ds_dsNet.rowcount; nRow++ ) {
		for( iRow = 0; iRow < ds_saleTypCdMin.rowcount; iRow++ ) {
			for( var sRow = 0; sRow < ds_orgLst.rowcount; sRow++ ) {

			    if (ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") == "0") {
					sContBodys += '<cell align="right" col="'+colNum+'" colid="CASH_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'_'+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")+'" display="number" bkcolor="user13" font="돋움,8"/>\n';
			    } else if (ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") == "1") {
					sContBodys += '<cell align="right" col="'+colNum+'" colid="CMMS_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'_'+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")+'" display="number" bkcolor="user13" font="돋움,8"/>\n';
			    } else if (ds_dsNet.GetColumn(nRow,"COMM_CD_VAL") == "2") {
					sContBodys += '<cell align="right" col="'+colNum+'" colid="CASH_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'_'+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")+'" display="number" bkcolor="user13" font="돋움,8"/>\n';
			    }
				colNum++;
				sContBodys += '<cell align="right" col="'+colNum+'" colid="PR_MNY_01_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'_'+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")+'" display="number" font="돋움,8"/>\n';
				colNum++;
				sContBodys += '<cell align="right" col="'+colNum+'" colid="PR_MNY_04_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'_'+ ds_orgLst.GetColumn(sRow,"ORG_ID_4")+'" display="number" font="돋움,8"/>\n';


				if ((sRow+1) == ds_orgLst.rowcount) {
	                colNum++;
					sContBodys += '<cell align="right" col="'+colNum+'" colid="A_'+ds_saleTypCdMin.GetColumn(iRow,"COMM_CD_VAL")+'_'+ds_dsNet.GetColumn(nRow,"COMM_CD_VAL")+'" display="number" bkcolor="user8" font="돋움,8"/>\n';
				}
				colNum++;
			}
		}
	}

	sContBodys += '</body>\n';
	sContents = sContents + sContBodys;


	sContents	+= '</format>\n';
	sContents	+= '</contents>\n';

	var objGrid = object(grdName);

	objGrid.Contents = sContents;

}

function div_search_cmb_orgNm_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	grd_polSplst.Format = "Init";
}


/*******************************************************************************
* 함수명 : f_getSaleTypCdMin
* 기능명 : 세부판매유형 코드 조회
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function f_getSaleTypCdMin()
{
	var sSvcID = "svcSearchSaleTypCdMin";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_saleTypCdMin=ds_saleTypCdMin";
	var sArg = "sktst.pol.PSM#getSaleTypCdMin";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);
}






/*******************************************************************************
* 함수명 : div_search_edt_hour_OnChanged
* 기능명 :  조회기준일시 시간 변경시 
*******************************************************************************/
function div_search_edt_hour_OnChanged(obj,strText)
{
	if(uf_isNull(strText) == false){

		if(length(strText) !=2) {

			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"SEARCH_HOUR",strText);
		}

		if(strText > 23){
			ds_condition.SetColumn(0,"SEARCH_HOUR","00");
			obj.SetFocus();
			alert("23시를 초과할수 없습니다.");
			return false;
		}
	}
}

/*******************************************************************************
* 함수명 : div_search_edt_min_OnChanged
* 기능명 :  조회기준일시 분 변경시 
*******************************************************************************/
function div_search_edt_min_OnChanged(obj,strText)
{
		if(uf_isNull(strText) == false){

		if(length(strText) !=2) {
			strText = Lpad(strText,"0",2);
			ds_condition.SetColumn(0,"SEARCH_MINUTE",strText);
		}

		if(strText > 59){
			ds_condition.SetColumn(0,"SEARCH_MINUTE","00");
			obj.SetFocus();
			alert("59분을 초과할수 없습니다.");
			return false;
		}
	}
}

/*******************************************************************************
* 함수명 : div_search_cmb_slNetCd_OnChanged
* 기능명 :  영업망 변경시 조회조건에 영업구분 세팅
*******************************************************************************/
function div_search_cmb_slNetCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    ds_condition.SetColumn(0,"COMM_CD_ID",ds_slNetCd.GetColumn(nNewIndex,"COMM_CD_ID"));
	//alert(ds_slNetCd.GetColumn(nNewIndex,"COMM_CD_ID"));
}



/*******************************************************************************
 * @description    :  거래처구분코드를 영업구분코드로 변환
*******************************************************************************/
function sf_convertDealCoClToSlCl(sDealCoCl)
{
	var sRetValue;
	switch(sDealCoCl)
	{
	case "A2":
		sRetValue = "01";
		break;

	case "A3":
	case "A4":	
		sRetValue = "02";
		break;

	case "B1":	
		sRetValue = "03";
		break;

	case "B2":	
		sRetValue = "04";
		break;

	default:
		break;
	}
	return sRetValue;
}
