	//cf_setCommonCode("ds_prMnyCl", "ZPOL_C_00520");   //일반장려금정책구분

	cf_checkCommonCode();

	//div_search.cal_polYm.Value = substr(Today(),0,6);
	ds_condition.setColumn(0, "APLY_YM", APLY_YM);
	ds_condition.setColumn(0, "ORG_ID", ORG_ID);
	ds_condition.setColumn(0, "POL_OPER", POL_OPER);
	ds_condition.setColumn(0, "PLST_APLY_YN", PLST_APLY_YN);
	ds_condition.setColumn(0, "ACC_APLY_YN", ACC_APLY_YN);
	ds_condition.setColumn(0, "SEARCH_CL", SEARCH_CL);
	ds_condition.SetColumn(0, "STL_PLC",STL_PLC);
	ds_condition.SetColumn(0, "SL_CL",SL_CL);


	//dialog("main::processingBar.xml","",350,120,"TitleBar=false CloseFlag=false");

	df_polOperSearch();
	df_search();
	//ds_polOper.setColumn(0, "COMM_CD_VAL", "");
	//ds_polOper.setColumn(0, "COMM_CD_VAL_NM", "전체");
	//div_search.cmb_polOper.Index = 0;    //정책운영

	/**
	var oWinObj = AllWindows("processingBar");

