function grd_polSaleTrgtAchvLst_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell < 9) {
		var sArg  = "IPOL_ID="+quote(ds_asmptPrMnyDtl.getColumn(nRow,"IPOL_ID"));
			sArg += " POL_YM=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"POL_YM")); 
			sArg += " POL_TS=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"POL_TS")); 
			sArg += " APLY_STA_DTM=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"APLY_STA_DTM")); 
			sArg += " APLY_END_DTM=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"APLY_END_DTM")); 
			sArg += " LAST_YN=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"LAST_YN")); 
			sArg += " POP_YN=" + quote("Y"); 
		//gf_OpenWindow( "100000133", sArg); 

		var sHeight = 600;
		var sWidth  = 830;

		if (ds_asmptPrMnyDtl.getColumn(nRow,"POL_TYP_CD") == "일반장려금") {
			dialog("pol::POLBOP00200.xml", sArg, sWidth, sHeight,,30 ,60 );
		} else if (ds_asmptPrMnyDtl.getColumn(nRow,"POL_TYP_CD") == "부가상품장려금") {
			dialog("pol::POLBOP00500.xml", sArg, sWidth, sHeight,,30 ,60 );
		} else {
          alert("관리자에게 확인 바랍니다.");
		}

	} else {

		var sArg  = " POL_ID="+quote(ds_asmptPrMnyDtl.getColumn(nRow,"IPOL_ID"));
			sArg += " POL_YM=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"POL_YM")); 
			sArg += " POL_TS=" + quote(ds_asmptPrMnyDtl.getColumn(nRow,"POL_TS")); 
			sArg += " APLY_YM=" + quote(ds_condition.getColumn(0,"APLY_YM")); 
			sArg += " ORG_ID=" + quote(ds_condition.getColumn(0,"ORG_ID")); 
			sArg += " POL_OPER=" + quote(ds_condition.getColumn(0,"POL_OPER")); 
			sArg += " PLST_APLY_YN=" + quote(ds_condition.getColumn(0,"PLST_APLY_YN")); 
			sArg += " ACC_APLY_YN=" + quote(ds_condition.getColumn(0,"ACC_APLY_YN")); 
		//gf_OpenWindow( "100000133", sArg); 

		var sHeight = 500;
		var sWidth  = 1024;

		if (ds_asmptPrMnyDtl.getColumn(nRow,"POL_TYP_CD") == "일반장려금") {
			dialog("pol::POLPSM00600.xml", sArg, sWidth, sHeight,,30 ,60 );
		} else if (ds_asmptPrMnyDtl.getColumn(nRow,"POL_TYP_CD") == "부가상품장려금") {
			//dialog("pol::POLPSM00600.xml", sArg, sWidth, sHeight,,30 ,60 );
		} else {
          alert("관리자에게 확인 바랍니다.");
		}

	}

}
