/*******************************************************************************
// 2. 폼 변수 정의
*******************************************************************************/
var FV_STR_NAME;    // 사용자 이름
var FV_ORG_ID;		// 소속
var FV_ORG_CL;		// 소속구분
var FV_POL_CL;      // 정책구분

/*******************************************************************************
* 함수명 : POLSPM00210_OnLoadCompleted
* 기능명 : OnLoadCompleted 이벤트
*******************************************************************************/
function POLSPM00210_OnLoadCompleted(obj)
{
    FV_POL_CL = POL_CL;

	cf_setGrdChecknSortEvent(grd_polSplst);	// 그리드 정렬
	// 권한 설정
	cf_setAuth(pFrmNum);

	FV_ORG_ID     = gds_session.GetColumn(0, "orgid");
	FV_ORG_CL     = gds_session.GetColumn(0, "orgcl");

	http.Sync = true;
	// 공통 코드를 위한 데이타셋 설정
	cf_setCommonCodeDsEnv();
	cf_setCommonCode("div_search.cmb_saleTypCd", "ZPOL_C_00030", "전체"); //판매유형
	cf_checkCommonCode();

//	ds_adptProdMgmt.Copy(parent.ds_CopyAdptProdMgmt);

//	f_setTotalCount(ds_prodMgmt.RowCount(), ds_adptProdMgmt.RowCount());

	div_search.cal_polYm.Value = substr(Today(),0,6);

//	cf_copyDataset(ds_zPolC, parent.ds_zPolC, null, true);

	if(uf_isNull(SPLST_ID) == true){ //신규버튼 클릭시
		uf_ObjComponentEnable(div_search,true);
	}else{
		uf_ObjComponentEnable(div_search,false);
	}

	//uf_enableAgencyByAuth("div_search", "edt_orgNm,edt_orgId,btn_org");

	http.Sync = false;
}

/*******************************************************************************
* 함수명 : f_callBack 
* 기능명 : 트랜잭션 콜백 
* 파라미터
1. sSvcID  : 서비스 아이디
*******************************************************************************/
function f_callBack(sSvcID)
{
	if(sSvcID == "svcSearch"){

		return;

	}
}

/*******************************************************************************
* 함수명 : df_search
* 기능명 : 공통 조회 버튼 클릭
* 파라미터
1. sButtonID  : 버튼 아이디
*******************************************************************************/
function df_search(sButtonID)
{

	if(uf_isNull(ds_condition.GetColumn(0,"aply_ym")) == true){
		alert(cf_getMessage(MSG_00135, "적용년월"));
        div_search.cal_polYm.SetFocus();
		return false; 
	}

	if(uf_isNull(ds_condition.GetColumn(0,"org_id")) == true){
		alert(cf_getMessage(MSG_00135, "조직"));
		div_search.edt_orgNm.SetFocus();
		return false; 
	}

	ds_condition.SetColumn(0,"pol_cl", FV_POL_CL);

	var sSvcID = "svcSearch";
	var sInDs = "nc_input_fieldDs=ds_condition";
	var sOutDs = "ds_polSplst=ds_polSplst";
	var sArg = "sktst.pol.SPM#getPolSplst";

	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);

}

/*******************************************************************************
* 함수명 : btn_confirm_OnClick
* 기능명 : 확인버튼
*******************************************************************************/
function btn_confirm_OnClick(obj)
{
	if(ds_polSplst.currow == "-1"){
		alert(cf_getMessage(MSG_00134, "판매가격표 정책"));
		return false; 
	}

    var rtn[5];
    var currRow = ds_polSplst.currow;

    rtn[0] = "1";
    rtn[1] = ds_polSplst.GetColumn(currRow,"splst_id");
    rtn[2] = ds_polSplst.GetColumn(currRow,"aply_ym");
    rtn[3] = ds_polSplst.GetColumn(currRow,"org_id");
    rtn[4] = ds_polSplst.GetColumn(currRow,"sale_typ_cd");

	this.close(rtn);
}

/*******************************************************************************
* 함수명 : df_close
* 기능명 : 화면 종료
*******************************************************************************/
function btn_cancel_OnClick(sButtonID)
{
    var rtn[5];
    rtn[0] = "0";
	this.close(rtn);
}

/*******************************************************************************
 * @description    :  조직 키 이벤트
*******************************************************************************/
function div_search_edt_orgNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13)
	{
	    cf_commPopOrgCd("div_search.edt_orgId", "div_search.edt_orgNm", 13);	
	}

}

/*******************************************************************************
 * @description    :  조직 버튼 클릭 이벤트
*******************************************************************************/
function div_search_btn_org_OnClick(obj)
{
	    cf_commPopOrgCd("div_search.edt_orgId", "div_search.edt_orgNm", 13);	
}

/*******************************************************************************
 * @description    :  월달력 사용 - 이벤트
*******************************************************************************/
function btn_monthCalendar_OnClick(obj)
{
	cf_callMonthCalendar(div_search.cal_polYm);
}

/*******************************************************************************
* 함수명 : grd_polSplst_OnCellDblClick
* 기능명 :  참조가격표 리스트 OnCellDblClick 발생
*******************************************************************************/
function grd_polSplst_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	btn_confirm_OnClick();
}

function div_search_edt_orgNm_OnCharChanged(obj,strPreText,strPostText)
{
	div_search.edt_orgId.Text = "";
}
