function lf_getSalePlcInfo()
{
	sf_clearControls("div_dealCoInfo.edt_salePlcUkeyCd,div_dealCoInfo.edt_salePlcSubCd,div_dealCoInfo.edt_salePlcChannelCd");
	//var sSaleDtm = div_dealCoInfo.cal_saleDtm.Value + div_dealCoInfo.edt_saleDtmHh.Text + div_dealCoInfo.edt_saleDtmMm.Text;
	//var sSvcDtm = div_dealCoInfo.cal_svcDtm.Value + div_dealCoInfo.edt_svcDtmHh.Text + div_dealCoInfo.edt_svcDtmMm.Text;

	var sArg  = "deal_co_cl1=" +quote(sf_getDealCoCl1());
		sArg += " deal_status="+quote("3"); 	//옵션 (거래처 상태) - 1: 수납정지 N  , 2: 출고정지 N , 3: 판매정지 N 
		sArg += " pos_agency=" +quote(gds_session.GetColumn(0,"posAgencyId")); // 옵션	    
		sArg += " eff_dtm="    +quote(FV_PROC_DTM);
		sArg += " deal_co_cd=" +quote(div_dealCoInfo.edt_salePlc.Text );

	cf_callDealPop(sArg, div_dealCoInfo, "edt_salePlcNm,edt_salePlc,edt_salePlcUkeyCd,edt_salePlcSubCd,edt_salePlcChannelCd");
}

function btn_save_OnClick(obj)
{
    var sSaleChgHstCl = div_prodExchange.cmb_saleChgHstCl.Value;

    if ( !lf_saveValidationCheck() )
    {
        return;
    }

	//일반판매
	//ds_generalSale
    //var sSaleDtm = div_dealCoInfo.cal_saleDtm.Value + div_dealCoInfo.edt_saleDtmHh.Text + div_dealCoInfo.edt_saleDtmMm.Text + "00";
    var sSaleChgDtm  = div_prodExchange.cal_saleChgDtm.Value + div_prodExchange.edt_saleChgDtmHh.Text + div_prodExchange.edt_saleChgDtmMm.Text + "00";

    cf_setParam(ds_generalSale, "sale_chg_dtm"              , sSaleChgDtm );                                 //판매변경일시
    cf_setParam(ds_generalSale, "sale_chg_plc"              , div_prodExchange.edt_saleChgPlc.Text );        //판매변경일시
    cf_setParam(ds_generalSale, "acc_chg_hst_cl"            , sf_getAccChgHstCl(sSaleChgHstCl) );            //정산변경이력구분  

    cf_setParam(ds_sktPrprc   , "bf_scrb_fee"               , ds_sktPrprc.GetColumn(0, "scrb_fee_old") );    //변경전 가입비
    cf_setParam(ds_sktPrprc   , "bf_scrb_fee_cl"            , ds_sktPrprc.GetColumn(0, "scrb_fee_cl_old") ); //변경전 가입비구분
    cf_setParam(ds_sktPrprc   , "bf_mnp_cmms"               , ds_sktPrprc.GetColumn(0, "mnp_cmms_old") );    //변경전 수수료
    cf_setParam(ds_sktPrprc   , "bf_mnp_cmms_cl"            , ds_sktPrprc.GetColumn(0, "mnp_cmms_cl_old") ); //변경전 수수료구분
    cf_setParam(ds_sktPrprc   , "scrb_fee"                  , div_afSktPrprc.msk_scrbFee.Value );            //가입비
    cf_setParam(ds_sktPrprc   , "mnp_cmms"                  , div_afSktPrprc.msk_mnpCmms.Value );            //가입비
    //cf_setParam(ds_sktPrprc   , "scrb_fee_cl"               , "02" );                                        //가입비구분

    cf_setParam(ds_payment    , "bf_cash_pay_amt"           , ds_payment.GetColumn(0, "cash_pay_amt_old") );           
    cf_setParam(ds_payment    , "bf_crdtcrd_corp1"          , ds_payment.GetColumn(0, "crdtcrd_corp1_old") );          
    cf_setParam(ds_payment    , "bf_crdtcrd_pay_amt1"       , ds_payment.GetColumn(0, "crdtcrd_pay_amt1_old") ); 
    cf_setParam(ds_payment    , "bf_crdtcrd_corp2"          , ds_payment.GetColumn(0, "crdtcrd_corp2_old") ); 
    cf_setParam(ds_payment    , "bf_crdtcrd_pay_amt2"       , ds_payment.GetColumn(0, "crdtcrd_pay_amt2_old") ); 
    cf_setParam(ds_payment    , "bf_skt_prprc_cash_pay_amt" , ds_payment.GetColumn(0, "skt_cash_pay_amt_old") ); 
    cf_setParam(ds_payment    , "bf_skt_prprc_prxpay_amt"   , ds_payment.GetColumn(0, "skt_prxpay_amt_old") ); 
    cf_setParam(ds_payment    , "skt_prprc_cash_pay_amt"    , div_afSktPrprc.msk_cashPayAmt.Value ); //변경후 현금수납
    cf_setParam(ds_payment    , "skt_prprc_prxpay_amt"      , div_afSktPrprc.msk_prxpayAmt.Value );  //변경후 대납수납

	//현금판매가 - 단말기/USIM
	//현재 판매가 OLD -> 이전판매가 , 현재판매가 - 입력 받은 판매가 사용
	ds_cashSale.SetColumn(0 , "bf_eqp_cash_sale_prc"   , ds_cashSale.GetColumn(0, "eqp_cash_sale_prc_old") );
	ds_cashSale.SetColumn(0 , "bf_usim_cash_sale_prc"  , ds_cashSale.GetColumn(0, "usim_cash_sale_prc_old") );
	ds_cashSale.SetColumn(0 , "bf_etc_prod_sale_prc"   , ds_cashSale.GetColumn(0, "etc_prod_sale_prc_old") );
	ds_cashSale.SetColumn(0 , "bf_etc_prod_org_prc"    , ds_cashSale.GetColumn(0, "etc_prod_org_prc_old") );

	//할부매출
	ds_allotSale.SetColumn(0, "bf_eqp_allot_sale_prc"  , ds_allotSale.GetColumn(0, "eqp_allot_sale_prc_old") );
	ds_allotSale.SetColumn(0, "bf_usim_allot_sale_prc" , ds_allotSale.GetColumn(0, "usim_allot_sale_prc_old") );

	//약정보조금
	ds_agreementAstamt.SetColumn(0, "bf_agrmt_astamt"     , ds_agreementAstamt.GetColumn(0, "agrmt_astamt_old") );
	ds_agreementAstamt.SetColumn(0, "bf_add_agrmt_astamt" , ds_agreementAstamt.GetColumn(0, "add_agrmt_astamt_old") );

	//OCBMCARD
	ds_ocbMcard.SetColumn(0, "bf_ocb_mcard"     , ds_ocbMcard.GetColumn(0, "ocb_mcard_old") );
	ds_ocbMcard.SetColumn(0, "bf_ocb_mcard_prc" , ds_ocbMcard.GetColumn(0, "ocb_mcard_prc_old") );

	//판매수수료
	ds_saleCmms.SetColumn(0, "bf_sale_cmms"         , ds_saleCmms.GetColumn(0, "sale_cmms_old") );
	ds_saleCmms.SetColumn(0, "bf_supl_svc_sale_cmms", ds_saleCmms.GetColumn(0, "supl_svc_sale_cmms_old") );
	ds_saleCmms.SetColumn(0, "bf_add_sale_cmms"     , ds_saleCmms.GetColumn(0, "add_sale_cmms_old") );
	ds_saleCmms.SetColumn(0, "bf_cmms_adj"          , ds_saleCmms.GetColumn(0, "cmms_adj_old") );

	//장려금
	ds_promotionMoney.SetColumn(0, "bf_gnrl_pr_mny"        , ds_promotionMoney.GetColumn(0, "gnrl_pr_mny_old") );
	ds_promotionMoney.SetColumn(0, "bf_supl_svc_pr_mny"    , ds_promotionMoney.GetColumn(0, "supl_svc_pr_mny_old") );
	ds_promotionMoney.SetColumn(0, "bf_sale_suprt_amt"     , ds_promotionMoney.GetColumn(0, "sale_suprt_amt_old") );
	ds_promotionMoney.SetColumn(0, "bf_add_sale_suprt_amt" , ds_promotionMoney.GetColumn(0, "add_sale_suprt_amt_old") );

	//온라인 수납
	ds_onlinePayment.SetColumn(0, "bf_allot_pay_cl"     , ds_onlinePayment.GetColumn(0, "allot_pay_cl_old") );
	ds_onlinePayment.SetColumn(0, "bf_acc_trnsf_cl"     , ds_onlinePayment.GetColumn(0, "acc_trnsf_cl_old") );
	ds_onlinePayment.SetColumn(0, "bf_acc_trnsf_amt"    , ds_onlinePayment.GetColumn(0, "acc_trnsf_amt_old") );
	ds_onlinePayment.SetColumn(0, "bf_virtl_acc_cl"     , ds_onlinePayment.GetColumn(0, "virtl_acc_cl_old") );
	ds_onlinePayment.SetColumn(0, "bf_virtl_acc_amt"    , ds_onlinePayment.GetColumn(0, "virtl_acc_amt_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_corp1"    , ds_onlinePayment.GetColumn(0, "crdtcrd_corp1_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_foi_cl1"  , ds_onlinePayment.GetColumn(0, "crdtcrd_foi_cl1_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_pay_amt1" , ds_onlinePayment.GetColumn(0, "crdtcrd_pay_amt1_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_corp2"    , ds_onlinePayment.GetColumn(0, "crdtcrd_corp2_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_foi_cl2"  , ds_onlinePayment.GetColumn(0, "crdtcrd_foi_cl2_old") );
	ds_onlinePayment.SetColumn(0, "bf_crdtcrd_pay_amt2" , ds_onlinePayment.GetColumn(0, "crdtcrd_pay_amt2_old") );

	//온라인 수수료
	ds_onlineCmms.SetColumn(0, "bf_cash_sale_cmms"      , ds_onlineCmms.GetColumn(0, "cash_sale_cmms_old") );
	ds_onlineCmms.SetColumn(0, "bf_allot_sale_cmms"     , ds_onlineCmms.GetColumn(0, "allot_sale_cmms_old") );
	ds_onlineCmms.SetColumn(0, "bf_settl_crdtcrd_cmms"  , ds_onlineCmms.GetColumn(0, "settl_crdtcrd_cmms_old") );
	ds_onlineCmms.SetColumn(0, "bf_cmms_adj"            , ds_onlineCmms.GetColumn(0, "cmms_adj_old") );
	ds_onlineCmms.SetColumn(0, "bf_acc_trnsf_cmms"      , ds_onlineCmms.GetColumn(0, "acc_trnsf_cmms_old") );
	ds_onlineCmms.SetColumn(0, "bf_virtl_acc_cmms"      , ds_onlineCmms.GetColumn(0, "virtl_acc_cmms_old") );


   // 판매번호 채번을 위한 정보
	// 영업망 - R: 소매, A:도매, O:온라인
	// 판매구분 - E 공기기판매, G: 일반판매, P:수납대행,S:부가서비스유치
    //ds_input
    ds_input.Clear();
	cf_setParam(ds_input, "sl_net_cl"  , FV_SALE_NO_SL_NET_CD );  //영업망
	cf_setParam(ds_input, "sale_cl"    , FV_SALE_NO_SALE_CL );    //판매구분

	cf_setParam(ds_input, "if_proc_cl" , FV_IF_PROC_CL );         //Ukey Interface 처리구분  1:Ukey if , 2:PS , 3: , 4:판매처변경, 5:가입비분납구분
	cf_setParam(ds_input, "op_dt"      , ds_scrbfeeChgIfInfo.GetColumn(0,"op_dt") );   //처리일자
	cf_setParam(ds_input, "op_tm"      , ds_scrbfeeChgIfInfo.GetColumn(0,"op_tm") );   //처리시각
	cf_setParam(ds_input, "seq"        , ds_scrbfeeChgIfInfo.GetColumn(0,"seq") );     //순번
	cf_setParam(ds_input, "upd_cnt"    , ds_scrbfeeChgIfInfo.GetColumn(0,"upd_cnt") ); //update count
	cf_setParam(ds_input, "old_rtn_yn" , "N" );                               //중고반납여부
	cf_setParam(ds_input, "prod_cl"    , "G" );                                         //판매구분


	cf_setParam(ds_input, "pol_error_cl"      , "");    //정책 오류 코드
	cf_setParam(ds_input, "pol_rmks"          , "" );   //정책 오류 메세지
	cf_setParam(ds_input, "dis_eqp_error_yn"  , "N" );  //단말기 오류 코드 clear
	cf_setParam(ds_input, "dis_eqp_rmrk"      , "" );   //단말기 오류 메세지 clear
	cf_setParam(ds_input, "dis_usim_error_yn" , "N" );  //USIM 오류 코드 clear
	cf_setParam(ds_input, "dis_usim_rmrk"     , "" );   //USIM 오류 메세지 clear

	cf_setParam(ds_input, "ps_apnd_yn" , "Y");  // 제외 상태로 설정
	cf_setParam(ds_input, "table_nm" , "tsal_scrbfee_chg_if"); 

    var sSvcID = "saveSaleChgInfo";
	var sInDs  = "nc_input_fieldDs=ds_input";
	    sINDs  += " ds_generalSale=ds_generalSale ds_equipmentSale=ds_equipmentSale     ds_usimSale=ds_usimSale";
	    sInDs  += " ds_suplservice=ds_suplservice ds_oldrtnEquipment=ds_oldrtnEquipment ds_allotSale=ds_allotSale ds_sktPrprc=ds_sktPrprc";
	    sInDs  += " ds_agreementAstamt=ds_agreementAstamt ds_cashSale=ds_cashSale ds_payment=ds_payment ds_ocbMcard=ds_ocbMcard";
	    sInDs  += " ds_saleCmms=ds_saleCmms ds_promotionMoney=ds_promotionMoney";
	    sInDs  += " ds_onlinePayment=ds_onlinePayment ds_prodExchange=ds_prodExchange";
	    sInDs  += " ds_onlineCmms=ds_onlineCmms";	    
	    sInDs  += " ds_disInEqpInfo=ds_disInEqpInfo ds_disInUsimInfo=ds_disInUsimInfo";
	    sInDs  += " ds_disOutEqpInfo=ds_disOutEqpInfo ds_disOutUsimInfo=ds_disOutUsimInfo";

	var sOutDs =  "";
	var sArg   = "sktst.sal.SCO#saveSaleChgInfo";	

    http.Sync = true;
	cf_Transaction(sSvcID, sInDs, sOutDs, sArg);     	
    http.Sync = false;

    f_saveEnd();


    return;	
}

/*******************************************************************************
 * @description    : 일반판매 저장시 상품구분-단말기,USIM validation check
*******************************************************************************/
function lf_saveValidationCheck()
{        
    var bFlag = true;
    var sSaleChgDtm = div_prodExchange.cal_saleChgDtm.Value + div_prodExchange.edt_saleChgDtmHh.Text + div_prodExchange.edt_saleChgDtmMm.Text + "00";

	// 일마감 체크
	cf_CheckCloseDay("02", sSaleChgDtm, "변경일자"); // 02 영업
	//cf_CheckCloseMnth("03", sSaleChgDtm, "변경일자"); // 03 정산
	if(FV_BOOLEAN_CLOSE) return false;                // 입력한 일자가 마감 되었을 경우 '1'(true) 리턴.  

	if ( uf_IsNull(div_prodExchange.cal_saleChgDtm.Value) )
	{
		alert("변경일자를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_prodExchange.edt_saleChgDtmHh.Text) )
	{
		alert("판매일시를 입력해 주십시요.");
		return false;
	}

	if ( uf_IsNull(div_prodExchange.edt_saleChgDtmMm.Text) )
	{
		alert("변경시간 및 분을 입력해 주십시요.");
		return false;
	}

	if ( substr(sSaleChgDtm,0,12) < substr(ds_generalSale.GetColumn(0,"sale_chg_dtm"),0,12) )
	{
		alert("입력한 변경일시가 최종이력의 변경일시 보다 이전입니다.");
		return false;
	}

    if ( uf_IsNull( div_prodExchange.cmb_saleChgUserId.Value ) )
    {
        alert("판매변경담당자를 입력해 주세요.");
        return false;
    }

    //SKT예수급 수납대상금액 validation check
    var iSktTotSaleAmt = ParseInt( div_afSktPrprc.msk_payAmt.Value );
    var iSktPaySaleAmt = ParseInt( div_afSktPrprc.msk_paySumAmt.Value );

    if ( iSktTotSaleAmt < 0 || iSktPaySaleAmt < 0 )
    {
        alert("현금수납 및 대납금액은 (-)불가합니다.");
        return false;
    }

    if ( iSktTotSaleAmt != isktPaySaleAmt )
    {
        alert("예수금수납대상금액과 수납금액이 일치하지 않습니다.");
        return false; 
    }

    return bFlag;
}

/*******************************************************************************
 * @description    : 시간 validation check
*******************************************************************************/
function div_dealCoInfo_edt_saleDtmHh_OnChanged(obj,strText)
{
	if (strText > 23)
	{
	    alert("유효한 시간을 입력해주세요.");
	}
	else
	{
	    if (length(strText) == 1)
	    {
	        obj.value = "0" + strText;
	    }
	}

}

/*******************************************************************************
 * @description    : 분 validation check
*******************************************************************************/
function div_dealCoInfo_edt_saleDtmMm_OnChanged(obj,strText)
{
	if (strText > 59)
	{
	    alert("유효한 시간을 입력해주세요.");
	}
	else
	{
	    if (length(strText) == 1)
	    {
	        obj.value = "0" + strText;
	    }
	}	
}


