/*******************************************************************************
 * @description    : 모델별판매가격표 정보를 조회(단말기)
*******************************************************************************/
function f_getSplstMdlInfo()
{
    //모델별 판매가격표 조회
    //중고일대는 조회하지 않음.
    //조회 조건을 만족해야함.
    var sSaleDtm = div_afSaleInfo.cal_saleDtm.Value + div_afSaleInfo.edt_saleDtmHh.Text + div_afSaleInfo.edt_saleDtmMm.Text + "00";
    var sOrgId   = div_afSaleInfo.edt_posAgencyCd.Text;

    //중고 판매일 경우 가격조회 안함.
    //if ( div_eqpInfo.chk_eqpOldSaleYn.Value == "Y" || ds_generalSaleIfInfo.GetColumn(0,"settl_cond") == "C" )
    if ( div_eqpInfo.chk_eqpOldSaleYn.Value == "Y" )
    {
        return;
    }

    //있을때만 조회하게 한다.
    var sFlag = "true";
    if ( uf_IsNull(div_afSaleInfo.cal_saleDtm.Value) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(div_afSaleInfo.edt_saleDtmHh.Text ) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(div_afSaleInfo.edt_saleDtmMm.Text ) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(div_afSaleInfo.edt_salePlc.Text ) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(div_afSaleInfo.cmb_slNetCd.Value) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(div_afSaleInfo.cmb_saleDtlTyp.Value) )
    {
        sFlag = "false";
    }

    if ( uf_IsNull(ds_equipmentSale.GetColumn(0,"eqp_mdl_cd") ) )
    {
        sFlag = "false";
    }

    if ( sFlag == "true" )    {
			cf_setParam(ds_input, "sale_typ_cd_min" , div_afSaleInfo.cmb_saleDtlTyp.Value);           //판매유형
			cf_setParam(ds_input, "sale_dtm"        , sSaleDtm);                                      //판매일시
			cf_setParam(ds_input, "org_id"          , sOrgId); 
			cf_setParam(ds_input, "eqp_mdl_cd"      , ds_equipmentSale.GetColumn(0,"eqp_mdl_cd") );   //단말기모델코드
			cf_setParam(ds_input, "sl_net_cd"       , div_afSaleInfo.cmb_slNetCd.Value);              //영업망 
			cf_setParam(ds_input, "ds_net_cd"       , ds_generalSale.GetColumn(0,"ds_net_cd") );      //유통망 
			cf_setParam(ds_input, "agrmt_prd_cd"    , ds_generalSale.GetColumn(0,"agrmt_prd_cd") );   //약정기간코드

			var sSvcID = "getSplstMdlInfo";
			var sInDs  = "nc_input_fieldDs=ds_input";
			var sOutDs = "ds_splstMdl=ds_splstMdl";
			var sArg   = "sktst.sal.SCO#getSplstMdlInfo";

			if (parent.ds_channelChgIfInfo.GetColumn(0,"sl_cl") == "03" || 
			    parent.ds_channelChgIfInfo.GetColumn(0,"sl_cl") == "04" || 
			    parent.ds_channelChgIfInfo.GetColumn(0,"sl_cl") == "05")
			{
				sArg = "sktst.sal.SCO#getOnlineSplstMdlInfo";
			}

			http.Sync = true;
			cf_Transaction(sSvcID, sInDs, sOutDs, sArg);	 
			http.Sync = false;
    }
    else
    {
        //FV_POL_ERR_YN = "Y";
    }

}

/*******************************************************************************
 * @description    : USIM 운영모델 단가표 조회 
*******************************************************************************/
function f_getUplst()
{
    var sUsimMdlCd = ds_usimSale.GetColumn(0,"usim_mdl_cd");
	var sSaleDtm = div_afSaleInfo.cal_saleDtm.Value + div_afSaleInfo.edt_saleDtmHh.Text + div_afSaleInfo.edt_saleDtmMm.Text + "00";

    //중고 판매일 경우 가격조회 안함.
    //if ( div_eqpInfo.chk_eqpOldSaleYn.Value == "Y" || ds_generalSaleIfInfo.GetColumn(0,"settl_cond") == "C" )
    if ( div_usimInfo.chk_usimOldSaleYn.Value == "Y" )
    {
        return;
    }

    if ( !uf_IsNull(sUsimMdlCd) )
    {
		cf_setParam(ds_input, "mdl_id"     , sUsimMdlCd);   //USIM모델코드
		cf_setParam(ds_input, "sale_dtm"   , sSaleDtm );    //판매일시

		var sSvcID = "getUplst";
		var sInDs  = "nc_input_fieldDs=ds_input";
		var sOutDs = "ds_uplst=List";
		var sArg   = "sktst.sal.SCO#getUplst";	

		http.Sync = true;
		cf_Transaction(sSvcID, sInDs, sOutDs, sArg);  	
		http.Sync = false;	
    }
}

/*******************************************************************************
 * @description   : 단말기,USIM 재고 메세지 및 가격정책 메세지 alert 처리
*******************************************************************************/
function lf_prodErrMsg(sCl,sEqpErrCd,sEqpErrMsg,sUsimErrCd,sUsimErrMsg)
{
    var sOutMsg = "";

	if ( uf_IsNull(sEqpErrMsg) )
	{
		sOutMsg = "";
	}
	else
	{
	    //if ( !uf_IsNull(sEqpErrCd) || sEqpErrCd != "20" )
	    if ( sEqpErrCd != "20" )
	    {
		    sOutMsg =  "단말기 : " + sEqpErrMsg;
		}
	}

	//sOutMsg = sOUtMsg;

	if ( uf_IsNull(sUsimErrMsg) )
	{
		sOutMsg = sOutMsg + "";
	}
	else 
	{
	    //if( !uf_IsNull(sUsimErrCd) )
	    if( sUsimErrCd != "20" )
	    {
		    sOutMsg = sOutMsg + "\n" + "USIM : " + sUsimErrMsg;
		}
	}

	if ( !uf_IsNull(sOutMsg) )
	{
	    alert( sOutMsg );

	    if ( sCl == "1" )
	    {
	        FV_DIS_ERR_YN = "Y";
	    }
	    else
	    {
	        FV_POL_ERR_YN = "Y";   
	    }
    }
    else 
    {
	    if ( sCl == "1" )
	    {
	        FV_DIS_ERR_YN = "N";
	    }
	    else
	    {
	        FV_POL_ERR_YN = "N";   
	    }
    }

    lf_enableBtnSave("1");
/*    
	if ( !uf_IsNull(sOutMsg) )
	{
	    lf_enableBtnSave("1");
		alert( sOutMsg );
	}
	else
	{
	    lf_enableBtnSave("0");
	}
*/	
	return;
}

/*******************************************************************************
 * @description   : 저장버튼 0:할성화 ,  1:비활성화
*******************************************************************************/
function lf_enableBtnSave(param)
{
    if (FV_DIS_ERR_YN == "N" && FV_POL_ERR_YN =="N")
    {
        btn_save.Enable = true;
    }
    else
    {
        btn_save.Enable = false;
    }

}

