function div_afSaleInfo_btn_salePlc_OnClick(obj)
{
    var sDealCoCl = "";
    var sSlCl     = parent.ds_channelChgIfInfo.GetColumn(0,"sl_cl") ;
    var sSaleDtm  = div_afSaleInfo.cal_saleDtm.Value + div_afSaleInfo.edt_saleDtmHh.Text + div_afSaleInfo.edt_saleDtmMm.Text + "00";

    sDealCoCl = sf_getDealCoCl1(true);   
	//sf_clearControls("div_afSaleInfo.edt_salePlcUkeyCd,div_afSaleInfo.edt_salePlcSubCd,div_afSaleInfo.edt_salePlcChannelCd,div_afSaleInfo.edt_salePlc");

	var sArg  = "deal_co_cl1=" +quote(sDealCoCl);
		sArg += " deal_status="+quote("3"); 	//옵션 (거래처 상태) - 1: 수납정지 N  , 2: 출고정지 N , 3: 판매정지 N 
		sArg += " pos_agency=" +quote(gds_session.GetColumn(0,"posAgencyId")); // 옵션	    
		sArg += " eff_dtm="    +quote(sSaleDtm);
        sArg += " deal_co_cd=" +quote(div_afSaleInfo.edt_salePlc.Text);

	cf_callDealPop(sArg, div_afSaleInfo, "edt_salePlcNm,edt_salePlc,edt_salePlcUkeyCd,edt_salePlcSubCd,edt_salePlcChannelCd");

    if(uf_isNull(FV_STR_POP_RESULT) == true) return;

    if ( !uf_IsNull(div_afSaleInfo.edt_salePlc.Text) )
    {
		var sSaleChgDtm = "";

		if ( FV_IF_PROC_CL == "1")
		{
			FV_PROC_DTM = ds_channelChgIfInfo.GetColumn(0,"proc_dt") + ds_channelChgIfInfo.GetColumn(0,"proc_tm") + "00"; 
			sSaleChgDtm = ds_channelChgIfInfo.GetColumn(0,"proc_dt") + ds_channelChgIfInfo.GetColumn(0,"proc_tm"); 
		}
		else
		{
			FV_PROC_DTM = ds_generalSale.GetColumn(0, "sale_chg_dtm"); 
			sSaleChgDtm = FV_PROC_DTM;
		}
		f_getSalePlcChgChgrgId();

		lf_getStlPlc();    

		lf_getPol();             
    }


}

